schema:
{
 "properties": {
  "spec": {
   "properties": {
    "authProvider": {
     "description": "AuthProvider specifies a custom authentication plugin for the kubernetes cluster.",
     "properties": {
      "config": {
       "additionalProperties": {
        "type": "string"
       },
       "type": "object"
      },
      "name": {
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "aws": {
     "properties": {
      "accessKeyID": {
       "type": "string"
      },
      "assumeRoleArn": {
       "type": "string"
      },
      "assumeRoleExternalID": {
       "type": "string"
      },
      "cache": {
       "type": "boolean"
      },
      "clusterID": {
       "type": "string"
      },
      "expiration": {
       "format": "int64",
       "type": "integer"
      },
      "forwardSessionName": {
       "type": "boolean"
      },
      "region": {
       "type": "string"
      },
      "secretAccessKey": {
       "type": "string"
      },
      "sessionName": {
       "type": "string"
      },
      "token": {
       "description": "Temporary Token for 15 mins only, if expired or not set create a new one",
       "type": "string"
      }
     },
     "required": [
      "accessKeyID",
      "secretAccessKey"
     ],
     "type": "object"
    },
    "clientCertificateData": {
     "description": "ClientCertificateData contains PEM-encoded data from a client cert file for TLS.",
     "format": "byte",
     "type": "string"
    },
    "clientKeyData": {
     "description": "ClientKeyData contains PEM-encoded data from a client key file for TLS.",
     "format": "byte",
     "type": "string"
    },
    "clusterUID": {
     "type": "string"
    },
    "credentialName": {
     "type": "string"
    },
    "googleCloud": {
     "properties": {
      "projectID": {
       "type": "string"
      },
      "serviceAccount": {
       "type": "string"
      }
     },
     "required": [
      "projectID",
      "serviceAccount"
     ],
     "type": "object"
    },
    "googleOAuth": {
     "properties": {
      "accessToken": {
       "type": "string"
      },
      "clientID": {
       "type": "string"
      },
      "clientSecret": {
       "type": "string"
      },
      "expiry": {
       "format": "int64",
       "type": "integer"
      },
      "refreshToken": {
       "type": "string"
      }
     },
     "required": [
      "accessToken",
      "clientID",
      "clientSecret"
     ],
     "type": "object"
    },
    "impersonate": {
     "description": "Impersonate is the username to act-as.",
     "type": "string"
    },
    "impersonateGroups": {
     "description": "ImpersonateGroups is the groups to impersonate.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "impersonateUserExtra": {
     "additionalProperties": {
      "description": "ExtraValue masks the value so protobuf can generate",
      "items": {
       "type": "string"
      },
      "type": "array"
     },
     "description": "ImpersonateUserExtra contains additional information for impersonated user.",
     "type": "object"
    },
    "password": {
     "description": "Password is the password for basic authentication to the kubernetes cluster.",
     "type": "string"
    },
    "provider": {
     "description": "Provider Access Token params",
     "type": "string"
    },
    "token": {
     "description": "Token is the bearer token for authentication to the kubernetes cluster.",
     "type": "string"
    },
    "userID": {
     "format": "int64",
     "type": "integer"
    },
    "username": {
     "description": "Username is the username for basic authentication to the kubernetes cluster.",
     "type": "string"
    }
   },
   "required": [
    "clusterUID",
    "userID"
   ],
   "type": "object"
  }
 },
 "title": "Cluster User Auth",
 "type": "object"
}
