component.schema:
{
 "description": "AerospikeBackupService is the Schema for the aerospikebackupservices API",
 "properties": {
  "spec": {
   "description": "AerospikeBackupServiceSpec defines the desired state of AerospikeBackupService",
   "properties": {
    "config": {
     "description": "Config is the free form configuration for the backup service in YAML format.\nThis config is used to start the backup service. The config is passed as a file to the backup service.\nIt includes: service, backup-policies, storage, secret-agent.",
     "format": "textarea",
     "type": "string"
    },
    "image": {
     "description": "Image is the image for the backup service.",
     "type": "string"
    },
    "resources": {
     "description": "Resources defines the requests and limits for the backup service container.\nResources.Limits should be more than Resources.Requests.",
     "properties": {
      "claims": {
       "description": "Claims lists the names of resources, defined in spec.resourceClaims,\nthat are used by this container.\n\n\nThis is an alpha field and requires enabling the\nDynamicResourceAllocation feature gate.\n\n\nThis field is immutable. It can only be set for containers.",
       "items": {
        "description": "ResourceClaim references one entry in PodSpec.ResourceClaims.",
        "properties": {
         "name": {
          "description": "Name must match the name of one entry in pod.spec.resourceClaims of\nthe Pod where this field is used. It makes that resource available\ninside a container.",
          "type": "string"
         }
        },
        "required": [
         "name"
        ],
        "type": "object"
       },
       "type": "array",
       "x-kubernetes-list-map-keys": [
        "name"
       ],
       "x-kubernetes-list-type": "map"
      },
      "limits": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
        "x-kubernetes-int-or-string": true
       },
       "description": "Limits describes the maximum amount of compute resources allowed.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
       "type": "object"
      },
      "requests": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
        "x-kubernetes-int-or-string": true
       },
       "description": "Requests describes the minimum amount of compute resources required.\nIf Requests is omitted for a container, it defaults to Limits if that is explicitly specified,\notherwise to an implementation-defined value. Requests cannot exceed Limits.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
       "type": "object"
      }
     },
     "type": "object"
    },
    "secrets": {
     "description": "SecretMounts is the list of secret to be mounted in the backup service.",
     "items": {
      "description": "SecretMount specifies the secret and its corresponding volume mount options.",
      "properties": {
       "secretName": {
        "description": "SecretName is the name of the secret to be mounted.",
        "type": "string"
       },
       "volumeMount": {
        "description": "VolumeMount is the volume mount options for the secret.",
        "properties": {
         "mountPath": {
          "description": "Path within the container at which the volume should be mounted.  Must\nnot contain ':'.",
          "type": "string"
         },
         "mountPropagation": {
          "description": "mountPropagation determines how mounts are propagated from the host\nto container and the other way around.\nWhen not set, MountPropagationNone is used.\nThis field is beta in 1.10.",
          "type": "string"
         },
         "name": {
          "description": "This must match the Name of a Volume.",
          "type": "string"
         },
         "readOnly": {
          "description": "Mounted read-only if true, read-write otherwise (false or unspecified).\nDefaults to false.",
          "type": "boolean"
         },
         "subPath": {
          "description": "Path within the volume from which the container's volume should be mounted.\nDefaults to \"\" (volume's root).",
          "type": "string"
         },
         "subPathExpr": {
          "description": "Expanded path within the volume from which the container's volume should be mounted.\nBehaves similarly to SubPath but environment variable references $(VAR_NAME) are expanded using the container's environment.\nDefaults to \"\" (volume's root).\nSubPathExpr and SubPath are mutually exclusive.",
          "type": "string"
         }
        },
        "required": [
         "mountPath",
         "name"
        ],
        "type": "object"
       }
      },
      "required": [
       "secretName",
       "volumeMount"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "service": {
     "description": "Service defines the Kubernetes service configuration for the backup service.\nIt is used to expose the backup service deployment. By default, the service type is ClusterIP.",
     "properties": {
      "type": {
       "description": "Type is the Kubernetes service type.",
       "type": "string"
      }
     },
     "required": [
      "type"
     ],
     "type": "object"
    }
   },
   "required": [
    "config",
    "image"
   ],
   "type": "object"
  }
 },
 "title": "Aerospike Backup Service",
 "type": "object"
}
