component.schema:
{
 "description": "AerospikeCluster is the schema for the AerospikeCluster API",
 "properties": {
  "spec": {
   "description": "AerospikeClusterSpec defines the desired state of AerospikeCluster",
   "properties": {
    "aerospikeAccessControl": {
     "description": "Has the Aerospike roles and users definitions. Required if aerospike cluster security is enabled.",
     "properties": {
      "adminPolicy": {
       "description": "AerospikeClientAdminPolicy specify the aerospike client admin policy for access control operations.",
       "properties": {
        "timeout": {
         "description": "Timeout for admin client policy in milliseconds.",
         "type": "integer"
        }
       },
       "required": [
        "timeout"
       ],
       "type": "object"
      },
      "roles": {
       "description": "Roles is the set of roles to allow on the Aerospike cluster.",
       "items": {
        "description": "AerospikeRoleSpec specifies an Aerospike database role and its associated privileges.",
        "properties": {
         "name": {
          "description": "Name of this role.",
          "type": "string"
         },
         "privileges": {
          "description": "Privileges granted to this role.",
          "items": {
           "type": "string"
          },
          "type": "array",
          "x-kubernetes-list-type": "set"
         },
         "readQuota": {
          "description": "ReadQuota specifies permitted rate of read records for current role (the value is in RPS)",
          "format": "int32",
          "type": "integer"
         },
         "whitelist": {
          "description": "Whitelist of host address allowed for this role.",
          "items": {
           "type": "string"
          },
          "type": "array",
          "x-kubernetes-list-type": "set"
         },
         "writeQuota": {
          "description": "WriteQuota specifies permitted rate of write records for current role (the value is in RPS)",
          "format": "int32",
          "type": "integer"
         }
        },
        "required": [
         "name",
         "privileges"
        ],
        "type": "object"
       },
       "type": "array",
       "x-kubernetes-list-map-keys": [
        "name"
       ],
       "x-kubernetes-list-type": "map"
      },
      "users": {
       "description": "Users is the set of users to allow on the Aerospike cluster.",
       "items": {
        "description": "AerospikeUserSpec specifies an Aerospike database user, the secret name for the password and, associated roles.",
        "properties": {
         "name": {
          "description": "Name is the user's username.",
          "type": "string"
         },
         "roles": {
          "description": "Roles is the list of roles granted to the user.",
          "items": {
           "type": "string"
          },
          "type": "array",
          "x-kubernetes-list-type": "set"
         },
         "secretName": {
          "description": "SecretName has secret info created by user. User needs to create this secret from password literal.\neg: kubectl create secret generic dev-db-secret --from-literal=password='password'",
          "type": "string"
         }
        },
        "required": [
         "name",
         "roles",
         "secretName"
        ],
        "type": "object"
       },
       "type": "array",
       "x-kubernetes-list-map-keys": [
        "name"
       ],
       "x-kubernetes-list-type": "map"
      }
     },
     "required": [
      "users"
     ],
     "type": "object"
    },
    "aerospikeConfig": {
     "description": "Sets config in aerospike.conf file. Other configs are taken as default",
     "format": "textarea",
     "type": "string"
    },
    "aerospikeNetworkPolicy": {
     "description": "AerospikeNetworkPolicy specifies how clients and tools access the Aerospike cluster.",
     "properties": {
      "access": {
       "description": "AccessType is the type of network address to use for Aerospike access address.\nDefaults to hostInternal.",
       "enum": [
        "pod",
        "hostInternal",
        "hostExternal",
        "configuredIP",
        "customInterface"
       ],
       "type": "string"
      },
      "alternateAccess": {
       "description": "AlternateAccessType is the type of network address to use for Aerospike alternate access address.\nDefaults to hostExternal.",
       "enum": [
        "pod",
        "hostInternal",
        "hostExternal",
        "configuredIP",
        "customInterface"
       ],
       "type": "string"
      },
      "customAccessNetworkNames": {
       "description": "CustomAccessNetworkNames is the list of the pod's network interfaces used for Aerospike access address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign\nnetwork interfaces to the pod.\nRequired with 'customInterface' access type.",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "customAlternateAccessNetworkNames": {
       "description": "CustomAlternateAccessNetworkNames is the list of the pod's network interfaces used for Aerospike\nalternate access address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign\nnetwork interfaces to the pod.\nRequired with 'customInterface' alternateAccess type",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "customFabricNetworkNames": {
       "description": "CustomFabricNetworkNames is the list of the pod's network interfaces used for Aerospike fabric address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign\nnetwork interfaces to the pod.\nRequired with 'customInterface' fabric type",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "customTLSAccessNetworkNames": {
       "description": "CustomTLSAccessNetworkNames is the list of the pod's network interfaces used for Aerospike TLS access address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign\nnetwork interfaces to the pod.\nRequired with 'customInterface' tlsAccess type",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "customTLSAlternateAccessNetworkNames": {
       "description": "CustomTLSAlternateAccessNetworkNames is the list of the pod's network interfaces used for Aerospike TLS\nalternate access address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign\nnetwork interfaces to the pod.\nRequired with 'customInterface' tlsAlternateAccess type",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "customTLSFabricNetworkNames": {
       "description": "CustomTLSFabricNetworkNames is the list of the pod's network interfaces used for Aerospike TLS fabric address.\nEach element in the list is specified with a namespace and the name of a NetworkAttachmentDefinition,\nseparated by a forward slash (/).\nThese elements must be defined in the pod annotation k8s.v1.cni.cncf.io/networks in order to assign network\ninterfaces to the pod.\nRequired with 'customInterface' tlsFabric type",
       "items": {
        "type": "string"
       },
       "minItems": 1,
       "type": "array"
      },
      "fabric": {
       "description": "FabricType is the type of network address to use for Aerospike fabric address.\nDefaults is empty meaning all interfaces 'any'.",
       "enum": [
        "customInterface"
       ],
       "type": "string"
      },
      "tlsAccess": {
       "description": "TLSAccessType is the type of network address to use for Aerospike TLS access address.\nDefaults to hostInternal.",
       "enum": [
        "pod",
        "hostInternal",
        "hostExternal",
        "configuredIP",
        "customInterface"
       ],
       "type": "string"
      },
      "tlsAlternateAccess": {
       "description": "TLSAlternateAccessType is the type of network address to use for Aerospike TLS alternate access address.\nDefaults to hostExternal.",
       "enum": [
        "pod",
        "hostInternal",
        "hostExternal",
        "configuredIP",
        "customInterface"
       ],
       "type": "string"
      },
      "tlsFabric": {
       "description": "TLSFabricType is the type of network address to use for Aerospike TLS fabric address.\nDefaults is empty meaning all interfaces 'any'.",
       "enum": [
        "customInterface"
       ],
       "type": "string"
      }
     },
     "type": "object"
    },
    "disablePDB": {
     "description": "Disable the PodDisruptionBudget creation for the Aerospike cluster.",
     "type": "boolean"
    },
    "enableDynamicConfigUpdate": {
     "description": "EnableDynamicConfigUpdate enables dynamic config update flow of the operator.\nIf enabled, operator will try to update the Aerospike config dynamically.\nIn case of inconsistent state during dynamic config update, operator falls back to rolling restart.",
     "type": "boolean"
    },
    "headlessService": {
     "description": "HeadlessService defines additional configuration parameters for the headless service created to discover\nAerospike Cluster nodes",
     "properties": {
      "metadata": {
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Key - Value pair that may be set by external tools to store and retrieve arbitrary metadata",
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Key - Value pairs that can be used to organize and categorize scope and select objects",
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "image": {
     "description": "Aerospike server image",
     "type": "string"
    },
    "k8sNodeBlockList": {
     "description": "K8sNodeBlockList is a list of Kubernetes nodes which are not used for Aerospike pods. Pods are not scheduled on\nthese nodes. Pods are migrated from these nodes if already present. This is useful for the maintenance of\nKubernetes nodes.",
     "items": {
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    },
    "maxUnavailable": {
     "anyOf": [
      {
       "type": "integer"
      },
      {
       "type": "string"
      }
     ],
     "description": "MaxUnavailable is the percentage/number of pods that can be allowed to go down or unavailable before application\ndisruption. This value is used to create PodDisruptionBudget. Defaults to 1.\nRefer Aerospike documentation for more details.",
     "x-kubernetes-int-or-string": true
    },
    "operations": {
     "description": "Operations is a list of on-demand operations to be performed on the Aerospike cluster.",
     "items": {
      "properties": {
       "id": {
        "description": "ID is the unique identifier for the operation. It is used by the operator to track the operation.",
        "maxLength": 20,
        "minLength": 1,
        "type": "string"
       },
       "kind": {
        "description": "Kind is the type of operation to be performed on the Aerospike cluster.",
        "enum": [
         "WarmRestart",
         "PodRestart"
        ],
        "type": "string"
       },
       "podList": {
        "description": "PodList is the list of pods on which the operation is to be performed.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "id",
       "kind"
      ],
      "type": "object"
     },
     "maxItems": 1,
     "type": "array"
    },
    "operatorClientCert": {
     "description": "Certificates to connect to Aerospike.",
     "properties": {
      "certPathInOperator": {
       "description": "AerospikeCertPathInOperatorSource contain configuration for certificates used by operator to connect to aerospike\ncluster.\nAll paths are on operator's filesystem.",
       "properties": {
        "caCertsPath": {
         "type": "string"
        },
        "clientCertPath": {
         "type": "string"
        },
        "clientKeyPath": {
         "type": "string"
        }
       },
       "type": "object"
      },
      "secretCertSource": {
       "properties": {
        "caCertsFilename": {
         "type": "string"
        },
        "caCertsSource": {
         "properties": {
          "secretName": {
           "type": "string"
          },
          "secretNamespace": {
           "type": "string"
          }
         },
         "required": [
          "secretName"
         ],
         "type": "object"
        },
        "clientCertFilename": {
         "type": "string"
        },
        "clientKeyFilename": {
         "type": "string"
        },
        "secretName": {
         "type": "string"
        },
        "secretNamespace": {
         "type": "string"
        }
       },
       "required": [
        "secretName"
       ],
       "type": "object"
      },
      "tlsClientName": {
       "description": "If specified, this name will be added to tls-authenticate-client list by the operator",
       "type": "string"
      }
     },
     "type": "object"
    },
    "paused": {
     "description": "Paused flag is used to pause the reconciliation for the AerospikeCluster.",
     "type": "boolean"
    },
    "podService": {
     "description": "PodService defines additional configuration parameters for the pod service created to expose the\nAerospike Cluster nodes outside the Kubernetes cluster. This service is created only created when\n`multiPodPerHost` is set to `true` and `aerospikeNetworkPolicy` has one of the network types:\n'hostInternal', 'hostExternal', 'configuredIP'",
     "properties": {
      "metadata": {
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Key - Value pair that may be set by external tools to store and retrieve arbitrary metadata",
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Key - Value pairs that can be used to organize and categorize scope and select objects",
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "podSpec": {
     "description": "Specify additional configuration for the Aerospike pods",
     "properties": {
      "aerospikeContainer": {
       "description": "AerospikeContainerSpec configures the aerospike-server container\ncreated by the operator.",
       "properties": {
        "resources": {
         "description": "Define resources requests and limits for Aerospike Server Container.\nPlease contact aerospike for proper sizing exercise\nOnly Memory and Cpu resources can be given\nResources.Limits should be more than Resources.Requests.",
         "properties": {
          "claims": {
           "description": "Claims lists the names of resources, defined in spec.resourceClaims,\nthat are used by this container.\n\nThis is an alpha field and requires enabling the\nDynamicResourceAllocation feature gate.\n\nThis field is immutable. It can only be set for containers.",
           "items": {
            "description": "ResourceClaim references one entry in PodSpec.ResourceClaims.",
            "properties": {
             "name": {
              "description": "Name must match the name of one entry in pod.spec.resourceClaims of\nthe Pod where this field is used. It makes that resource available\ninside a container.",
              "type": "string"
             },
             "request": {
              "description": "Request is the name chosen for a request in the referenced claim.\nIf empty, everything from the claim is made available, otherwise\nonly the result of this request.",
              "type": "string"
             }
            },
            "required": [
             "name"
            ],
            "type": "object"
           },
           "type": "array",
           "x-kubernetes-list-map-keys": [
            "name"
           ],
           "x-kubernetes-list-type": "map"
          },
          "limits": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
            "x-kubernetes-int-or-string": true
           },
           "description": "Limits describes the maximum amount of compute resources allowed.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
           "type": "object"
          },
          "requests": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
            "x-kubernetes-int-or-string": true
           },
           "description": "Requests describes the minimum amount of compute resources required.\nIf Requests is omitted for a container, it defaults to Limits if that is explicitly specified,\notherwise to an implementation-defined value. Requests cannot exceed Limits.\nMore info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
           "type": "object"
          }
         },
         "type": "object"
        },
        "securityContext": {
         "description": "SecurityContext that will be added to aerospike-server container created by operator.",
         "properties": {
          "allowPrivilegeEscalation": {
           "description": "AllowPrivilegeEscalation controls whether a process can gain more\nprivileges than its parent process. This bool directly controls if\nthe no_new_privs flag will be set on the container process.\nAllowPrivilegeEscalation is true always when the container is:\n1) run as Privileged\n2) has CAP_SYS_ADMIN\nNote that this field cannot be set when spec.os.name is windows.",
           "type": "boolean"
          },
          "appArmorProfile": {
           "description": "appArmorProfile is the AppArmor options to use by this container. If set, this profile\noverrides the pod's appArmorProfile.\nNote that this field cannot be set when spec.os.name is windows.",
           "properties": {
            "localhostProfile": {
             "description": "localhostProfile indicates a profile loaded on the node that should be used.\nThe profile must be preconfigured on the node to work.\nMust match the loaded name of the profile.\nMust be set if and only if type is \"Localhost\".",
             "type": "string"
            },
            "type": {
             "description": "type indicates which kind of AppArmor profile will be applied.\nValid options are:\n  Localhost - a profile pre-loaded on the node.\n  RuntimeDefault - the container runtime's default profile.\n  Unconfined - no AppArmor enforcement.",
             "type": "string"
            }
           },
           "required": [
            "type"
           ],
           "type": "object"
          },
          "capabilities": {
           "description": "The capabilities to add/drop when running containers.\nDefaults to the default set of capabilities granted by the container runtime.\nNote that this field cannot be set when spec.os.name is windows.",
           "properties": {
            "add": {
             "description": "Added capabilities",
             "items": {
              "description": "Capability represent POSIX capabilities type",
              "type": "string"
             },
             "type": "array",
             "x-kubernetes-list-type": "atomic"
            },
            "drop": {
             "description": "Removed capabilities",
             "items": {
              "description": "Capability represent POSIX capabilities type",
              "type": "string"
             },
             "type": "array",
             "x-kubernetes-list-type": "atomic"
            }
           },
           "type": "object"
          },
          "privileged": {
           "description": "Run container in privileged mode.\nProcesses in privileged containers are essentially equivalent to root on the host.\nDefaults to false.\nNote that this field cannot be set when spec.os.name is windows.",
           "type": "boolean"
          },
          "procMount": {
           "description": "procMount denotes the type of proc mount to use for the containers.\nThe default value is Default which uses the container runtime defaults for\nreadonly paths and masked paths.\nThis requires the ProcMountType feature flag to be enabled.\nNote that this field cannot be set when spec.os.name is windows.",
           "type": "string"
          },
          "readOnlyRootFilesystem": {
           "description": "Whether this container has a read-only root filesystem.\nDefault is false.\nNote that this field cannot be set when spec.os.name is windows.",
           "type": "boolean"
          },
          "runAsGroup": {
           "description": "The GID to run the entrypoint of the container process.\nUses runtime default if unset.\nMay also be set in PodSecurityContext.  If set in both SecurityContext and\nPodSecurityContext, the value specified in SecurityContext takes precedence.\nNote that this field cannot be set when spec.os.name is windows.",
           "format": "int64",
           "type": "integer"
          },
          "runAsNonRoot": {
           "description": "Indicates that the container must run as a non-root user.\nIf true, the Kubelet will validate the image at runtime to ensure that it\ndoes not run as UID 0 (root) and fail to start the container if it does.\nIf unset or false, no such validation will be performed.\nMay also be set in PodSecurityContext.  If set in both SecurityContext and\nPodSecurityContext, the value specified in SecurityContext takes precedence.",
           "type": "boolean"
          },
          "runAsUser": {
           "description": "The UID to run the entrypoint of the container process.\nDefaults to user specified in image metadata if unspecified.\nMay also be set in PodSecurityContext.  If set in both SecurityContext and\nPodSecurityContext, the value specified in SecurityContext takes precedence.\nNote that this field cannot be set when spec.os.name is windows.",
           "format": "int64",
           "type": "integer"
          },
          "seLinuxOptions": {
           "description": "The SELinux context to be applied to the container.\nIf unspecified, the container runtime will allocate a random SELinux context for each\ncontainer.  May also be set in PodSecurityContext.  If set in both SecurityContext and\nPodSecurityContext, the value specified in SecurityContext takes precedence.\nNote that this field cannot be set when spec.os.name is windows.",
           "properties": {
            "level": {
             "description": "Level is SELinux level label that applies to the container.",
             "type": "string"
            },
            "role": {
             "description": "Role is a SELinux role label that applies to the container.",
             "type": "string"
            },
            "type": {
             "description": "Type is a SELinux type label that applies to the container.",
             "type": "string"
            },
            "user": {
             "description": "User is a SELinux user label that applies to the container.",
             "type": "string"
            }
           },
           "type": "object"
          },
          "seccompProfile": {
           "description": "The seccomp options to use by this container. If seccomp options are\nprovided at both the pod \u0026 container level, the container options\noverride the pod options.\nNote that this field cannot be set when spec.os.name is windows.",
           "properties": {
            "localhostProfile": {
             "description": "localhostProfile indicates a profile defined in a file on the node should be used.\nThe profile must be preconfigured on the node to work.\nMust be a descending path, relative to the kubelet's configured seccomp profile location.\nMust be set if type is \"Localhost\". Must NOT be set for any other type.",
             "type": "string"
            },
            "type": {
             "description": "type indicates which kind of seccomp profile will be applied.\nValid options are:\n\nLocalhost - a profile defined in a file on the node should be used.\nRuntimeDefault - the container runtime default profile should be used.\nUnconfined - no profile should be applied.",
             "type": "string"
            }
           },
           "required": [
            "type"
           ],
           "type": "object"
          },
          "windowsOptions": {
           "description": "The Windows specific settings applied to all containers.\nIf unspecified, the options from the PodSecurityContext will be used.\nIf set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence.\nNote that this field cannot be set when spec.os.name is linux.",
           "properties": {
            "gmsaCredentialSpec": {
             "description": "GMSACredentialSpec is where the GMSA admission webhook\n(https://github.com/kubernetes-sigs/windows-gmsa) inlines the contents of the\nGMSA credential spec named by the GMSACredentialSpecName field.",
             "type": "string"
            },
            "gmsaCredentialSpecName": {
             "description": "GMSACredentialSpecName is the name of the GMSA credential spec to use.",
             "type": "string"
            },
            "hostProcess": {
             "description": "HostProcess determines if a container should be run as a 'Host Process' container.\nAll of a Pod's containers must have the same effective HostProcess value\n(it is not allowed to have a mix of HostProcess containers and non-HostProcess containers).\nIn addition, if HostProcess is true then HostNetwork must also be set to true.",
             "type": "boolean"
            },
            "runAsUserName": {
             "description": "The UserName in Windows to run the entrypoint of the container process.\nDefaults to the user specified in image metadata if unspecified.\nMay also be set in PodSecurityContext. If set in both SecurityContext and\nPodSecurityContext, the value specified in SecurityContext takes precedence.",
             "type": "string"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "aerospikeInitContainer": {
       "description": "AerospikeInitContainerSpec configures the aerospike-init container\ncreated by the operator.",
       "properties": {
        "imageNameAndTag": {
         "description": "ImageNameAndTag is the name:tag of aerospike-init container image",
         "type": "string"
        },
        "imageRegistry": {
         "description": "ImageRegistry is the name of image registry for aerospike-init container image\nImageRegistry, e.g. docker.io, redhat.access.com",
         "type": "string"
        },
        "imageRegistryNamespace": {
         "description": "ImageRegistryNamespace is the name of namespace in registry for aerospike-init container image",
         "type": "string"
        },
        "resources": {
         "description": "Define resources requests and limits for Aerospike init Container.\nOnly Memory and Cpu resources can be given\nResources.Limits should be more than Resources.Requests.",
         "properties": {
          "claims": {
           "description": "Claims lists the names of resources, defined in spec.resourceClaims,\nthat are used by this container.\n\nThis is an alpha field and requires enabling the\nDynamicResourceAllocation feature gate.\n\nThis field is immutable. It can only be set for containers.",
           "items": {
            "description": "ResourceClaim references one entry in PodSpec.ResourceClaims.",
            "properties": {
             "name": {
