schema:
{
 "description": "TLSContext is the Schema for the tlscontexts API",
 "properties": {
  "spec": {
   "description": "TLSContextSpec defines the desired state of TLSContext",
   "properties": {
    "alpn_protocols": {
     "type": "string"
    },
    "ambassador_id": {
     "description": "AmbassadorID declares which Ambassador instances should pay attention to this resource.  May either be a string or a list of strings.  If no value is provided, the default is: \n    ambassador_id:    - \"default\"",
     "items": {
      "type": "string"
     },
     "oneOf": [
      {
       "type": "string"
      },
      {
       "type": "array"
      }
     ]
    },
    "ca_secret": {
     "type": "string"
    },
    "cacert_chain_file": {
     "type": "string"
    },
    "cert_chain_file": {
     "type": "string"
    },
    "cert_required": {
     "type": "boolean"
    },
    "cipher_suites": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "ecdh_curves": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "hosts": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "max_tls_version": {
     "enum": [
      "v1.0",
      "v1.1",
      "v1.2",
      "v1.3"
     ],
     "type": "string"
    },
    "min_tls_version": {
     "enum": [
      "v1.0",
      "v1.1",
      "v1.2",
      "v1.3"
     ],
     "type": "string"
    },
    "private_key_file": {
     "type": "string"
    },
    "redirect_cleartext_from": {
     "type": "integer"
    },
    "secret": {
     "type": "string"
    },
    "secret_namespacing": {
     "type": "boolean"
    },
    "sni": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "TLS Context",
 "type": "object"
}
