schema:
{
 "description": "DBCluster is the Schema for the DBClusters API",
 "properties": {
  "spec": {
   "description": "DBClusterSpec defines the desired state of DBCluster.\n\n\nDetailed information about a cluster.",
   "properties": {
    "availabilityZones": {
     "description": "A list of Amazon EC2 Availability Zones that instances in the cluster can\nbe created in.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "backupRetentionPeriod": {
     "description": "The number of days for which automated backups are retained. You must specify\na minimum value of 1.\n\n\nDefault: 1\n\n\nConstraints:\n\n\n  - Must be a value from 1 to 35.",
     "format": "int64",
     "type": "integer"
    },
    "dbClusterIdentifier": {
     "description": "The cluster identifier. This parameter is stored as a lowercase string.\n\n\nConstraints:\n\n\n  - Must contain from 1 to 63 letters, numbers, or hyphens.\n\n\n  - The first character must be a letter.\n\n\n  - Cannot end with a hyphen or contain two consecutive hyphens.\n\n\nExample: my-cluster",
     "type": "string"
    },
    "dbClusterParameterGroupName": {
     "description": "The name of the cluster parameter group to associate with this cluster.",
     "type": "string"
    },
    "dbSubnetGroupName": {
     "description": "A subnet group to associate with this cluster.\n\n\nConstraints: Must match the name of an existing DBSubnetGroup. Must not be\ndefault.\n\n\nExample: mySubnetgroup",
     "type": "string"
    },
    "dbSubnetGroupRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "deletionProtection": {
     "description": "Specifies whether this cluster can be deleted. If DeletionProtection is enabled,\nthe cluster cannot be deleted unless it is modified and DeletionProtection\nis disabled. DeletionProtection protects clusters from being accidentally\ndeleted.",
     "type": "boolean"
    },
    "destinationRegion": {
     "description": "DestinationRegion is used for presigning the request to a given region.",
     "type": "string"
    },
    "enableCloudwatchLogsExports": {
     "description": "A list of log types that need to be enabled for exporting to Amazon CloudWatch\nLogs. You can enable audit logs or profiler logs. For more information, see\nAuditing Amazon DocumentDB Events (https://docs.aws.amazon.com/documentdb/latest/developerguide/event-auditing.html)\nand Profiling Amazon DocumentDB Operations (https://docs.aws.amazon.com/documentdb/latest/developerguide/profiling.html).",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "engine": {
     "description": "The name of the database engine to be used for this cluster.\n\n\nValid values: docdb",
     "type": "string"
    },
    "engineVersion": {
     "description": "The version number of the database engine to use. The --engine-version will\ndefault to the latest major engine version. For production workloads, we\nrecommend explicitly declaring this parameter with the intended major engine\nversion.",
     "type": "string"
    },
    "globalClusterIdentifier": {
     "description": "The cluster identifier of the new global cluster.",
     "type": "string"
    },
    "kmsKeyID": {
     "description": "The KMS key identifier for an encrypted cluster.\n\n\nThe KMS key identifier is the Amazon Resource Name (ARN) for the KMS encryption\nkey. If you are creating a cluster using the same Amazon Web Services account\nthat owns the KMS encryption key that is used to encrypt the new cluster,\nyou can use the KMS key alias instead of the ARN for the KMS encryption key.\n\n\nIf an encryption key is not specified in KmsKeyId:\n\n\n  - If the StorageEncrypted parameter is true, Amazon DocumentDB uses your\n    default encryption key.\n\n\nKMS creates the default encryption key for your Amazon Web Services account.\nYour Amazon Web Services account has a different default encryption key for\neach Amazon Web Services Regions.",
     "type": "string"
    },
    "kmsKeyRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "masterUserPassword": {
     "description": "The password for the master database user. This password can contain any\nprintable ASCII character except forward slash (/), double quote (\"), or\nthe \"at\" symbol (@).\n\n\nConstraints: Must contain from 8 to 100 characters.",
     "properties": {
      "key": {
       "description": "Key is the key within the secret",
       "type": "string"
      },
      "name": {
       "description": "name is unique within a namespace to reference a secret resource.",
       "type": "string"
      },
      "namespace": {
       "description": "namespace defines the space within which the secret name must be unique.",
       "type": "string"
      }
     },
     "required": [
      "key"
     ],
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    },
    "masterUsername": {
     "description": "The name of the master user for the cluster.\n\n\nConstraints:\n\n\n  - Must be from 1 to 63 letters or numbers.\n\n\n  - The first character must be a letter.\n\n\n  - Cannot be a reserved word for the chosen database engine.",
     "type": "string"
    },
    "port": {
     "description": "The port number on which the instances in the cluster accept connections.",
     "format": "int64",
     "type": "integer"
    },
    "preSignedURL": {
     "description": "Not currently supported.",
     "type": "string"
    },
    "preferredBackupWindow": {
     "description": "The daily time range during which automated backups are created if automated\nbackups are enabled using the BackupRetentionPeriod parameter.\n\n\nThe default is a 30-minute window selected at random from an 8-hour block\nof time for each Amazon Web Services Region.\n\n\nConstraints:\n\n\n  - Must be in the format hh24:mi-hh24:mi.\n\n\n  - Must be in Universal Coordinated Time (UTC).\n\n\n  - Must not conflict with the preferred maintenance window.\n\n\n  - Must be at least 30 minutes.",
     "type": "string"
    },
    "preferredMaintenanceWindow": {
     "description": "The weekly time range during which system maintenance can occur, in Universal\nCoordinated Time (UTC).\n\n\nFormat: ddd:hh24:mi-ddd:hh24:mi\n\n\nThe default is a 30-minute window selected at random from an 8-hour block\nof time for each Amazon Web Services Region, occurring on a random day of\nthe week.\n\n\nValid days: Mon, Tue, Wed, Thu, Fri, Sat, Sun\n\n\nConstraints: Minimum 30-minute window.",
     "type": "string"
    },
    "snapshotIdentifier": {
     "description": "The identifier for the snapshot or cluster snapshot to restore from.\n\n\nYou can use either the name or the Amazon Resource Name (ARN) to specify\na cluster snapshot. However, you can use only the ARN to specify a snapshot.\n\n\nConstraints:\n\n\n  - Must match the identifier of an existing snapshot.",
     "type": "string"
    },
    "sourceRegion": {
     "description": "SourceRegion is the source region where the resource exists. This is not\nsent over the wire and is only used for presigning. This value should always\nhave the same region as the source ARN.",
     "type": "string"
    },
    "storageEncrypted": {
     "description": "Specifies whether the cluster is encrypted.",
     "type": "boolean"
    },
    "storageType": {
     "description": "The storage type to associate with the DB cluster.\n\n\nFor information on storage types for Amazon DocumentDB clusters, see Cluster\nstorage configurations in the Amazon DocumentDB Developer Guide.\n\n\n# Valid values for storage type - standard | iopt1\n\n\n# Default value is standard\n\n\nWhen you create a DocumentDB DB cluster with the storage type set to iopt1,\nthe storage type is returned in the response. The storage type isn't returned\nwhen you set it to standard.",
     "type": "string"
    },
    "tags": {
     "description": "The tags to be assigned to the cluster.",
     "items": {
      "description": "Metadata assigned to an Amazon DocumentDB resource consisting of a key-value\npair.",
      "properties": {
       "key": {
        "type": "string"
       },
       "value": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "vpcSecurityGroupIDs": {
     "description": "A list of EC2 VPC security groups to associate with this cluster.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "vpcSecurityGroupRefs": {
     "items": {
      "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
      "properties": {
       "from": {
        "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
        "properties": {
         "name": {
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "dbClusterIdentifier",
    "engine"
   ],
   "type": "object"
  }
 },
 "title": "DB Cluster",
 "type": "object"
}
