schema:
{
 "description": "Alias is the Schema for the Aliases API",
 "properties": {
  "spec": {
   "properties": {
    "description": {
     "description": "A description of the alias.",
     "type": "string"
    },
    "functionEventInvokeConfig": {
     "description": "Configures options for asynchronous invocation on an alias.\n\n\n- DestinationConfig\nA destination for events after they have been sent to a function for processing.\n\n\nTypes of Destinations:\nFunction - The Amazon Resource Name (ARN) of a Lambda function.\nQueue - The ARN of a standard SQS queue.\nTopic - The ARN of a standard SNS topic.\nEvent Bus - The ARN of an Amazon EventBridge event bus.\n\n\n- MaximumEventAgeInSeconds\nThe maximum age of a request that Lambda sends to a function for processing.\n\n\n- MaximumRetryAttempts\nThe maximum number of times to retry when the function returns an error.",
     "properties": {
      "destinationConfig": {
       "description": "A configuration object that specifies the destination of an event after Lambda\nprocesses it.",
       "properties": {
        "onFailure": {
         "description": "A destination for events that failed processing.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "onSuccess": {
         "description": "A destination for events that were processed successfully.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "functionName": {
       "type": "string"
      },
      "maximumEventAgeInSeconds": {
       "format": "int64",
       "type": "integer"
      },
      "maximumRetryAttempts": {
       "format": "int64",
       "type": "integer"
      },
      "qualifier": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "functionName": {
     "description": "The name of the Lambda function.\n\n\nName formats\n\n\n  - Function name - MyFunction.\n\n\n  - Function ARN - arn:aws:lambda:us-west-2:123456789012:function:MyFunction.\n\n\n  - Partial ARN - 123456789012:function:MyFunction.\n\n\nThe length constraint applies only to the full ARN. If you specify only the\nfunction name, it is limited to 64 characters in length.",
     "type": "string"
    },
    "functionRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "functionVersion": {
     "description": "The function version that the alias invokes.",
     "type": "string"
    },
    "name": {
     "description": "The name of the alias.",
     "type": "string"
    },
    "provisionedConcurrencyConfig": {
     "description": "Configures provisioned concurrency to a function's alias\n\n\n- ProvisionedConcurrentExecutions\nThe amount of provisioned concurrency to allocate for the version or alias.\nMinimum value of 1 is required",
     "properties": {
      "functionName": {
       "type": "string"
      },
      "provisionedConcurrentExecutions": {
       "format": "int64",
       "type": "integer"
      },
      "qualifier": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "routingConfig": {
     "description": "The routing configuration (https://docs.aws.amazon.com/lambda/latest/dg/configuration-aliases.html#configuring-alias-routing)\nof the alias.",
     "properties": {
      "additionalVersionWeights": {
       "additionalProperties": {
        "type": "number"
       },
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "functionVersion",
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Alias",
 "type": "object"
}
