schema:
{
 "description": "Version is the Schema for the Versions API",
 "properties": {
  "spec": {
   "properties": {
    "codeSHA256": {
     "description": "Only publish a version if the hash value matches the value that's specified.\nUse this option to avoid publishing a version if the function code has changed\nsince you last updated it. You can get the hash for the version that you\nuploaded from the output of UpdateFunctionCode.",
     "type": "string"
    },
    "description": {
     "description": "A description for the version to override the description in the function\nconfiguration.",
     "type": "string"
    },
    "functionEventInvokeConfig": {
     "properties": {
      "destinationConfig": {
       "description": "A configuration object that specifies the destination of an event after Lambda\nprocesses it.",
       "properties": {
        "onFailure": {
         "description": "A destination for events that failed processing.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "onSuccess": {
         "description": "A destination for events that were processed successfully.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "functionName": {
       "type": "string"
      },
      "maximumEventAgeInSeconds": {
       "format": "int64",
       "type": "integer"
      },
      "maximumRetryAttempts": {
       "format": "int64",
       "type": "integer"
      },
      "qualifier": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "functionName": {
     "description": "The name of the Lambda function.\n\n\nName formats\n\n\n  - Function name - MyFunction.\n\n\n  - Function ARN - arn:aws:lambda:us-west-2:123456789012:function:MyFunction.\n\n\n  - Partial ARN - 123456789012:function:MyFunction.\n\n\nThe length constraint applies only to the full ARN. If you specify only the\nfunction name, it is limited to 64 characters in length.",
     "type": "string"
    },
    "functionRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "provisionedConcurrencyConfig": {
     "properties": {
      "functionName": {
       "type": "string"
      },
      "provisionedConcurrentExecutions": {
       "format": "int64",
       "type": "integer"
      },
      "qualifier": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "revisionID": {
     "description": "Only update the function if the revision ID matches the ID that's specified.\nUse this option to avoid publishing a version if the function configuration\nhas changed since you last updated it.",
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Version",
 "type": "object"
}
