component.schema:
{
 "description": "Function is the Schema for the Functions API",
 "properties": {
  "spec": {
   "description": "FunctionSpec defines the desired state of Function.",
   "properties": {
    "architectures": {
     "description": "The instruction set architecture that the function supports. Enter a string\narray with one of the valid values (arm64 or x86_64). The default value is\nx86_64.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "code": {
     "description": "The code for the function.",
     "properties": {
      "imageURI": {
       "type": "string"
      },
      "s3Bucket": {
       "type": "string"
      },
      "s3BucketRef": {
       "description": "Reference field for S3Bucket",
       "properties": {
        "from": {
         "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
         "properties": {
          "name": {
           "type": "string"
          },
          "namespace": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "s3Key": {
       "type": "string"
      },
      "s3ObjectVersion": {
       "type": "string"
      },
      "sha256": {
       "type": "string"
      },
      "zipFile": {
       "format": "byte",
       "type": "string"
      }
     },
     "type": "object"
    },
    "codeSigningConfigARN": {
     "description": "To enable code signing for this function, specify the ARN of a code-signing\nconfiguration. A code-signing configuration includes a set of signing profiles,\nwhich define the trusted publishers for this function.",
     "type": "string"
    },
    "deadLetterConfig": {
     "description": "A dead-letter queue configuration that specifies the queue or topic where\nLambda sends asynchronous events when they fail processing. For more information,\nsee Dead-letter queues (https://docs.aws.amazon.com/lambda/latest/dg/invocation-async.html#invocation-dlq).",
     "properties": {
      "targetARN": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "description": {
     "description": "A description of the function.",
     "type": "string"
    },
    "environment": {
     "description": "Environment variables that are accessible from function code during execution.",
     "properties": {
      "variables": {
       "additionalProperties": {
        "type": "string"
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "ephemeralStorage": {
     "description": "The size of the function's /tmp directory in MB. The default value is 512,\nbut can be any whole number between 512 and 10,240 MB.",
     "properties": {
      "size": {
       "format": "int64",
       "type": "integer"
      }
     },
     "type": "object"
    },
    "fileSystemConfigs": {
     "description": "Connection settings for an Amazon EFS file system.",
     "items": {
      "description": "Details about the connection between a Lambda function and an Amazon EFS\nfile system (https://docs.aws.amazon.com/lambda/latest/dg/configuration-filesystem.html).",
      "properties": {
       "arn": {
        "type": "string"
       },
       "localMountPath": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "functionEventInvokeConfig": {
     "description": "Configures options for asynchronous invocation on a function.\n\n\n- DestinationConfig\nA destination for events after they have been sent to a function for processing.\n\n\nTypes of Destinations:\nFunction - The Amazon Resource Name (ARN) of a Lambda function.\nQueue - The ARN of a standard SQS queue.\nTopic - The ARN of a standard SNS topic.\nEvent Bus - The ARN of an Amazon EventBridge event bus.\n\n\n- MaximumEventAgeInSeconds\nThe maximum age of a request that Lambda sends to a function for processing.\n\n\n- MaximumRetryAttempts\nThe maximum number of times to retry when the function returns an error.",
     "properties": {
      "destinationConfig": {
       "description": "A configuration object that specifies the destination of an event after Lambda\nprocesses it.",
       "properties": {
        "onFailure": {
         "description": "A destination for events that failed processing.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "onSuccess": {
         "description": "A destination for events that were processed successfully.",
         "properties": {
          "destination": {
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "functionName": {
       "type": "string"
      },
      "maximumEventAgeInSeconds": {
       "format": "int64",
       "type": "integer"
      },
      "maximumRetryAttempts": {
       "format": "int64",
       "type": "integer"
      },
      "qualifier": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "handler": {
     "description": "The name of the method within your code that Lambda calls to run your function.\nHandler is required if the deployment package is a .zip file archive. The\nformat includes the file name. It can also include namespaces and other qualifiers,\ndepending on the runtime. For more information, see Lambda programming model\n(https://docs.aws.amazon.com/lambda/latest/dg/foundation-progmodel.html).",
     "type": "string"
    },
    "imageConfig": {
     "description": "Container image configuration values (https://docs.aws.amazon.com/lambda/latest/dg/configuration-images.html#configuration-images-settings)\nthat override the values in the container image Dockerfile.",
     "properties": {
      "command": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "entryPoint": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "workingDirectory": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "kmsKeyARN": {
     "description": "The ARN of the Key Management Service (KMS) key that's used to encrypt your\nfunction's environment variables. If it's not provided, Lambda uses a default\nservice key.",
     "type": "string"
    },
    "kmsKeyRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        },
        "namespace": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "layers": {
     "description": "A list of function layers (https://docs.aws.amazon.com/lambda/latest/dg/configuration-layers.html)\nto add to the function's execution environment. Specify each layer by its\nARN, including the version.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "memorySize": {
     "description": "The amount of memory available to the function (https://docs.aws.amazon.com/lambda/latest/dg/configuration-function-common.html#configuration-memory-console)\nat runtime. Increasing the function memory also increases its CPU allocation.\nThe default value is 128 MB. The value can be any multiple of 1 MB.",
     "format": "int64",
     "type": "integer"
    },
    "name": {
     "description": "The name of the Lambda function.\n\n\nName formats\n\n\n  - Function name – my-function.\n\n\n  - Function ARN – arn:aws:lambda:us-west-2:123456789012:function:my-function.\n\n\n  - Partial ARN – 123456789012:function:my-function.\n\n\nThe length constraint applies only to the full ARN. If you specify only the\nfunction name, it is limited to 64 characters in length.",
     "type": "string"
    },
    "packageType": {
     "description": "The type of deployment package. Set to Image for container image and set\nto Zip for .zip file archive.",
     "type": "string"
    },
    "publish": {
     "description": "Set to true to publish the first version of the function during creation.",
     "type": "boolean"
    },
    "reservedConcurrentExecutions": {
     "description": "The number of simultaneous executions to reserve for the function.",
     "format": "int64",
     "type": "integer"
    },
    "role": {
     "description": "The Amazon Resource Name (ARN) of the function's execution role.",
     "type": "string"
    },
    "roleRef": {
     "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
     "properties": {
      "from": {
       "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
       "properties": {
        "name": {
         "type": "string"
        },
        "namespace": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "runtime": {
     "description": "The identifier of the function's runtime (https://docs.aws.amazon.com/lambda/latest/dg/lambda-runtimes.html).\nRuntime is required if the deployment package is a .zip file archive.",
     "type": "string"
    },
    "snapStart": {
     "description": "The function's SnapStart (https://docs.aws.amazon.com/lambda/latest/dg/snapstart.html)\nsetting.",
     "properties": {
      "applyOn": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "tags": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "A list of tags (https://docs.aws.amazon.com/lambda/latest/dg/tagging.html)\nto apply to the function.",
     "type": "object"
    },
    "timeout": {
     "description": "The amount of time (in seconds) that Lambda allows a function to run before\nstopping it. The default is 3 seconds. The maximum allowed value is 900 seconds.\nFor more information, see Lambda execution environment (https://docs.aws.amazon.com/lambda/latest/dg/runtimes-context.html).",
     "format": "int64",
     "type": "integer"
    },
    "tracingConfig": {
     "description": "Set Mode to Active to sample and trace a subset of incoming requests with\nX-Ray (https://docs.aws.amazon.com/lambda/latest/dg/services-xray.html).",
     "properties": {
      "mode": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "vpcConfig": {
     "description": "For network connectivity to Amazon Web Services resources in a VPC, specify\na list of security groups and subnets in the VPC. When you connect a function\nto a VPC, it can access resources and the internet only through that VPC.\nFor more information, see Configuring a Lambda function to access resources\nin a VPC (https://docs.aws.amazon.com/lambda/latest/dg/configuration-vpc.html).",
     "properties": {
      "securityGroupIDs": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "securityGroupRefs": {
       "description": "Reference field for SecurityGroupIDs",
       "items": {
        "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
        "properties": {
         "from": {
          "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
          "properties": {
           "name": {
            "type": "string"
           },
           "namespace": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "subnetIDs": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "subnetRefs": {
       "description": "Reference field for SubnetIDs",
       "items": {
        "description": "AWSResourceReferenceWrapper provides a wrapper around *AWSResourceReference\ntype to provide more user friendly syntax for references using 'from' field\nEx:\nAPIIDRef:\n\n\n\tfrom:\n\t  name: my-api",
        "properties": {
         "from": {
          "description": "AWSResourceReference provides all the values necessary to reference another\nk8s resource for finding the identifier(Id/ARN/Name)",
          "properties": {
           "name": {
            "type": "string"
           },
           "namespace": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "code",
    "name"
   ],
   "type": "object"
  }
 },
 "title": "Function",
 "type": "object"
}
