component.schema:
{
 "description": "Bucket is the Schema for the Buckets API",
 "properties": {
  "spec": {
   "description": "BucketSpec defines the desired state of Bucket.\n\n\nIn terms of implementation, a Bucket is a resource. An Amazon S3 bucket name\nis globally unique, and the namespace is shared by all Amazon Web Services\naccounts.",
   "properties": {
    "accelerate": {
     "description": "Container for setting the transfer acceleration state.",
     "properties": {
      "status": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "acl": {
     "description": "The canned ACL to apply to the bucket.",
     "type": "string"
    },
    "analytics": {
     "items": {
      "description": "Specifies the configuration and any analyses for the analytics filter of\nan Amazon S3 bucket.",
      "properties": {
       "filter": {
        "description": "The filter used to describe a set of objects for analyses. A filter must\nhave exactly one prefix, one tag, or one conjunction (AnalyticsAndOperator).\nIf no filter is provided, all objects will be considered in any analysis.",
        "properties": {
         "and": {
          "description": "A conjunction (logical AND) of predicates, which is used in evaluating a\nmetrics filter. The operator must have at least two predicates in any combination,\nand an object must match all of the predicates for the filter to apply.",
          "properties": {
           "prefix": {
            "type": "string"
           },
           "tags": {
            "items": {
             "description": "A container of a key value name pair.",
             "properties": {
              "key": {
               "type": "string"
              },
              "value": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           }
          },
          "type": "object"
         },
         "prefix": {
          "type": "string"
         },
         "tag": {
          "description": "A container of a key value name pair.",
          "properties": {
           "key": {
            "type": "string"
           },
           "value": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "id": {
        "type": "string"
       },
       "storageClassAnalysis": {
        "description": "Specifies data related to access patterns to be collected and made available\nto analyze the tradeoffs between different storage classes for an Amazon\nS3 bucket.",
        "properties": {
         "dataExport": {
          "description": "Container for data related to the storage class analysis for an Amazon S3\nbucket for export.",
          "properties": {
           "destination": {
            "description": "Where to publish the analytics results.",
            "properties": {
             "s3BucketDestination": {
              "description": "Contains information about where to publish the analytics results.",
              "properties": {
               "bucket": {
                "type": "string"
               },
               "bucketAccountID": {
                "type": "string"
               },
               "format": {
                "type": "string"
               },
               "prefix": {
                "type": "string"
               }
              },
              "type": "object"
             }
            },
            "type": "object"
           },
           "outputSchemaVersion": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "cors": {
     "description": "Describes the cross-origin access configuration for objects in an Amazon\nS3 bucket. For more information, see Enabling Cross-Origin Resource Sharing\n(https://docs.aws.amazon.com/AmazonS3/latest/dev/cors.html) in the Amazon\nS3 User Guide.",
     "properties": {
      "corsRules": {
       "items": {
        "description": "Specifies a cross-origin access rule for an Amazon S3 bucket.",
        "properties": {
         "allowedHeaders": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "allowedMethods": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "allowedOrigins": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "exposeHeaders": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "id": {
          "type": "string"
         },
         "maxAgeSeconds": {
          "format": "int64",
          "type": "integer"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "createBucketConfiguration": {
     "description": "The configuration information for the bucket.",
     "properties": {
      "locationConstraint": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "encryption": {
     "description": "Specifies the default server-side-encryption configuration.",
     "properties": {
      "rules": {
       "items": {
        "description": "Specifies the default server-side encryption configuration.",
        "properties": {
         "applyServerSideEncryptionByDefault": {
          "description": "Describes the default server-side encryption to apply to new objects in the\nbucket. If a PUT Object request doesn't specify any server-side encryption,\nthis default encryption will be applied. If you don't specify a customer\nmanaged key at configuration, Amazon S3 automatically creates an Amazon Web\nServices KMS key in your Amazon Web Services account the first time that\nyou add an object encrypted with SSE-KMS to a bucket. By default, Amazon\nS3 uses this KMS key for SSE-KMS. For more information, see PUT Bucket encryption\n(https://docs.aws.amazon.com/AmazonS3/latest/API/RESTBucketPUTencryption.html)\nin the Amazon S3 API Reference.",
          "properties": {
           "kmsMasterKeyID": {
            "type": "string"
           },
           "sseAlgorithm": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "bucketKeyEnabled": {
          "type": "boolean"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "grantFullControl": {
     "description": "Allows grantee the read, write, read ACP, and write ACP permissions on the\nbucket.",
     "type": "string"
    },
    "grantRead": {
     "description": "Allows grantee to list the objects in the bucket.",
     "type": "string"
    },
    "grantReadACP": {
     "description": "Allows grantee to read the bucket ACL.",
     "type": "string"
    },
    "grantWrite": {
     "description": "Allows grantee to create new objects in the bucket.\n\n\nFor the bucket and object owners of existing objects, also allows deletions\nand overwrites of those objects.",
     "type": "string"
    },
    "grantWriteACP": {
     "description": "Allows grantee to write the ACL for the applicable bucket.",
     "type": "string"
    },
    "intelligentTiering": {
     "items": {
      "description": "Specifies the S3 Intelligent-Tiering configuration for an Amazon S3 bucket.\n\n\nFor information about the S3 Intelligent-Tiering storage class, see Storage\nclass for automatically optimizing frequently and infrequently accessed objects\n(https://docs.aws.amazon.com/AmazonS3/latest/dev/storage-class-intro.html#sc-dynamic-data-access).",
      "properties": {
       "filter": {
        "description": "The Filter is used to identify objects that the S3 Intelligent-Tiering configuration\napplies to.",
        "properties": {
         "and": {
          "description": "A container for specifying S3 Intelligent-Tiering filters. The filters determine\nthe subset of objects to which the rule applies.",
          "properties": {
           "prefix": {
            "type": "string"
           },
           "tags": {
            "items": {
             "description": "A container of a key value name pair.",
             "properties": {
              "key": {
               "type": "string"
              },
              "value": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           }
          },
          "type": "object"
         },
         "prefix": {
          "type": "string"
         },
         "tag": {
          "description": "A container of a key value name pair.",
          "properties": {
           "key": {
            "type": "string"
           },
           "value": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "id": {
        "type": "string"
       },
       "status": {
        "type": "string"
       },
       "tierings": {
        "items": {
         "description": "The S3 Intelligent-Tiering storage class is designed to optimize storage\ncosts by automatically moving data to the most cost-effective storage access\ntier, without additional operational overhead.",
         "properties": {
          "accessTier": {
           "type": "string"
          },
          "days": {
           "format": "int64",
           "type": "integer"
          }
         },
         "type": "object"
        },
        "type": "array"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "inventory": {
     "items": {
      "description": "Specifies the inventory configuration for an Amazon S3 bucket. For more information,\nsee GET Bucket inventory (https://docs.aws.amazon.com/AmazonS3/latest/API/RESTBucketGETInventoryConfig.html)\nin the Amazon S3 API Reference.",
      "properties": {
       "destination": {
        "description": "Specifies the inventory configuration for an Amazon S3 bucket.",
        "properties": {
         "s3BucketDestination": {
          "description": "Contains the bucket name, file format, bucket owner (optional), and prefix\n(optional) where inventory results are published.",
          "properties": {
           "accountID": {
            "type": "string"
           },
           "bucket": {
            "type": "string"
           },
           "encryption": {
            "description": "Contains the type of server-side encryption used to encrypt the inventory\nresults.",
            "properties": {
             "sseKMS": {
              "description": "Specifies the use of SSE-KMS to encrypt delivered inventory reports.",
              "properties": {
               "keyID": {
                "type": "string"
               }
              },
              "type": "object"
             }
            },
            "type": "object"
           },
           "format": {
            "type": "string"
           },
           "prefix": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "filter": {
        "description": "Specifies an inventory filter. The inventory only includes objects that meet\nthe filter's criteria.",
        "properties": {
         "prefix": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "id": {
        "type": "string"
       },
       "includedObjectVersions": {
        "type": "string"
       },
       "isEnabled": {
        "type": "boolean"
       },
       "optionalFields": {
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "schedule": {
        "description": "Specifies the schedule for generating inventory results.",
        "properties": {
         "frequency": {
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "lifecycle": {
     "description": "Container for lifecycle rules. You can add as many as 1,000 rules.",
     "properties": {
      "rules": {
       "items": {
        "description": "A lifecycle rule for individual objects in an Amazon S3 bucket.",
        "properties": {
         "abortIncompleteMultipartUpload": {
          "description": "Specifies the days since the initiation of an incomplete multipart upload\nthat Amazon S3 will wait before permanently removing all parts of the upload.\nFor more information, see Aborting Incomplete Multipart Uploads Using a Bucket\nLifecycle Policy (https://docs.aws.amazon.com/AmazonS3/latest/dev/mpuoverview.html#mpu-abort-incomplete-mpu-lifecycle-config)\nin the Amazon S3 User Guide.",
          "properties": {
           "daysAfterInitiation": {
            "format": "int64",
            "type": "integer"
           }
          },
          "type": "object"
         },
         "expiration": {
          "description": "Container for the expiration for the lifecycle of the object.",
          "properties": {
           "date": {
            "format": "date-time",
            "type": "string"
           },
           "days": {
            "format": "int64",
            "type": "integer"
           },
           "expiredObjectDeleteMarker": {
            "type": "boolean"
           }
          },
          "type": "object"
         },
         "filter": {
          "description": "The Filter is used to identify objects that a Lifecycle Rule applies to.\nA Filter must have exactly one of Prefix, Tag, or And specified.",
          "properties": {
           "and": {
            "description": "This is used in a Lifecycle Rule Filter to apply a logical AND to two or\nmore predicates. The Lifecycle Rule will apply to any object matching all\nof the predicates configured inside the And operator.",
            "properties": {
             "objectSizeGreaterThan": {
              "format": "int64",
              "type": "integer"
             },
             "objectSizeLessThan": {
              "format": "int64",
              "type": "integer"
             },
             "prefix": {
              "type": "string"
             },
             "tags": {
              "items": {
               "description": "A container of a key value name pair.",
               "properties": {
                "key": {
                 "type": "string"
                },
                "value": {
                 "type": "string"
                }
               },
               "type": "object"
              },
              "type": "array"
             }
            },
            "type": "object"
           },
           "objectSizeGreaterThan": {
            "format": "int64",
            "type": "integer"
           },
           "objectSizeLessThan": {
            "format": "int64",
            "type": "integer"
           },
           "prefix": {
            "type": "string"
           },
           "tag": {
            "description": "A container of a key value name pair.",
            "properties": {
             "key": {
              "type": "string"
             },
             "value": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "id": {
          "type": "string"
         },
         "noncurrentVersionExpiration": {
          "description": "Specifies when noncurrent object versions expire. Upon expiration, Amazon\nS3 permanently deletes the noncurrent object versions. You set this lifecycle\nconfiguration action on a bucket that has versioning enabled (or suspended)\nto request that Amazon S3 delete noncurrent object versions at a specific\nperiod in the object's lifetime.",
          "properties": {
           "newerNoncurrentVersions": {
            "format": "int64",
            "type": "integer"
           },
           "noncurrentDays": {
            "format": "int64",
            "type": "integer"
           }
          },
          "type": "object"
         },
         "noncurrentVersionTransitions": {
          "items": {
           "description": "Container for the transition rule that describes when noncurrent objects\ntransition to the STANDARD_IA, ONEZONE_IA, INTELLIGENT_TIERING, GLACIER_IR,\nGLACIER, or DEEP_ARCHIVE storage class. If your bucket is versioning-enabled\n(or versioning is suspended), you can set this action to request that Amazon\nS3 transition noncurrent object versions to the STANDARD_IA, ONEZONE_IA,\nINTELLIGENT_TIERING, GLACIER_IR, GLACIER, or DEEP_ARCHIVE storage class at\na specific period in the object's lifetime.",
           "properties": {
            "newerNoncurrentVersions": {
             "format": "int64",
             "type": "integer"
            },
            "noncurrentDays": {
             "format": "int64",
             "type": "integer"
            },
            "storageClass": {
             "type": "string"
            }
           },
           "type": "object"
          },
          "type": "array"
         },
         "prefix": {
          "type": "string"
         },
         "status": {
          "type": "string"
         },
         "transitions": {
          "items": {
           "description": "Specifies when an object transitions to a specified storage class. For more\ninformation about Amazon S3 lifecycle configuration rules, see Transitioning\nObjects Using Amazon S3 Lifecycle (https://docs.aws.amazon.com/AmazonS3/latest/dev/lifecycle-transition-general-considerations.html)\nin the Amazon S3 User Guide.",
           "properties": {
            "date": {
             "format": "date-time",
             "type": "string"
            },
            "days": {
             "format": "int64",
             "type": "integer"
            },
            "storageClass": {
             "type": "string"
            }
           },
           "type": "object"
          },
          "type": "array"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "logging": {
     "description": "Container for logging status information.",
     "properties": {
      "loggingEnabled": {
       "description": "Describes where logs are stored and the prefix that Amazon S3 assigns to\nall log object keys for a bucket. For more information, see PUT Bucket logging\n(https://docs.aws.amazon.com/AmazonS3/latest/API/RESTBucketPUTlogging.html)\nin the Amazon S3 API Reference.",
       "properties": {
        "targetBucket": {
         "type": "string"
        },
        "targetGrants": {
         "items": {
          "description": "Container for granting information.\n\n\nBuckets that use the bucket owner enforced setting for Object Ownership don't\nsupport target grants. For more information, see Permissions server access\nlog delivery (https://docs.aws.amazon.com/AmazonS3/latest/userguide/enable-server-access-logging.html#grant-log-delivery-permissions-general)\nin the Amazon S3 User Guide.",
          "properties": {
           "grantee": {
            "description": "Container for the person being granted permissions.",
            "properties": {
             "displayName": {
              "type": "string"
             },
             "emailAddress": {
              "type": "string"
             },
             "id": {
              "type": "string"
             },
             "type_": {
              "type": "string"
             },
             "uRI": {
              "type": "string"
             }
            },
            "type": "object"
           },
           "permission": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "targetPrefix": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "metrics": {
     "items": {
      "description": "Specifies a metrics configuration for the CloudWatch request metrics (specified\nby the metrics configuration ID) from an Amazon S3 bucket. If you're updating\nan existing metrics configuration, note that this is a full replacement of\nthe existing metrics configuration. If you don't include the elements you\nwant to keep, they are erased. For more information, see PutBucketMetricsConfiguration\n(https://docs.aws.amazon.com/AmazonS3/latest/API/RESTBucketPUTMetricConfiguration.html).",
      "properties": {
       "filter": {
        "description": "Specifies a metrics configuration filter. The metrics configuration only\nincludes objects that meet the filter's criteria. A filter must be a prefix,\nan object tag, an access point ARN, or a conjunction (MetricsAndOperator).\nFor more information, see PutBucketMetricsConfiguration (https://docs.aws.amazon.com/AmazonS3/latest/API/API_PutBucketMetricsConfiguration.html).",
        "properties": {
         "accessPointARN": {
          "type": "string"
         },
         "and": {
          "description": "A conjunction (logical AND) of predicates, which is used in evaluating a\nmetrics filter. The operator must have at least two predicates, and an object\nmust match all of the predicates in order for the filter to apply.",
          "properties": {
           "accessPointARN": {
            "type": "string"
           },
           "prefix": {
            "type": "string"
           },
           "tags": {
            "items": {
             "description": "A container of a key value name pair.",
             "properties": {
              "key": {
               "type": "string"
              },
              "value": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           }
          },
          "type": "object"
         },
         "prefix": {
          "type": "string"
         },
         "tag": {
          "description": "A container of a key value name pair.",
          "properties": {
           "key": {
            "type": "string"
           },
           "value": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "id": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "name": {
     "description": "The name of the bucket to create.",
     "type": "string"
    },
    "notification": {
     "description": "A container for specifying the notification configuration of the bucket.\nIf this element is empty, notifications are turned off for the bucket.",
     "properties": {
      "lambdaFunctionConfigurations": {
       "items": {
        "description": "A container for specifying the configuration for Lambda notifications.",
        "properties": {
         "events": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "filter": {
          "description": "Specifies object key name filtering rules. For information about key name\nfiltering, see Configuring Event Notifications (https://docs.aws.amazon.com/AmazonS3/latest/dev/NotificationHowTo.html)\nin the Amazon S3 User Guide.",
          "properties": {
           "key": {
            "description": "A container for object key name prefix and suffix filtering rules.",
            "properties": {
             "filterRules": {
              "description": "A list of containers for the key-value pair that defines the criteria for\nthe filter rule.",
              "items": {
               "description": "Specifies the Amazon S3 object key name to filter on and whether to filter\non the suffix or prefix of the key name.",
               "properties": {
                "name": {
                 "type": "string"
                },
                "value": {
                 "type": "string"
                }
               },
               "type": "object"
              },
              "type": "array"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "id": {
          "description": "An optional unique identifier for configurations in a notification configuration.\nIf you don't provide one, Amazon S3 will assign an ID.",
          "type": "string"
         },
         "lambdaFunctionARN": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "queueConfigurations": {
       "items": {
        "description": "Specifies the configuration for publishing messages to an Amazon Simple Queue\nService (Amazon SQS) queue when Amazon S3 detects specified events.",
        "properties": {
         "events": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "filter": {
          "description": "Specifies object key name filtering rules. For information about key name\nfiltering, see Configuring Event Notifications (https://docs.aws.amazon.com/AmazonS3/latest/dev/NotificationHowTo.html)\nin the Amazon S3 User Guide.",
          "properties": {
           "key": {
            "description": "A container for object key name prefix and suffix filtering rules.",
            "properties": {
             "filterRules": {
              "description": "A list of containers for the key-value pair that defines the criteria for\nthe filter rule.",
              "items": {
               "description": "Specifies the Amazon S3 object key name to filter on and whether to filter\non the suffix or prefix of the key name.",
               "properties": {
                "name": {
                 "type": "string"
                },
                "value": {
                 "type": "string"
                }
               },
               "type": "object"
              },
              "type": "array"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "id": {
          "description": "An optional unique identifier for configurations in a notification configuration.\nIf you don't provide one, Amazon S3 will assign an ID.",
          "type": "string"
         },
         "queueARN": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "topicConfigurations": {
       "items": {
        "description": "A container for specifying the configuration for publication of messages\nto an Amazon Simple Notification Service (Amazon SNS) topic when Amazon S3\ndetects specified events.",
        "properties": {
         "events": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "filter": {
          "description": "Specifies object key name filtering rules. For information about key name\nfiltering, see Configuring Event Notifications (https://docs.aws.amazon.com/AmazonS3/latest/dev/NotificationHowTo.html)\nin the Amazon S3 User Guide.",
          "properties": {
           "key": {
            "description": "A container for object key name prefix and suffix filtering rules.",
            "properties": {
             "filterRules": {
              "description": "A list of containers for the key-value pair that defines the criteria for\nthe filter rule.",
              "items": {
               "description": "Specifies the Amazon S3 object key name to filter on and whether to filter\non the suffix or prefix of the key name.",
               "properties": {
                "name": {
                 "type": "string"
                },
                "value": {
                 "type": "string"
                }
               },
               "type": "object"
              },
              "type": "array"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         },
         "id": {
          "description": "An optional unique identifier for configurations in a notification configuration.\nIf you don't provide one, Amazon S3 will assign an ID.",
          "type": "string"
         },
         "topicARN": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "objectLockEnabledForBucket": {
     "description": "Specifies whether you want S3 Object Lock to be enabled for the new bucket.",
     "type": "boolean"
    },
    "objectOwnership": {
     "type": "string"
