{
 "description": "Generator information:\n- Generated from: /storage/resource-manager/Microsoft.Storage/stable/2022-09-01/file.json\n- ARM URI: /subscriptions/{subscriptionId}/resourceGroups/{resourceGroupName}/providers/Microsoft.Storage/storageAccounts/{accountName}/fileServices/default/shares/{shareName}",
 "properties": {
  "spec": {
   "properties": {
    "accessTier": {
     "description": "AccessTier: Access tier for specific share. GpV2 account can choose between TransactionOptimized (default), Hot, and\nCool. FileStorage account can choose Premium.",
     "enum": [
      "Cool",
      "Hot",
      "Premium",
      "TransactionOptimized"
     ],
     "type": "string"
    },
    "azureName": {
     "description": "AzureName: The name of the resource in Azure. This is often the same as the name of the resource in Kubernetes but it\ndoesn't have to be.",
     "maxLength": 63,
     "minLength": 3,
     "type": "string"
    },
    "enabledProtocols": {
     "description": "EnabledProtocols: The authentication protocol that is used for the file share. Can only be specified when creating a\nshare.",
     "enum": [
      "NFS",
      "SMB"
     ],
     "type": "string"
    },
    "metadata": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "Metadata: A name-value pair to associate with the share as metadata.",
     "type": "object"
    },
    "operatorSpec": {
     "description": "OperatorSpec: The specification for configuring operator behavior. This field is interpreted by the operator and not\npassed directly to Azure",
     "properties": {
      "configMapExpressions": {
       "description": "ConfigMapExpressions: configures where to place operator written dynamic ConfigMaps (created with CEL expressions).",
       "items": {
        "description": "DestinationExpression is a CEL expression and a destination to store the result in. The destination may\nbe a secret or a configmap. The value of the expression is stored at the specified location in\nthe destination.",
        "properties": {
         "key": {
          "description": "Key is the key in the ConfigMap or Secret being written to. If the CEL expression in Value returns a string\nthis is required to identify what key to write to. If the CEL expression in Value returns a map[string]string\nKey must not be set, instead the keys written will be determined dynamically based on the keys of the resulting\nmap[string]string.",
          "type": "string"
         },
         "name": {
          "description": "Name is the name of the Kubernetes configmap or secret to write to.\nThe configmap or secret will be created in the same namespace as the resource.",
          "type": "string"
         },
         "value": {
          "description": "Value is a CEL expression. The CEL expression may return a string or a map[string]string. For more information\non CEL in ASO see https://azure.github.io/azure-service-operator/guide/expressions/",
          "type": "string"
         }
        },
        "required": [
         "name",
         "value"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "secretExpressions": {
       "description": "SecretExpressions: configures where to place operator written dynamic secrets (created with CEL expressions).",
       "items": {
        "description": "DestinationExpression is a CEL expression and a destination to store the result in. The destination may\nbe a secret or a configmap. The value of the expression is stored at the specified location in\nthe destination.",
        "properties": {
         "key": {
          "description": "Key is the key in the ConfigMap or Secret being written to. If the CEL expression in Value returns a string\nthis is required to identify what key to write to. If the CEL expression in Value returns a map[string]string\nKey must not be set, instead the keys written will be determined dynamically based on the keys of the resulting\nmap[string]string.",
          "type": "string"
         },
         "name": {
          "description": "Name is the name of the Kubernetes configmap or secret to write to.\nThe configmap or secret will be created in the same namespace as the resource.",
          "type": "string"
         },
         "value": {
          "description": "Value is a CEL expression. The CEL expression may return a string or a map[string]string. For more information\non CEL in ASO see https://azure.github.io/azure-service-operator/guide/expressions/",
          "type": "string"
         }
        },
        "required": [
         "name",
         "value"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
