schema:
{
 "description": "Canary is the Schema for the canaries API",
 "properties": {
  "spec": {
   "description": "CanarySpec defines the desired state of Canary",
   "properties": {
    "analysis": {
     "description": "metrics and thresholds",
     "properties": {
      "interval": {
       "type": "integer"
      },
      "maxFailures": {
       "type": "integer"
      },
      "metrics": {
       "items": {
        "description": "CanaryMetric holds the reference to Istio metrics used for canary analysis",
        "properties": {
         "name": {
          "enum": [
           "RequestSuccessRate",
           "RequestDuration"
          ],
          "type": "string"
         },
         "query": {
          "type": "string"
         },
         "threshold": {
          "description": "TODO float64",
          "type": "integer"
         }
        },
        "required": [
         "name",
         "threshold"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "service": {
     "description": "virtual service spec",
     "properties": {
      "gateways": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "hosts": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "labelSelectors": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "name": {
       "type": "string"
      },
      "ports": {
       "items": {
        "description": "ServicePort contains information on service's port.",
        "properties": {
         "appProtocol": {
          "description": "The application protocol for this port. This field follows standard Kubernetes label syntax. Un-prefixed names are reserved for IANA standard service names (as per RFC-6335 and http://www.iana.org/assignments/service-names). Non-standard protocols should use prefixed names such as mycompany.com/my-custom-protocol. Field can be enabled with ServiceAppProtocol feature gate.",
          "type": "string"
         },
         "name": {
          "description": "The name of this port within the service. This must be a DNS_LABEL. All ports within a ServiceSpec must have unique names. When considering the endpoints for a Service, this must match the 'name' field in the EndpointPort. Optional if only one ServicePort is defined on this service.",
          "type": "string"
         },
         "nodePort": {
          "description": "The port on each node on which this service is exposed when type=NodePort or LoadBalancer. Usually assigned by the system. If specified, it will be allocated to the service if unused or else creation of the service will fail. Default is to auto-allocate a port if the ServiceType of this Service requires one. More info: https://kubernetes.io/docs/concepts/services-networking/service/#type-nodeport",
          "format": "int32",
          "type": "integer"
         },
         "port": {
          "description": "The port that will be exposed by this service.",
          "format": "int32",
          "type": "integer"
         },
         "protocol": {
          "description": "The IP protocol for this port. Supports \"TCP\", \"UDP\", and \"SCTP\". Default is TCP.",
          "type": "string"
         },
         "targetPort": {
          "anyOf": [
           {
            "type": "integer"
           },
           {
            "type": "string"
           }
          ],
          "description": "Number or name of the port to access on the pods targeted by the service. Number must be in the range 1 to 65535. Name must be an IANA_SVC_NAME. If this is a string, it will be looked up as a named port in the target Pod's container ports. If this is not specified, the value of the 'port' field is used (an identity map). This field is ignored for services with clusterIP=None, and should be omitted or set equal to the 'port' field. More info: https://kubernetes.io/docs/concepts/services-networking/service/#defining-a-service",
          "x-kubernetes-int-or-string": true
         }
        },
        "required": [
         "port"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "stepWeight": {
     "description": "Traffic shifting weight increment for each successful step",
     "type": "integer"
    },
    "targetRef": {
     "description": "reference to target resource",
     "properties": {
      "apiVersion": {
       "description": "API version of the referent",
       "enum": [
        "apps/v1"
       ],
       "type": "string"
      },
      "kind": {
       "description": "Kind of the referent; More info: https://git.k8s.io/community/contributors/devel/api-conventions.md#types-kinds\"",
       "enum": [
        "Deployment"
       ],
       "type": "string"
      },
      "name": {
       "description": "Name of the referent; More info: http://kubernetes.io/docs/user-guide/identifiers#names",
       "type": "string"
      }
     },
     "required": [
      "kind",
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "targetRef"
   ],
   "type": "object"
  }
 },
 "title": "Canary",
 "type": "object"
}
