schema:
{
 "properties": {
  "spec": {
   "description": "Configuration for access control on workloads. See more details at: https://istio.io/docs/reference/config/security/authorization-policy.html",
   "oneOf": [
    {
     "not": {
      "anyOf": [
       {
        "required": [
         "provider"
        ]
       }
      ]
     }
    },
    {
     "required": [
      "provider"
     ]
    }
   ],
   "properties": {
    "action": {
     "description": "Optional.",
     "enum": [
      "ALLOW",
      "DENY",
      "AUDIT",
      "CUSTOM"
     ],
     "type": "string"
    },
    "provider": {
     "description": "Specifies detailed configuration of the CUSTOM action.",
     "properties": {
      "name": {
       "description": "Specifies the name of the extension provider.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "rules": {
     "description": "Optional.",
     "items": {
      "properties": {
       "from": {
        "description": "Optional.",
        "items": {
         "properties": {
          "source": {
           "description": "Source specifies the source of a request.",
           "properties": {
            "ipBlocks": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "namespaces": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notIpBlocks": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notNamespaces": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notPrincipals": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notRemoteIpBlocks": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notRequestPrincipals": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "principals": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "remoteIpBlocks": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "requestPrincipals": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "to": {
        "description": "Optional.",
        "items": {
         "properties": {
          "operation": {
           "description": "Operation specifies the operation of a request.",
           "properties": {
            "hosts": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "methods": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notHosts": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notMethods": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notPaths": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "notPorts": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "paths": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "ports": {
             "description": "Optional.",
             "items": {
              "type": "string"
             },
             "type": "array"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "type": "array"
       },
       "when": {
        "description": "Optional.",
        "items": {
         "properties": {
          "key": {
           "description": "The name of an Istio attribute.",
           "type": "string"
          },
          "notValues": {
           "description": "Optional.",
           "items": {
            "type": "string"
           },
           "type": "array"
          },
          "values": {
           "description": "Optional.",
           "items": {
            "type": "string"
           },
           "type": "array"
          }
         },
         "required": [
          "key"
         ],
         "type": "object"
        },
        "type": "array"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "selector": {
     "description": "Optional.",
     "properties": {
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "One or more labels that indicate a specific set of pods/VMs on which a policy should be applied.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "targetRef": {
     "properties": {
      "group": {
       "type": "string"
      },
      "kind": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Authorization Policy",
 "type": "object"
}
