component.schema:
{
 "description": "Pipe is the Schema for the Pipe API.",
 "properties": {
  "spec": {
   "description": "the specification of a Pipe",
   "properties": {
    "dependencies": {
     "description": "the list of Camel or Maven dependencies required by the Pipe",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "errorHandler": {
     "description": "ErrorHandler is an optional handler called upon an error occurring in the integration",
     "format": "textarea",
     "type": "string"
    },
    "integration": {
     "description": "Integration is an optional integration used to specify custom parameters\nDeprecated don't use this. Use trait annotations if you need to change any cluster configuration.",
     "properties": {
      "configuration": {
       "description": "Deprecated:\nUse camel trait (camel.properties) to manage properties\nUse mount trait (mount.configs) to manage configs\nUse mount trait (mount.resources) to manage resources\nUse mount trait (mount.volumes) to manage volumes",
       "items": {
        "description": "ConfigurationSpec represents a generic configuration specification.",
        "properties": {
         "type": {
          "description": "represents the type of configuration, ie: property, configmap, secret, ...",
          "type": "string"
         },
         "value": {
          "description": "the value to assign to the configuration (syntax may vary depending on the `Type`)",
          "type": "string"
         }
        },
        "required": [
         "type",
         "value"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "dependencies": {
       "description": "the list of Camel or Maven dependencies required by the Integration",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "flows": {
       "description": "a source in YAML DSL language which contain the routes to run",
       "items": {
        "description": "Flow is an unstructured object representing a Camel Flow in YAML/JSON DSL.",
        "format": "textarea",
        "type": "string"
       },
       "type": "array"
      },
      "git": {
       "description": "the configuration of the project to build on Git",
       "properties": {
        "branch": {
         "description": "the git branch to check out",
         "type": "string"
        },
        "commit": {
         "description": "the git commit (full SHA) to check out",
         "type": "string"
        },
        "secret": {
         "description": "the Kubernetes secret where token is stored",
         "type": "string"
        },
        "tag": {
         "description": "the git tag to check out",
         "type": "string"
        },
        "url": {
         "description": "the URL of the project",
         "type": "string"
        }
       },
       "type": "object"
      },
      "integrationKit": {
       "description": "the reference of the `IntegrationKit` which is used for this Integration",
       "properties": {
        "apiVersion": {
         "description": "API version of the referent.",
         "type": "string"
        },
        "fieldPath": {
         "description": "If referring to a piece of an object instead of an entire object, this string\nshould contain a valid JSON/Go field access statement, such as desiredState.manifest.containers[2].\nFor example, if the object reference is to a container within a pod, this would take on a value like:\n\"spec.containers{name}\" (where \"name\" refers to the name of the container that triggered\nthe event) or if no container name is specified \"spec.containers[2]\" (container with\nindex 2 in this pod). This syntax is chosen only to have some well-defined way of\nreferencing a part of an object.",
         "type": "string"
        },
        "kind": {
         "description": "Kind of the referent.\nMore info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#types-kinds",
         "type": "string"
        },
        "name": {
         "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
         "type": "string"
        },
        "namespace": {
         "description": "Namespace of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/",
         "type": "string"
        },
        "resourceVersion": {
         "description": "Specific resourceVersion to which this reference is made, if any.\nMore info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#concurrency-control-and-consistency",
         "type": "string"
        },
        "uid": {
         "description": "UID of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#uids",
         "type": "string"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic"
      },
      "profile": {
       "description": "the profile needed to run this Integration",
       "type": "string"
      },
      "replicas": {
       "default": 1,
       "description": "the number of `Pods` needed for the running Integration",
       "format": "int32",
       "type": "integer"
      },
      "repositories": {
       "description": "additional Maven repositories to be used",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "serviceAccountName": {
       "description": "custom SA to use for the Integration",
       "type": "string"
      },
      "sources": {
       "description": "the sources which contain the Camel routes to run",
       "items": {
        "description": "SourceSpec defines the configuration for one or more routes to be executed in a certain Camel DSL language.",
        "properties": {
         "compression": {
          "description": "if the content is compressed (base64 encrypted)",
          "type": "boolean"
         },
         "content": {
          "description": "the source code (plain text)",
          "type": "string"
         },
         "contentKey": {
          "description": "the confimap key holding the source content",
          "type": "string"
         },
         "contentRef": {
          "description": "the confimap reference holding the source content",
          "type": "string"
         },
         "contentType": {
          "description": "the content type (tipically text or binary)",
          "type": "string"
         },
         "from-kamelet": {
          "description": "True if the spec is generated from a Kamelet",
          "type": "boolean"
         },
         "interceptors": {
          "description": "Interceptors are optional identifiers the org.apache.camel.k.RoutesLoader\nuses to pre/post process sources\nDeprecated: no longer in use.",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "language": {
          "description": "specify which is the language (Camel DSL) used to interpret this source code",
          "type": "string"
         },
         "loader": {
          "description": "Loader is an optional id of the org.apache.camel.k.RoutesLoader that will\ninterpret this source at runtime",
          "type": "string"
         },
         "name": {
          "description": "the name of the specification",
          "type": "string"
         },
         "path": {
          "description": "the path where the file is stored",
          "type": "string"
         },
         "property-names": {
          "description": "List of property names defined in the source (e.g. if type is \"template\")",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "rawContent": {
          "description": "the source code (binary)",
          "format": "byte",
          "type": "string"
         },
         "type": {
          "description": "Type defines the kind of source described by this object",
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "template": {
       "description": "Pod template customization\nDeprecated:\nUse container, init-containers or owner traits instead",
       "properties": {
        "spec": {
         "description": "the specification",
         "properties": {
          "activeDeadlineSeconds": {
           "description": "ActiveDeadlineSeconds",
           "format": "int64",
           "type": "integer"
          },
          "automountServiceAccountToken": {
           "description": "AutomountServiceAccountToken",
           "type": "boolean"
          },
          "containers": {
           "description": "Containers",
           "items": {
            "description": "A single application container that you want to run within a pod.",
            "properties": {
             "args": {
              "description": "Arguments to the entrypoint.\nThe container image's CMD is used if this is not provided.\nVariable references $(VAR_NAME) are expanded using the container's environment. If a variable\ncannot be resolved, the reference in the input string will be unchanged. Double $$ are reduced\nto a single $, which allows for escaping the $(VAR_NAME) syntax: i.e. \"$$(VAR_NAME)\" will\nproduce the string literal \"$(VAR_NAME)\". Escaped references will never be expanded, regardless\nof whether the variable exists or not. Cannot be updated.\nMore info: https://kubernetes.io/docs/tasks/inject-data-application/define-command-argument-container/#running-a-command-in-a-shell",
              "items": {
               "type": "string"
              },
              "type": "array",
              "x-kubernetes-list-type": "atomic"
             },
             "command": {
              "description": "Entrypoint array. Not executed within a shell.\nThe container image's ENTRYPOINT is used if this is not provided.\nVariable references $(VAR_NAME) are expanded using the container's environment. If a variable\ncannot be resolved, the reference in the input string will be unchanged. Double $$ are reduced\nto a single $, which allows for escaping the $(VAR_NAME) syntax: i.e. \"$$(VAR_NAME)\" will\nproduce the string literal \"$(VAR_NAME)\". Escaped references will never be expanded, regardless\nof whether the variable exists or not. Cannot be updated.\nMore info: https://kubernetes.io/docs/tasks/inject-data-application/define-command-argument-container/#running-a-command-in-a-shell",
              "items": {
               "type": "string"
              },
              "type": "array",
              "x-kubernetes-list-type": "atomic"
             },
             "env": {
              "description": "List of environment variables to set in the container.\nCannot be updated.",
              "items": {
               "description": "EnvVar represents an environment variable present in a Container.",
               "properties": {
                "name": {
                 "description": "Name of the environment variable.\nMay consist of any printable ASCII characters except '='.",
                 "type": "string"
                },
                "value": {
                 "description": "Variable references $(VAR_NAME) are expanded\nusing the previously defined environment variables in the container and\nany service environment variables. If a variable cannot be resolved,\nthe reference in the input string will be unchanged. Double $$ are reduced\nto a single $, which allows for escaping the $(VAR_NAME) syntax: i.e.\n\"$$(VAR_NAME)\" will produce the string literal \"$(VAR_NAME)\".\nEscaped references will never be expanded, regardless of whether the variable\nexists or not.\nDefaults to \"\".",
                 "type": "string"
                },
                "valueFrom": {
                 "description": "Source for the environment variable's value. Cannot be used if value is not empty.",
                 "properties": {
                  "configMapKeyRef": {
                   "description": "Selects a key of a ConfigMap.",
                   "properties": {
                    "key": {
                     "description": "The key to select.",
                     "type": "string"
                    },
                    "name": {
                     "default": "",
                     "description": "Name of the referent.\nThis field is effectively required, but due to backwards compatibility is\nallowed to be empty. Instances of this type with an empty value here are\nalmost certainly wrong.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
                     "type": "string"
                    },
                    "optional": {
                     "description": "Specify whether the ConfigMap or its key must be defined",
                     "type": "boolean"
                    }
                   },
                   "required": [
                    "key"
                   ],
                   "type": "object",
                   "x-kubernetes-map-type": "atomic"
                  },
                  "fieldRef": {
                   "description": "Selects a field of the pod: supports metadata.name, metadata.namespace, `metadata.labels['\u003cKEY\u003e']`, `metadata.annotations['\u003cKEY\u003e']`,\nspec.nodeName, spec.serviceAccountName, status.hostIP, status.podIP, status.podIPs.",
                   "properties": {
                    "apiVersion": {
                     "description": "Version of the schema the FieldPath is written in terms of, defaults to \"v1\".",
                     "type": "string"
                    },
                    "fieldPath": {
                     "description": "Path of the field to select in the specified API version.",
                     "type": "string"
                    }
                   },
                   "required": [
                    "fieldPath"
                   ],
                   "type": "object",
                   "x-kubernetes-map-type": "atomic"
                  },
                  "fileKeyRef": {
                   "description": "FileKeyRef selects a key of the env file.\nRequires the EnvFiles feature gate to be enabled.",
                   "properties": {
                    "key": {
                     "description": "The key within the env file. An invalid key will prevent the pod from starting.\nThe keys defined within a source may consist of any printable ASCII characters except '='.\nDuring Alpha stage of the EnvFiles feature gate, the key size is limited to 128 characters.",
                     "type": "string"
                    },
                    "optional": {
                     "default": false,
                     "description": "Specify whether the file or its key must be defined. If the file or key\ndoes not exist, then the env var is not published.\nIf optional is set to true and the specified key does not exist,\nthe environment variable will not be set in the Pod's containers.\n\nIf optional is set to false and the specified key does not exist,\nan error will be returned during Pod creation.",
                     "type": "boolean"
                    },
                    "path": {
                     "description": "The path within the volume from which to select the file.\nMust be relative and may not contain the '..' path or start with '..'.",
                     "type": "string"
                    },
                    "volumeName": {
                     "description": "The name of the volume mount containing the env file.",
                     "type": "string"
                    }
                   },
                   "required": [
                    "key",
                    "path",
                    "volumeName"
                   ],
                   "type": "object",
                   "x-kubernetes-map-type": "atomic"
                  },
                  "resourceFieldRef": {
                   "description": "Selects a resource of the container: only resources limits and requests\n(limits.cpu, limits.memory, limits.ephemeral-storage, requests.cpu, requests.memory and requests.ephemeral-storage) are currently supported.",
                   "properties": {
                    "containerName": {
                     "description": "Container name: required for volumes, optional for env vars",
                     "type": "string"
                    },
                    "divisor": {
                     "anyOf": [
                      {
                       "type": "integer"
                      },
                      {
                       "type": "string"
                      }
                     ],
                     "description": "Specifies the output format of the exposed resources, defaults to \"1\"",
                     "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
                     "x-kubernetes-int-or-string": true
                    },
                    "resource": {
                     "description": "Required: resource to select",
                     "type": "string"
                    }
                   },
                   "required": [
                    "resource"
                   ],
                   "type": "object",
                   "x-kubernetes-map-type": "atomic"
                  },
                  "secretKeyRef": {
                   "description": "Selects a key of a secret in the pod's namespace",
                   "properties": {
                    "key": {
                     "description": "The key of the secret to select from.  Must be a valid secret key.",
                     "type": "string"
                    },
                    "name": {
                     "default": "",
                     "description": "Name of the referent.\nThis field is effectively required, but due to backwards compatibility is\nallowed to be empty. Instances of this type with an empty value here are\nalmost certainly wrong.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
                     "type": "string"
                    },
                    "optional": {
                     "description": "Specify whether the Secret or its key must be defined",
                     "type": "boolean"
                    }
                   },
                   "required": [
                    "key"
                   ],
                   "type": "object",
                   "x-kubernetes-map-type": "atomic"
                  }
                 },
                 "type": "object"
                }
               },
               "required": [
                "name"
               ],
               "type": "object"
              },
              "type": "array",
              "x-kubernetes-list-map-keys": [
               "name"
              ],
              "x-kubernetes-list-type": "map"
             },
             "envFrom": {
              "description": "List of sources to populate environment variables in the container.\nThe keys defined within a source may consist of any printable ASCII characters except '='.\nWhen a key exists in multiple\nsources, the value associated with the last source will take precedence.\nValues defined by an Env with a duplicate key will take precedence.\nCannot be updated.",
              "items": {
               "description": "EnvFromSource represents the source of a set of ConfigMaps or Secrets",
               "properties": {
                "configMapRef": {
                 "description": "The ConfigMap to select from",
                 "properties": {
                  "name": {
                   "default": "",
                   "description": "Name of the referent.\nThis field is effectively required, but due to backwards compatibility is\nallowed to be empty. Instances of this type with an empty value here are\nalmost certainly wrong.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
                   "type": "string"
                  },
                  "optional": {
                   "description": "Specify whether the ConfigMap must be defined",
                   "type": "boolean"
                  }
                 },
                 "type": "object",
                 "x-kubernetes-map-type": "atomic"
                },
                "prefix": {
                 "description": "Optional text to prepend to the name of each environment variable.\nMay consist of any printable ASCII characters except '='.",
                 "type": "string"
                },
                "secretRef": {
                 "description": "The Secret to select from",
                 "properties": {
                  "name": {
                   "default": "",
                   "description": "Name of the referent.\nThis field is effectively required, but due to backwards compatibility is\nallowed to be empty. Instances of this type with an empty value here are\nalmost certainly wrong.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
                   "type": "string"
                  },
                  "optional": {
                   "description": "Specify whether the Secret must be defined",
                   "type": "boolean"
                  }
                 },
                 "type": "object",
                 "x-kubernetes-map-type": "atomic"
                }
               },
               "type": "object"
              },
              "type": "array",
              "x-kubernetes-list-type": "atomic"
             },
             "image": {
              "description": "Container image name.\nMore info: https://kubernetes.io/docs/concepts/containers/images\nThis field is optional to allow higher level config management to default or override\ncontainer images in workload controllers like Deployments and StatefulSets.",
              "type": "string"
             },
             "imagePullPolicy": {
              "description": "Image pull policy.\nOne of Always, Never, IfNotPresent.\nDefaults to Always if :latest tag is specified, or IfNotPresent otherwise.\nCannot be updated.\nMore info: https://kubernetes.io/docs/concepts/containers/images#updating-images",
              "type": "string"
             },
             "lifecycle": {
              "description": "Actions that the management system should take in response to container lifecycle events.\nCannot be updated.",
              "properties": {
               "postStart": {
                "description": "PostStart is called immediately after a container is created. If the handler fails,\nthe container is terminated and restarted according to its restart policy.\nOther management of the container blocks until the hook completes.\nMore info: https://kubernetes.io/docs/concepts/containers/container-lifecycle-hooks/#container-hooks",
                "properties": {
                 "exec": {
                  "description": "Exec specifies a command to execute in the container.",
                  "properties": {
                   "command": {
                    "description": "Command is the command line to execute inside the container, the working directory for the\ncommand  is root ('/') in the container's filesystem. The command is simply exec'd, it is\nnot run inside a shell, so traditional shell instructions ('|', etc) won't work. To use\na shell, you need to explicitly call out to that shell.\nExit status of 0 is treated as live/healthy and non-zero is unhealthy.",
                    "items": {
                     "type": "string"
                    },
                    "type": "array",
                    "x-kubernetes-list-type": "atomic"
                   }
                  },
                  "type": "object"
                 },
                 "httpGet": {
                  "description": "HTTPGet specifies an HTTP GET request to perform.",
                  "properties": {
                   "host": {
                    "description": "Host name to connect to, defaults to the pod IP. You probably want to set\n\"Host\" in httpHeaders instead.",
                    "type": "string"
                   },
                   "httpHeaders": {
                    "description": "Custom headers to set in the request. HTTP allows repeated headers.",
                    "items": {
                     "description": "HTTPHeader describes a custom header to be used in HTTP probes",
                     "properties": {
                      "name": {
                       "description": "The header field name.\nThis will be canonicalized upon output, so case-variant names will be understood as the same header.",
                       "type": "string"
                      },
                      "value": {
                       "description": "The header field value",
                       "type": "string"
                      }
                     },
                     "required": [
                      "name",
                      "value"
                     ],
                     "type": "object"
                    },
                    "type": "array",
                    "x-kubernetes-list-type": "atomic"
                   },
                   "path": {
                    "description": "Path to access on the HTTP server.",
                    "type": "string"
                   },
                   "port": {
                    "anyOf": [
                     {
                      "type": "integer"
                     },
                     {
                      "type": "string"
                     }
                    ],
                    "description": "Name or number of the port to access on the container.\nNumber must be in the range 1 to 65535.\nName must be an IANA_SVC_NAME.",
                    "x-kubernetes-int-or-string": true
                   },
                   "scheme": {
                    "description": "Scheme to use for connecting to the host.\nDefaults to HTTP.",
                    "type": "string"
                   }
                  },
                  "required": [
                   "port"
                  ],
                  "type": "object"
                 },
                 "sleep": {
                  "description": "Sleep represents a duration that the container should sleep.",
                  "properties": {
                   "seconds": {
                    "description": "Seconds is the number of seconds to sleep.",
                    "format": "int64",
                    "type": "integer"
                   }
                  },
                  "required": [
                   "seconds"
                  ],
                  "type": "object"
                 },
                 "tcpSocket": {
                  "description": "Deprecated. TCPSocket is NOT supported as a LifecycleHandler and kept\nfor backward compatibility. There is no validation of this field and\nlifecycle hooks will fail at runtime when it is specified.",
                  "properties": {
                   "host": {
                    "description": "Optional: Host name to connect to, defaults to the pod IP.",
                    "type": "string"
                   },
                   "port": {
                    "anyOf": [
                     {
                      "type": "integer"
                     },
                     {
                      "type": "string"
                     }
                    ],
                    "description": "Number or name of the port to access on the container.\nNumber must be in the range 1 to 65535.\nName must be an IANA_SVC_NAME.",
                    "x-kubernetes-int-or-string": true
                   }
                  },
                  "required": [
                   "port"
                  ],
                  "type": "object"
                 }
                },
                "type": "object"
               },
               "preStop": {
                "description": "PreStop is called immediately before a container is terminated due to an\nAPI request or management event such as liveness/startup probe failure,\npreemption, resource contention, etc. The handler is not called if the\ncontainer crashes or exits. The Pod's termination grace period countdown begins before the\nPreStop hook is executed. Regardless of the outcome of the handler, the\ncontainer will eventually terminate within the Pod's termination grace\nperiod (unless delayed by finalizers). Other management of the container blocks until the hook completes\nor until the termination grace period is reached.\nMore info: https://kubernetes.io/docs/concepts/containers/container-lifecycle-hooks/#container-hooks",
                "properties": {
                 "exec": {
