schema:
{
 "description": "ProxySetting is the Schema for the proxysettings API.",
 "properties": {
  "spec": {
   "description": "ProxySettingSpec defines the additional Capsule Proxy settings for additional users of the Tenant. Resource is Namespace-scoped and applies the settings to the belonged Tenant.",
   "properties": {
    "subjects": {
     "description": "Subjects that should receive additional permissions.",
     "items": {
      "properties": {
       "kind": {
        "description": "Kind of tenant owner. Possible values are \"User\", \"Group\", and \"ServiceAccount\"",
        "enum": [
         "User",
         "Group",
         "ServiceAccount"
        ],
        "type": "string"
       },
       "name": {
        "description": "Name of tenant owner.",
        "type": "string"
       },
       "proxySettings": {
        "description": "Proxy settings for tenant owner.",
        "items": {
         "properties": {
          "kind": {
           "enum": [
            "Nodes",
            "StorageClasses",
            "IngressClasses",
            "PriorityClasses",
            "RuntimeClasses",
            "PersistentVolumes"
           ],
           "type": "string"
          },
          "operations": {
           "items": {
            "enum": [
             "List",
             "Update",
             "Delete"
            ],
            "type": "string"
           },
           "type": "array"
          }
         },
         "required": [
          "kind",
          "operations"
         ],
         "type": "object"
        },
        "type": "array"
       }
      },
      "required": [
       "kind",
       "name"
      ],
      "type": "object"
     },
     "minItems": 1,
     "type": "array"
    }
   },
   "required": [
    "subjects"
   ],
   "type": "object"
  }
 },
 "title": "Proxy Setting",
 "type": "object"
}
