           "ports": {
            "description": "Ports is a list of L4 port/protocol",
            "items": {
             "description": "PortProtocol specifies an L4 port with an optional transport protocol",
             "properties": {
              "endPort": {
               "description": "EndPort can only be an L4 port number.",
               "format": "int32",
