component.schema:
{
 "description": "OpenStackCluster is the Schema for the openstackclusters API.\n\nDeprecated: v1alpha7.OpenStackCluster has been replaced by v1beta1.OpenStackCluster.",
 "properties": {
  "spec": {
   "description": "OpenStackClusterSpec defines the desired state of OpenStackCluster.",
   "properties": {
    "allowAllInClusterTraffic": {
     "description": "AllowAllInClusterTraffic is only used when managed security groups are in use.\nIf set to true, the rules for the managed security groups are configured so that all\ningress and egress between cluster nodes is permitted, allowing CNIs other than\nCalico to be used.",
     "type": "boolean"
    },
    "apiServerFixedIP": {
     "description": "APIServerFixedIP is the fixed IP which will be associated with the API server.\nIn the case where the API server has a floating IP but not a managed load balancer,\nthis field is not used.\nIf a managed load balancer is used and this field is not specified, a fixed IP will\nbe dynamically allocated for the load balancer.\nIf a managed load balancer is not used AND the API server floating IP is disabled,\nthis field MUST be specified and should correspond to a pre-allocated port that\nholds the fixed IP to be used as a VIP.",
     "type": "string"
    },
    "apiServerFloatingIP": {
     "description": "APIServerFloatingIP is the floatingIP which will be associated with the API server.\nThe floatingIP will be created if it does not already exist.\nIf not specified, a new floatingIP is allocated.\nThis field is not used if DisableAPIServerFloatingIP is set to true.",
     "type": "string"
    },
    "apiServerLoadBalancer": {
     "description": "APIServerLoadBalancer configures the optional LoadBalancer for the APIServer.\nIt must be activated by setting `enabled: true`.",
     "properties": {
      "additionalPorts": {
       "description": "AdditionalPorts adds additional tcp ports to the load balancer.",
       "items": {
        "type": "integer"
       },
       "type": "array"
      },
      "allowedCidrs": {
       "description": "AllowedCIDRs restrict access to all API-Server listeners to the given address CIDRs.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "enabled": {
       "description": "Enabled defines whether a load balancer should be created.",
       "type": "boolean"
      },
      "provider": {
       "description": "Octavia Provider Used to create load balancer",
       "type": "string"
      }
     },
     "type": "object"
    },
    "apiServerPort": {
     "description": "APIServerPort is the port on which the listener on the APIServer\nwill be created",
     "type": "integer"
    },
    "bastion": {
     "description": "Bastion is the OpenStack instance to login the nodes\n\nAs a rolling update is not ideal during a bastion host session, we\nprevent changes to a running bastion configuration. Set `enabled: false` to\nmake changes.",
     "properties": {
      "availabilityZone": {
       "type": "string"
      },
      "enabled": {
       "type": "boolean"
      },
      "instance": {
       "description": "Instance for the bastion itself",
       "properties": {
        "additionalBlockDevices": {
         "description": "AdditionalBlockDevices is a list of specifications for additional block devices to attach to the server instance",
         "items": {
          "description": "AdditionalBlockDevice is a block device to attach to the server.",
          "properties": {
           "name": {
            "description": "Name of the block device in the context of a machine.\nIf the block device is a volume, the Cinder volume will be named\nas a combination of the machine name and this name.\nAlso, this name will be used for tagging the block device.\nInformation about the block device tag can be obtained from the OpenStack\nmetadata API or the config drive.",
            "type": "string"
           },
           "sizeGiB": {
            "description": "SizeGiB is the size of the block device in gibibytes (GiB).",
            "type": "integer"
           },
           "storage": {
            "description": "Storage specifies the storage type of the block device and\nadditional storage options.",
            "properties": {
             "type": {
              "description": "Type is the type of block device to create.\nThis can be either \"Volume\" or \"Local\".",
              "type": "string"
             },
             "volume": {
              "description": "Volume contains additional storage options for a volume block device.",
              "properties": {
               "availabilityZone": {
                "description": "AvailabilityZone is the volume availability zone to create the volume in.\nIf omitted, the availability zone of the server will be used.\nThe availability zone must NOT contain spaces otherwise it will lead to volume that belongs\nto this availability zone register failure, see kubernetes/cloud-provider-openstack#1379 for\nfurther information.",
                "type": "string"
               },
               "type": {
                "description": "Type is the Cinder volume type of the volume.\nIf omitted, the default Cinder volume type that is configured in the OpenStack cloud\nwill be used.",
                "type": "string"
               }
              },
              "type": "object"
             }
            },
            "required": [
             "type"
            ],
            "type": "object"
           }
          },
          "required": [
           "name",
           "sizeGiB",
           "storage"
          ],
          "type": "object"
         },
         "type": "array",
         "x-kubernetes-list-map-keys": [
          "name"
         ],
         "x-kubernetes-list-type": "map"
        },
        "cloudName": {
         "description": "The name of the cloud to use from the clouds secret",
         "type": "string"
        },
        "configDrive": {
         "description": "Config Drive support",
         "type": "boolean"
        },
        "flavor": {
         "description": "The flavor reference for the flavor for your server instance.",
         "minLength": 1,
         "type": "string"
        },
        "flavorID": {
         "description": "FlavorID allows flavors to be specified by ID.  This field takes precedence\nover Flavor.",
         "minLength": 1,
         "type": "string"
        },
        "floatingIP": {
         "description": "The floatingIP which will be associated to the machine, only used for master.\nThe floatingIP should have been created and haven't been associated.",
         "type": "string"
        },
        "identityRef": {
         "description": "IdentityRef is a reference to a identity to be used when reconciling this cluster.\nIf not specified, the identity ref of the cluster will be used instead.",
         "properties": {
          "kind": {
           "description": "Kind of the identity. Must be supported by the infrastructure\nprovider and may be either cluster or namespace-scoped.",
           "minLength": 1,
           "type": "string"
          },
          "name": {
           "description": "Name of the infrastructure identity to be used.\nMust be either a cluster-scoped resource, or namespaced-scoped\nresource the same namespace as the resource(s) being provisioned.",
           "type": "string"
          }
         },
         "required": [
          "kind",
          "name"
         ],
         "type": "object"
        },
        "image": {
         "description": "The name of the image to use for your server instance.\nIf the RootVolume is specified, this will be ignored and use rootVolume directly.",
         "type": "string"
        },
        "imageUUID": {
         "description": "The uuid of the image to use for your server instance.\nif it's empty, Image name will be used",
         "type": "string"
        },
        "instanceID": {
         "description": "InstanceID is the OpenStack instance ID for this machine.",
         "type": "string"
        },
        "ports": {
         "description": "Ports to be attached to the server instance. They are created if a port with the given name does not already exist.\nIf not specified a default port will be added for the default cluster network.",
         "items": {
          "properties": {
           "adminStateUp": {
            "type": "boolean"
           },
           "allowedAddressPairs": {
            "items": {
             "properties": {
              "ipAddress": {
               "type": "string"
              },
              "macAddress": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           },
           "description": {
            "type": "string"
           },
           "disablePortSecurity": {
            "description": "DisablePortSecurity enables or disables the port security when set.\nWhen not set, it takes the value of the corresponding field at the network level.",
            "type": "boolean"
           },
           "fixedIPs": {
            "description": "Specify pairs of subnet and/or IP address. These should be subnets of the network with the given NetworkID.",
            "items": {
             "properties": {
              "ipAddress": {
               "type": "string"
              },
              "subnet": {
               "description": "Subnet is an openstack subnet query that will return the id of a subnet to create\nthe fixed IP of a port in. This query must not return more than one subnet.",
               "properties": {
                "cidr": {
                 "type": "string"
                },
                "description": {
                 "type": "string"
                },
                "gateway_ip": {
                 "type": "string"
                },
                "id": {
                 "type": "string"
                },
                "ipVersion": {
                 "type": "integer"
                },
                "ipv6AddressMode": {
                 "type": "string"
                },
                "ipv6RaMode": {
                 "type": "string"
                },
                "name": {
                 "type": "string"
                },
                "notTags": {
                 "type": "string"
                },
                "notTagsAny": {
                 "type": "string"
                },
                "projectId": {
                 "type": "string"
                },
                "tags": {
                 "type": "string"
                },
                "tagsAny": {
                 "type": "string"
                }
               },
               "type": "object"
              }
             },
             "required": [
              "subnet"
             ],
             "type": "object"
            },
            "type": "array"
           },
           "hostId": {
            "description": "The ID of the host where the port is allocated",
            "type": "string"
           },
           "macAddress": {
            "type": "string"
           },
           "nameSuffix": {
            "description": "Used to make the name of the port unique. If unspecified, instead the 0-based index of the port in the list is used.",
            "type": "string"
           },
           "network": {
            "description": "Network is a query for an openstack network that the port will be created or discovered on.\nThis will fail if the query returns more than one network.",
            "properties": {
             "description": {
              "type": "string"
             },
             "id": {
              "type": "string"
             },
             "name": {
              "type": "string"
             },
             "notTags": {
              "type": "string"
             },
             "notTagsAny": {
              "type": "string"
             },
             "projectId": {
              "type": "string"
             },
             "tags": {
              "type": "string"
             },
             "tagsAny": {
              "type": "string"
             }
            },
            "type": "object"
           },
           "profile": {
            "description": "Profile is a set of key-value pairs that are used for binding details.\nWe intentionally don't expose this as a map[string]string because we only want to enable\nthe users to set the values of the keys that are known to work in OpenStack Networking API.\nSee https://docs.openstack.org/api-ref/network/v2/index.html?expanded=create-port-detail#create-port",
            "properties": {
             "ovsHWOffload": {
              "description": "OVSHWOffload enables or disables the OVS hardware offload feature.",
              "type": "boolean"
             },
             "trustedVF": {
              "description": "TrustedVF enables or disables the “trusted mode” for the VF.",
              "type": "boolean"
             }
            },
            "type": "object"
           },
           "propagateUplinkStatus": {
            "description": "PropageteUplinkStatus enables or disables the propagate uplink status on the port.",
            "type": "boolean"
           },
           "securityGroupFilters": {
            "description": "The names, uuids, filters or any combination these of the security groups to assign to the instance",
            "items": {
             "properties": {
              "description": {
               "type": "string"
              },
              "id": {
               "type": "string"
              },
              "name": {
               "type": "string"
              },
              "notTags": {
               "type": "string"
              },
              "notTagsAny": {
               "type": "string"
              },
              "projectId": {
               "type": "string"
              },
              "tags": {
               "type": "string"
              },
              "tagsAny": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "type": "array"
           },
           "tags": {
            "description": "Tags applied to the port (and corresponding trunk, if a trunk is configured.)\nThese tags are applied in addition to the instance's tags, which will also be applied to the port.",
            "items": {
             "type": "string"
            },
            "type": "array",
            "x-kubernetes-list-type": "set"
           },
           "trunk": {
            "description": "Enables and disables trunk at port level. If not provided, openStackMachine.Spec.Trunk is inherited.",
            "type": "boolean"
           },
           "valueSpecs": {
            "description": "Value specs are extra parameters to include in the API request with OpenStack.\nThis is an extension point for the API, so what they do and if they are supported,\ndepends on the specific OpenStack implementation.",
            "items": {
             "description": "ValueSpec represents a single value_spec key-value pair.",
             "properties": {
              "key": {
               "description": "Key is the key in the key-value pair.",
               "type": "string"
              },
              "name": {
               "description": "Name is the name of the key-value pair.\nThis is just for identifying the pair and will not be sent to the OpenStack API.",
               "type": "string"
              },
              "value": {
               "description": "Value is the value in the key-value pair.",
               "type": "string"
              }
             },
             "required": [
              "key",
              "name",
              "value"
             ],
             "type": "object"
            },
            "type": "array",
            "x-kubernetes-list-map-keys": [
             "name"
            ],
            "x-kubernetes-list-type": "map"
           },
           "vnicType": {
            "description": "The virtual network interface card (vNIC) type that is bound to the neutron port.",
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "providerID": {
         "description": "ProviderID is the unique identifier as specified by the cloud provider.",
         "type": "string"
        },
        "rootVolume": {
         "description": "The volume metadata to boot from",
         "properties": {
          "availabilityZone": {
           "type": "string"
          },
          "diskSize": {
           "type": "integer"
          },
          "volumeType": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "securityGroups": {
         "description": "The names of the security groups to assign to the instance",
         "items": {
          "properties": {
           "description": {
            "type": "string"
           },
           "id": {
            "type": "string"
           },
           "name": {
            "type": "string"
           },
           "notTags": {
            "type": "string"
           },
           "notTagsAny": {
            "type": "string"
           },
           "projectId": {
            "type": "string"
           },
           "tags": {
            "type": "string"
           },
           "tagsAny": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "serverGroupID": {
         "description": "The server group to assign the machine to",
         "type": "string"
        },
        "serverMetadata": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Metadata mapping. Allows you to create a map of key value pairs to add to the server instance.",
         "type": "object"
        },
        "sshKeyName": {
         "description": "The ssh key to inject in the instance",
         "type": "string"
        },
        "tags": {
         "description": "Machine tags\nRequires Nova api 2.52 minimum!",
         "items": {
          "type": "string"
         },
         "type": "array",
         "x-kubernetes-list-type": "set"
        },
        "trunk": {
         "description": "Whether the server instance is created on a trunk port or not.",
         "type": "boolean"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "cloudName": {
     "description": "The name of the cloud to use from the clouds secret",
     "type": "string"
    },
    "controlPlaneAvailabilityZones": {
     "description": "ControlPlaneAvailabilityZones is the az to deploy control plane to",
     "items": {
      "type": "string"
     },
     "type": "array",
     "x-kubernetes-list-type": "set"
    },
    "controlPlaneEndpoint": {
     "description": "ControlPlaneEndpoint represents the endpoint used to communicate with the control plane.",
     "properties": {
      "host": {
       "description": "The hostname on which the API server is serving.",
       "type": "string"
      },
      "port": {
       "description": "The port on which the API server is serving.",
       "format": "int32",
       "type": "integer"
      }
     },
     "required": [
      "host",
      "port"
     ],
     "type": "object"
    },
    "controlPlaneOmitAvailabilityZone": {
     "description": "Indicates whether to omit the az for control plane nodes, allowing the Nova scheduler\nto make a decision on which az to use based on other scheduling constraints",
     "type": "boolean"
    },
    "disableAPIServerFloatingIP": {
     "description": "DisableAPIServerFloatingIP determines whether or not to attempt to attach a floating\nIP to the API server. This allows for the creation of clusters when attaching a floating\nIP to the API server (and hence, in many cases, exposing the API server to the internet)\nis not possible or desirable, e.g. if using a shared VLAN for communication between\nmanagement and workload clusters or when the management cluster is inside the\nproject network.\nThis option requires that the API server use a VIP on the cluster network so that the\nunderlying machines can change without changing ControlPlaneEndpoint.Host.\nWhen using a managed load balancer, this VIP will be managed automatically.\nIf not using a managed load balancer, cluster configuration will fail without additional\nconfiguration to manage the VIP on the control plane machines, which falls outside of\nthe scope of this controller.",
     "type": "boolean"
    },
    "disablePortSecurity": {
     "description": "DisablePortSecurity disables the port security of the network created for the\nKubernetes cluster, which also disables SecurityGroups",
     "type": "boolean"
    },
    "dnsNameservers": {
     "description": "DNSNameservers is the list of nameservers for OpenStack Subnet being created.\nSet this value when you need create a new network/subnet while the access\nthrough DNS is required.",
     "items": {
      "type": "string"
     },
     "type": "array",
     "x-kubernetes-list-type": "set"
    },
    "externalNetworkId": {
     "description": "ExternalNetworkID is the ID of an external OpenStack Network. This is necessary\nto get public internet to the VMs.",
     "type": "string"
    },
    "externalRouterIPs": {
     "description": "ExternalRouterIPs is an array of externalIPs on the respective subnets.\nThis is necessary if the router needs a fixed ip in a specific subnet.",
     "items": {
      "properties": {
       "fixedIP": {
        "description": "The FixedIP in the corresponding subnet",
        "type": "string"
       },
       "subnet": {
        "description": "The subnet in which the FixedIP is used for the Gateway of this router",
        "properties": {
         "cidr": {
          "type": "string"
         },
         "description": {
          "type": "string"
         },
         "gateway_ip": {
          "type": "string"
         },
         "id": {
          "type": "string"
         },
         "ipVersion": {
          "type": "integer"
         },
         "ipv6AddressMode": {
          "type": "string"
         },
         "ipv6RaMode": {
          "type": "string"
         },
         "name": {
          "type": "string"
         },
         "notTags": {
          "type": "string"
         },
         "notTagsAny": {
          "type": "string"
         },
         "projectId": {
          "type": "string"
         },
         "tags": {
          "type": "string"
         },
         "tagsAny": {
          "type": "string"
         }
        },
        "type": "object"
       }
      },
      "required": [
       "subnet"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "identityRef": {
     "description": "IdentityRef is a reference to a identity to be used when reconciling this cluster",
     "properties": {
      "kind": {
       "description": "Kind of the identity. Must be supported by the infrastructure\nprovider and may be either cluster or namespace-scoped.",
       "minLength": 1,
       "type": "string"
      },
      "name": {
       "description": "Name of the infrastructure identity to be used.\nMust be either a cluster-scoped resource, or namespaced-scoped\nresource the same namespace as the resource(s) being provisioned.",
       "type": "string"
      }
     },
     "required": [
      "kind",
      "name"
     ],
     "type": "object"
    },
    "managedSecurityGroups": {
     "description": "ManagedSecurityGroups determines whether OpenStack security groups for the cluster\nwill be managed by the OpenStack provider or whether pre-existing security groups will\nbe specified as part of the configuration.\nBy default, the managed security groups have rules that allow the Kubelet, etcd, the\nKubernetes API server and the Calico CNI plugin to function correctly.",
     "type": "boolean"
    },
    "network": {
     "description": "If NodeCIDR cannot be set this can be used to detect an existing network.",
     "properties": {
      "description": {
       "type": "string"
      },
      "id": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "notTags": {
       "type": "string"
      },
      "notTagsAny": {
       "type": "string"
      },
      "projectId": {
       "type": "string"
      },
      "tags": {
       "type": "string"
      },
      "tagsAny": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "networkMtu": {
     "description": "NetworkMTU sets the maximum transmission unit (MTU) value to address fragmentation for the private network ID.\nThis value will be used only if the Cluster actuator creates the network.\nIf leaved empty, the network will have the default MTU defined in Openstack network service.\nTo use this field, the Openstack installation requires the net-mtu neutron API extension.",
     "type": "integer"
    },
    "nodeCidr": {
     "description": "NodeCIDR is the OpenStack Subnet to be created. Cluster actuator will create a\nnetwork, a subnet with NodeCIDR, and a router connected to this subnet.\nIf you leave this empty, no network will be created.",
     "type": "string"
    },
    "router": {
     "description": "If NodeCIDR is set this option can be used to detect an existing router.\nIf specified, no new router will be created.",
     "properties": {
      "description": {
       "type": "string"
      },
      "id": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "notTags": {
       "type": "string"
      },
      "notTagsAny": {
       "type": "string"
      },
      "projectId": {
       "type": "string"
      },
      "tags": {
       "type": "string"
      },
      "tagsAny": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "subnet": {
     "description": "If NodeCIDR cannot be set this can be used to detect an existing subnet.",
     "properties": {
      "cidr": {
       "type": "string"
      },
      "description": {
       "type": "string"
      },
      "gateway_ip": {
       "type": "string"
      },
      "id": {
       "type": "string"
      },
      "ipVersion": {
       "type": "integer"
      },
      "ipv6AddressMode": {
       "type": "string"
      },
      "ipv6RaMode": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "notTags": {
       "type": "string"
      },
      "notTagsAny": {
       "type": "string"
      },
      "projectId": {
       "type": "string"
      },
      "tags": {
       "type": "string"
      },
      "tagsAny": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "tags": {
     "description": "Tags for all resources in cluster",
     "items": {
      "type": "string"
     },
     "type": "array",
     "x-kubernetes-list-type": "set"
    }
   },
   "type": "object"
  }
 },
 "title": "Open Stack Cluster",
 "type": "object"
}
