schema:
{
 "description": "Cluster is the Schema for the clusters API",
 "properties": {
  "spec": {
   "description": "ClusterSpec defines the desired state of Cluster",
   "properties": {
    "authInfo": {
     "description": "AuthInfo holds information that describes how a client can get credentials to access the cluster.",
     "properties": {
      "secretRef": {
       "description": "Equivalent of types.NamespacedName with JSON tags",
       "properties": {
        "name": {
         "type": "string"
        },
        "namespace": {
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "clusterID": {
     "description": "UID of the kube-system namespace",
     "type": "string"
    },
    "kubernetesApiEndpoints": {
     "description": "KubernetesAPIEndpoints represents the endpoints of the API server for this cluster.",
     "items": {
      "properties": {
       "caBundle": {
        "description": "CABundle contains the certificate authority information.",
        "format": "byte",
        "type": "string"
       },
       "clientNetwork": {
        "description": "The network name of the client to match whether if it should use the corresponding server address.",
        "type": "string"
       },
       "serverAddress": {
        "description": "Address of this server, suitable for a client that matches the clientNetwork if specified. This can be a hostname, hostname:port, IP or IP:port.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "clusterID"
   ],
   "type": "object"
  }
 },
 "title": "Cluster",
 "type": "object"
}
