schema:
{
 "description": "ContourDeployment is the schema for a Contour Deployment.",
 "properties": {
  "spec": {
   "description": "ContourDeploymentSpec specifies options for how a Contour instance should be provisioned.",
   "properties": {
    "contour": {
     "description": "Contour specifies deployment-time settings for the Contour part of the installation, i.e. the xDS server/control plane and associated resources, including things like replica count for the Deployment, and node placement constraints for the pods.",
     "properties": {
      "kubernetesLogLevel": {
       "description": "KubernetesLogLevel Enable Kubernetes client debug logging with log level. If unset, defaults to 0.",
       "maximum": 9,
       "minimum": 0,
       "type": "integer"
      },
      "logLevel": {
       "description": "LogLevel sets the log level for Contour Allowed values are \"info\", \"debug\".",
       "type": "string"
      },
      "nodePlacement": {
       "description": "NodePlacement describes node scheduling configuration of Contour pods.",
       "properties": {
        "nodeSelector": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "NodeSelector is the simplest recommended form of node selection constraint and specifies a map of key-value pairs. For the pod to be eligible to run on a node, the node must have each of the indicated key-value pairs as labels (it can have additional labels as well). \n If unset, the pod(s) will be scheduled to any available node.",
         "type": "object"
        },
        "tolerations": {
         "description": "Tolerations work with taints to ensure that pods are not scheduled onto inappropriate nodes. One or more taints are applied to a node; this marks that the node should not accept any pods that do not tolerate the taints. \n The default is an empty list. \n See https://kubernetes.io/docs/concepts/configuration/taint-and-toleration/ for additional details.",
         "items": {
          "description": "The pod this Toleration is attached to tolerates any taint that matches the triple \u003ckey,value,effect\u003e using the matching operator \u003coperator\u003e.",
          "properties": {
           "effect": {
            "description": "Effect indicates the taint effect to match. Empty means match all taint effects. When specified, allowed values are NoSchedule, PreferNoSchedule and NoExecute.",
            "type": "string"
           },
           "key": {
            "description": "Key is the taint key that the toleration applies to. Empty means match all taint keys. If the key is empty, operator must be Exists; this combination means to match all values and all keys.",
            "type": "string"
           },
           "operator": {
            "description": "Operator represents a key's relationship to the value. Valid operators are Exists and Equal. Defaults to Equal. Exists is equivalent to wildcard for value, so that a pod can tolerate all taints of a particular category.",
            "type": "string"
           },
           "tolerationSeconds": {
            "description": "TolerationSeconds represents the period of time the toleration (which must be of effect NoExecute, otherwise this field is ignored) tolerates the taint. By default, it is not set, which means tolerate the taint forever (do not evict). Zero and negative values will be treated as 0 (evict immediately) by the system.",
            "format": "int64",
            "type": "integer"
           },
           "value": {
            "description": "Value is the taint value the toleration matches to. If the operator is Exists, the value should be empty, otherwise just a regular string.",
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        }
       },
       "type": "object"
      },
      "replicas": {
       "description": "Replicas is the desired number of Contour replicas. If unset, defaults to 2.",
       "format": "int32",
       "minimum": 0,
       "type": "integer"
      },
      "resources": {
       "description": "Compute Resources required by contour container. Cannot be updated. More info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
       "properties": {
        "limits": {
         "additionalProperties": {
          "anyOf": [
           {
            "type": "integer"
           },
           {
            "type": "string"
           }
          ],
          "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
          "x-kubernetes-int-or-string": true
         },
         "description": "Limits describes the maximum amount of compute resources allowed. More info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
         "type": "object"
        },
        "requests": {
         "additionalProperties": {
          "anyOf": [
           {
            "type": "integer"
           },
           {
            "type": "string"
           }
          ],
          "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
          "x-kubernetes-int-or-string": true
         },
         "description": "Requests describes the minimum amount of compute resources required. If Requests is omitted for a container, it defaults to Limits if that is explicitly specified, otherwise to an implementation-defined value. More info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "envoy": {
     "description": "Envoy specifies deployment-time settings for the Envoy part of the installation, i.e. the xDS client/data plane and associated resources, including things like the workload type to use (DaemonSet or Deployment), node placement constraints for the pods, and various options for the Envoy service.",
     "properties": {
      "extraVolumeMounts": {
       "description": "ExtraVolumeMounts holds the extra volume mounts to add (normally used with extraVolumes).",
       "items": {
        "description": "VolumeMount describes a mounting of a Volume within a container.",
        "properties": {
         "mountPath": {
          "description": "Path within the container at which the volume should be mounted.  Must not contain ':'.",
          "type": "string"
         },
         "mountPropagation": {
          "description": "mountPropagation determines how mounts are propagated from the host to container and the other way around. When not set, MountPropagationNone is used. This field is beta in 1.10.",
          "type": "string"
         },
         "name": {
          "description": "This must match the Name of a Volume.",
          "type": "string"
         },
         "readOnly": {
          "description": "Mounted read-only if true, read-write otherwise (false or unspecified). Defaults to false.",
          "type": "boolean"
         },
         "subPath": {
          "description": "Path within the volume from which the container's volume should be mounted. Defaults to \"\" (volume's root).",
          "type": "string"
         },
         "subPathExpr": {
          "description": "Expanded path within the volume from which the container's volume should be mounted. Behaves similarly to SubPath but environment variable references $(VAR_NAME) are expanded using the container's environment. Defaults to \"\" (volume's root). SubPathExpr and SubPath are mutually exclusive.",
          "type": "string"
         }
        },
        "required": [
         "mountPath",
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "extraVolumes": {
       "description": "ExtraVolumes holds the extra volumes to add.",
       "items": {
        "description": "Volume represents a named volume in a pod that may be accessed by any container in the pod.",
        "properties": {
         "awsElasticBlockStore": {
          "description": "awsElasticBlockStore represents an AWS Disk resource that is attached to a kubelet's host machine and then exposed to the pod. More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore",
          "properties": {
           "fsType": {
            "description": "fsType is the filesystem type of the volume that you want to mount. Tip: Ensure that the filesystem type is supported by the host operating system. Examples: \"ext4\", \"xfs\", \"ntfs\". Implicitly inferred to be \"ext4\" if unspecified. More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore TODO: how do we prevent errors in the filesystem from compromising the machine",
            "type": "string"
           },
           "partition": {
            "description": "partition is the partition in the volume that you want to mount. If omitted, the default is to mount by volume name. Examples: For volume /dev/sda1, you specify the partition as \"1\". Similarly, the volume partition for /dev/sda is \"0\" (or you can leave the property empty).",
            "format": "int32",
            "type": "integer"
           },
           "readOnly": {
            "description": "readOnly value true will force the readOnly setting in VolumeMounts. More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore",
            "type": "boolean"
           },
           "volumeID": {
            "description": "volumeID is unique ID of the persistent disk resource in AWS (Amazon EBS volume). More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore",
            "type": "string"
           }
          },
          "required": [
           "volumeID"
          ],
          "type": "object"
         },
         "azureDisk": {
          "description": "azureDisk represents an Azure Data Disk mount on the host and bind mount to the pod.",
          "properties": {
           "cachingMode": {
            "description": "cachingMode is the Host Caching mode: None, Read Only, Read Write.",
            "type": "string"
           },
           "diskName": {
            "description": "diskName is the Name of the data disk in the blob storage",
            "type": "string"
           },
           "diskURI": {
            "description": "diskURI is the URI of data disk in the blob storage",
            "type": "string"
           },
           "fsType": {
            "description": "fsType is Filesystem type to mount. Must be a filesystem type supported by the host operating system. Ex. \"ext4\", \"xfs\", \"ntfs\". Implicitly inferred to be \"ext4\" if unspecified.",
            "type": "string"
           },
           "kind": {
            "description": "kind expected values are Shared: multiple blob disks per storage account  Dedicated: single blob disk per storage account  Managed: azure managed data disk (only in managed availability set). defaults to shared",
            "type": "string"
           },
           "readOnly": {
            "description": "readOnly Defaults to false (read/write). ReadOnly here will force the ReadOnly setting in VolumeMounts.",
            "type": "boolean"
           }
          },
          "required": [
           "diskName",
           "diskURI"
          ],
          "type": "object"
         },
         "azureFile": {
          "description": "azureFile represents an Azure File Service mount on the host and bind mount to the pod.",
          "properties": {
           "readOnly": {
            "description": "readOnly defaults to false (read/write). ReadOnly here will force the ReadOnly setting in VolumeMounts.",
            "type": "boolean"
           },
           "secretName": {
            "description": "secretName is the  name of secret that contains Azure Storage Account Name and Key",
            "type": "string"
           },
           "shareName": {
            "description": "shareName is the azure share Name",
            "type": "string"
           }
          },
          "required": [
           "secretName",
           "shareName"
          ],
          "type": "object"
         },
         "cephfs": {
          "description": "cephFS represents a Ceph FS mount on the host that shares a pod's lifetime",
          "properties": {
           "monitors": {
            "description": "monitors is Required: Monitors is a collection of Ceph monitors More info: https://examples.k8s.io/volumes/cephfs/README.md#how-to-use-it",
            "items": {
             "type": "string"
            },
            "type": "array"
           },
           "path": {
            "description": "path is Optional: Used as the mounted root, rather than the full Ceph tree, default is /",
            "type": "string"
           },
           "readOnly": {
            "description": "readOnly is Optional: Defaults to false (read/write). ReadOnly here will force the ReadOnly setting in VolumeMounts. More info: https://examples.k8s.io/volumes/cephfs/README.md#how-to-use-it",
            "type": "boolean"
           },
           "secretFile": {
            "description": "secretFile is Optional: SecretFile is the path to key ring for User, default is /etc/ceph/user.secret More info: https://examples.k8s.io/volumes/cephfs/README.md#how-to-use-it",
            "type": "string"
           },
           "secretRef": {
            "description": "secretRef is Optional: SecretRef is reference to the authentication secret for User, default is empty. More info: https://examples.k8s.io/volumes/cephfs/README.md#how-to-use-it",
            "properties": {
             "name": {
              "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
              "type": "string"
             }
            },
            "type": "object"
           },
           "user": {
            "description": "user is optional: User is the rados user name, default is admin More info: https://examples.k8s.io/volumes/cephfs/README.md#how-to-use-it",
            "type": "string"
           }
          },
          "required": [
           "monitors"
          ],
          "type": "object"
         },
         "cinder": {
          "description": "cinder represents a cinder volume attached and mounted on kubelets host machine. More info: https://examples.k8s.io/mysql-cinder-pd/README.md",
          "properties": {
           "fsType": {
            "description": "fsType is the filesystem type to mount. Must be a filesystem type supported by the host operating system. Examples: \"ext4\", \"xfs\", \"ntfs\". Implicitly inferred to be \"ext4\" if unspecified. More info: https://examples.k8s.io/mysql-cinder-pd/README.md",
            "type": "string"
           },
           "readOnly": {
            "description": "readOnly defaults to false (read/write). ReadOnly here will force the ReadOnly setting in VolumeMounts. More info: https://examples.k8s.io/mysql-cinder-pd/README.md",
            "type": "boolean"
           },
           "secretRef": {
            "description": "secretRef is optional: points to a secret object containing parameters used to connect to OpenStack.",
            "properties": {
             "name": {
              "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
              "type": "string"
             }
            },
            "type": "object"
           },
           "volumeID": {
            "description": "volumeID used to identify the volume in cinder. More info: https://examples.k8s.io/mysql-cinder-pd/README.md",
            "type": "string"
           }
          },
          "required": [
           "volumeID"
          ],
          "type": "object"
         },
         "configMap": {
          "description": "configMap represents a configMap that should populate this volume",
          "properties": {
           "defaultMode": {
            "description": "defaultMode is optional: mode bits used to set permissions on created files by default. Must be an octal value between 0000 and 0777 or a decimal value between 0 and 511. YAML accepts both octal and decimal values, JSON requires decimal values for mode bits. Defaults to 0644. Directories within the path are not affected by this setting. This might be in conflict with other options that affect the file mode, like fsGroup, and the result can be other mode bits set.",
            "format": "int32",
            "type": "integer"
           },
           "items": {
            "description": "items if unspecified, each key-value pair in the Data field of the referenced ConfigMap will be projected into the volume as a file whose name is the key and content is the value. If specified, the listed keys will be projected into the specified paths, and unlisted keys will not be present. If a key is specified which is not present in the ConfigMap, the volume setup will error unless it is marked optional. Paths must be relative and may not contain the '..' path or start with '..'.",
            "items": {
             "description": "Maps a string key to a path within a volume.",
             "properties": {
              "key": {
               "description": "key is the key to project.",
               "type": "string"
              },
              "mode": {
               "description": "mode is Optional: mode bits used to set permissions on this file. Must be an octal value between 0000 and 0777 or a decimal value between 0 and 511. YAML accepts both octal and decimal values, JSON requires decimal values for mode bits. If not specified, the volume defaultMode will be used. This might be in conflict with other options that affect the file mode, like fsGroup, and the result can be other mode bits set.",
               "format": "int32",
               "type": "integer"
              },
              "path": {
               "description": "path is the relative path of the file to map the key to. May not be an absolute path. May not contain the path element '..'. May not start with the string '..'.",
               "type": "string"
              }
             },
             "required": [
              "key",
              "path"
             ],
             "type": "object"
            },
            "type": "array"
           },
           "name": {
            "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
            "type": "string"
           },
           "optional": {
            "description": "optional specify whether the ConfigMap or its keys must be defined",
            "type": "boolean"
           }
          },
          "type": "object"
         },
         "csi": {
          "description": "csi (Container Storage Interface) represents ephemeral storage that is handled by certain external CSI drivers (Beta feature).",
          "properties": {
           "driver": {
            "description": "driver is the name of the CSI driver that handles this volume. Consult with your admin for the correct name as registered in the cluster.",
            "type": "string"
           },
           "fsType": {
            "description": "fsType to mount. Ex. \"ext4\", \"xfs\", \"ntfs\". If not provided, the empty value is passed to the associated CSI driver which will determine the default filesystem to apply.",
            "type": "string"
           },
           "nodePublishSecretRef": {
            "description": "nodePublishSecretRef is a reference to the secret object containing sensitive information to pass to the CSI driver to complete the CSI NodePublishVolume and NodeUnpublishVolume calls. This field is optional, and  may be empty if no secret is required. If the secret object contains more than one secret, all secret references are passed.",
            "properties": {
             "name": {
              "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
              "type": "string"
             }
            },
            "type": "object"
           },
           "readOnly": {
            "description": "readOnly specifies a read-only configuration for the volume. Defaults to false (read/write).",
            "type": "boolean"
           },
           "volumeAttributes": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "volumeAttributes stores driver-specific properties that are passed to the CSI driver. Consult your driver's documentation for supported values.",
            "type": "object"
           }
          },
          "required": [
           "driver"
          ],
          "type": "object"
         },
         "downwardAPI": {
          "description": "downwardAPI represents downward API about the pod that should populate this volume",
          "properties": {
           "defaultMode": {
            "description": "Optional: mode bits to use on created files by default. Must be a Optional: mode bits used to set permissions on created files by default. Must be an octal value between 0000 and 0777 or a decimal value between 0 and 511. YAML accepts both octal and decimal values, JSON requires decimal values for mode bits. Defaults to 0644. Directories within the path are not affected by this setting. This might be in conflict with other options that affect the file mode, like fsGroup, and the result can be other mode bits set.",
            "format": "int32",
            "type": "integer"
           },
           "items": {
            "description": "Items is a list of downward API volume file",
            "items": {
             "description": "DownwardAPIVolumeFile represents information to create the file containing the pod field",
             "properties": {
              "fieldRef": {
               "description": "Required: Selects a field of the pod: only annotations, labels, name and namespace are supported.",
               "properties": {
                "apiVersion": {
                 "description": "Version of the schema the FieldPath is written in terms of, defaults to \"v1\".",
                 "type": "string"
                },
                "fieldPath": {
                 "description": "Path of the field to select in the specified API version.",
                 "type": "string"
                }
               },
               "required": [
                "fieldPath"
               ],
               "type": "object"
              },
              "mode": {
               "description": "Optional: mode bits used to set permissions on this file, must be an octal value between 0000 and 0777 or a decimal value between 0 and 511. YAML accepts both octal and decimal values, JSON requires decimal values for mode bits. If not specified, the volume defaultMode will be used. This might be in conflict with other options that affect the file mode, like fsGroup, and the result can be other mode bits set.",
               "format": "int32",
               "type": "integer"
              },
              "path": {
               "description": "Required: Path is  the relative path name of the file to be created. Must not be absolute or contain the '..' path. Must be utf-8 encoded. The first item of the relative path must not start with '..'",
               "type": "string"
              },
              "resourceFieldRef": {
               "description": "Selects a resource of the container: only resources limits and requests (limits.cpu, limits.memory, requests.cpu and requests.memory) are currently supported.",
               "properties": {
                "containerName": {
                 "description": "Container name: required for volumes, optional for env vars",
                 "type": "string"
                },
                "divisor": {
                 "anyOf": [
                  {
                   "type": "integer"
                  },
                  {
                   "type": "string"
                  }
                 ],
                 "description": "Specifies the output format of the exposed resources, defaults to \"1\"",
                 "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
                 "x-kubernetes-int-or-string": true
                },
                "resource": {
                 "description": "Required: resource to select",
                 "type": "string"
                }
               },
               "required": [
                "resource"
               ],
               "type": "object"
              }
             },
             "required": [
              "path"
             ],
             "type": "object"
            },
            "type": "array"
           }
          },
          "type": "object"
         },
         "emptyDir": {
          "description": "emptyDir represents a temporary directory that shares a pod's lifetime. More info: https://kubernetes.io/docs/concepts/storage/volumes#emptydir",
          "properties": {
           "medium": {
            "description": "medium represents what type of storage medium should back this directory. The default is \"\" which means to use the node's default medium. Must be an empty string (default) or Memory. More info: https://kubernetes.io/docs/concepts/storage/volumes#emptydir",
            "type": "string"
           },
           "sizeLimit": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "description": "sizeLimit is the total amount of local storage required for this EmptyDir volume. The size limit is also applicable for memory medium. The maximum usage on memory medium EmptyDir would be the minimum value between the SizeLimit specified here and the sum of memory limits of all containers in a pod. The default is nil which means that the limit is undefined. More info: http://kubernetes.io/docs/user-guide/volumes#emptydir",
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
            "x-kubernetes-int-or-string": true
           }
          },
          "type": "object"
         },
         "ephemeral": {
          "description": "ephemeral represents a volume that is handled by a cluster storage driver. The volume's lifecycle is tied to the pod that defines it - it will be created before the pod starts, and deleted when the pod is removed. \n Use this if: a) the volume is only needed while the pod runs, b) features of normal volumes like restoring from snapshot or capacity    tracking are needed, c) the storage driver is specified through a storage class, and d) the storage driver supports dynamic volume provisioning through    a PersistentVolumeClaim (see EphemeralVolumeSource for more    information on the connection between this volume type    and PersistentVolumeClaim). \n Use PersistentVolumeClaim or one of the vendor-specific APIs for volumes that persist for longer than the lifecycle of an individual pod. \n Use CSI for light-weight local ephemeral volumes if the CSI driver is meant to be used that way - see the documentation of the driver for more information. \n A pod can use both types of ephemeral volumes and persistent volumes at the same time.",
          "properties": {
           "volumeClaimTemplate": {
            "description": "Will be used to create a stand-alone PVC to provision the volume. The pod in which this EphemeralVolumeSource is embedded will be the owner of the PVC, i.e. the PVC will be deleted together with the pod.  The name of the PVC will be `\u003cpod name\u003e-\u003cvolume name\u003e` where `\u003cvolume name\u003e` is the name from the `PodSpec.Volumes` array entry. Pod validation will reject the pod if the concatenated name is not valid for a PVC (for example, too long). \n An existing PVC with that name that is not owned by the pod will *not* be used for the pod to avoid using an unrelated volume by mistake. Starting the pod is then blocked until the unrelated PVC is removed. If such a pre-created PVC is meant to be used by the pod, the PVC has to updated with an owner reference to the pod once the pod exists. Normally this should not be necessary, but it may be useful when manually reconstructing a broken cluster. \n This field is read-only and no changes will be made by Kubernetes to the PVC after it has been created. \n Required, must not be nil.",
            "properties": {
             "metadata": {
              "description": "May contain labels and annotations that will be copied into the PVC when creating it. No other fields are allowed and will be rejected during validation.",
              "type": "object"
             },
             "spec": {
              "description": "The specification for the PersistentVolumeClaim. The entire content is copied unchanged into the PVC that gets created from this template. The same fields as in a PersistentVolumeClaim are also valid here.",
              "properties": {
               "accessModes": {
                "description": "accessModes contains the desired access modes the volume should have. More info: https://kubernetes.io/docs/concepts/storage/persistent-volumes#access-modes-1",
                "items": {
                 "type": "string"
                },
                "type": "array"
               },
               "dataSource": {
                "description": "dataSource field can be used to specify either: * An existing VolumeSnapshot object (snapshot.storage.k8s.io/VolumeSnapshot) * An existing PVC (PersistentVolumeClaim) If the provisioner or an external controller can support the specified data source, it will create a new volume based on the contents of the specified data source. If the AnyVolumeDataSource feature gate is enabled, this field will always have the same contents as the DataSourceRef field.",
                "properties": {
                 "apiGroup": {
                  "description": "APIGroup is the group for the resource being referenced. If APIGroup is not specified, the specified Kind must be in the core API group. For any other third-party types, APIGroup is required.",
                  "type": "string"
                 },
                 "kind": {
                  "description": "Kind is the type of resource being referenced",
                  "type": "string"
                 },
                 "name": {
                  "description": "Name is the name of resource being referenced",
