schema:
{
 "description": "EDIT THIS FILE!  THIS IS SCAFFOLDING FOR YOU TO OWN! NOTE: json tags are required.  Any new fields you add must have json tags for the fields to be serialized. AIPodAutoscalerSpec defines the desired state of AIPodAutoscaler",
 "properties": {
  "autoAdjustReplicasCount": {
   "description": "AutoAdjustReplicasCount enabled flag for auto adjust replicas count",
   "type": "boolean"
  },
  "autoAdjustStartupDuration": {
   "description": "AutoAdjustStartupDuration enabled flag for auto adjust startup duration",
   "type": "boolean"
  },
  "maxReplicas": {
   "default": 30,
   "description": "MaxReplicas maximal replicas count",
   "format": "int32",
   "type": "integer"
  },
  "metricsOffset": {
   "default": 0,
   "description": "MetricsOffset maximal replicas count",
   "format": "int64",
   "type": "integer"
  },
  "minReplicas": {
   "default": 1,
   "description": "MinReplicas minimal replicas count",
   "format": "int32",
   "type": "integer"
  },
  "scaleTargetRef": {
   "description": "ScaleTargetRef block of spec metadata for info about scale resource",
   "properties": {
    "apiVersion": {
     "default": "apps/v1",
     "description": "ApiVersion is API version of scale resource",
     "type": "string"
    },
    "kind": {
     "default": "Deployment",
     "description": "Specifies type of resource. Valid values are: - \"Deployment\" (default); - \"StatefulSet\"; - \"DaemonSet\"",
     "enum": [
      "Deployment",
      "StatefulSet",
      "DaemonSet"
     ],
     "type": "string"
    },
    "name": {
     "description": "Name set name of scale resource",
     "maxLength": 16,
     "minLength": 3,
     "type": "string"
    }
   },
   "required": [
    "apiVersion",
    "kind",
    "name"
   ],
   "type": "object"
  },
  "sources": {
   "description": "Sources sources metadata block",
   "items": {
    "description": "Source for scaling resource",
    "properties": {
     "metrics": {
      "items": {
       "description": "ScaleSourceMetrics describes types of metrics for scaling source.",
       "enum": [
        "cpu",
        "memory",
        "disk",
        "network",
        "logs",
        "nginx"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "period": {
      "items": {
       "description": "ScaleSourcePeriod describes types of period for scaling source.",
       "enum": [
        "daily",
        "weekly",
        "monthly"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "type": {
      "description": "Specifies type of source.",
      "enum": [
       "History"
      ],
      "type": "string"
     }
    },
    "required": [
     "metrics",
     "period",
     "type"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "startupDuration": {
   "description": "StartupDuration startup duration for Doer service",
   "type": "string"
  },
  "stepDuration": {
   "default": "10s",
   "description": "StepDuration step duration for monitoring queries",
   "pattern": "^([0-9]+(m|s|ms|mk|h|d|w))+$",
   "type": "string"
  },
  "updateInterval": {
   "default": "60s",
   "description": "UpdateInterval duration timeout value for search resource loop iteration",
   "pattern": "^([0-9]+(m|s|ms|mk|h|d|w))+$",
   "type": "string"
  }
 },
 "required": [
  "autoAdjustReplicasCount",
  "autoAdjustStartupDuration",
  "maxReplicas",
  "minReplicas",
  "scaleTargetRef",
  "sources",
  "startupDuration",
  "stepDuration",
  "updateInterval"
 ],
 "title": "AI Pod Autoscaler",
 "type": "object"
}
