schema:
{
 "description": "ApisixTlsSpec is the specification of ApisixSSL.",
 "properties": {
  "client": {
   "description": "ApisixMutualTlsClientConfig describes the mutual TLS CA and verify depth",
   "properties": {
    "caSecret": {
     "description": "ApisixSecret describes the Kubernetes Secret name and namespace.",
     "properties": {
      "name": {
       "minLength": 1,
       "type": "string"
      },
      "namespace": {
       "minLength": 1,
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "depth": {
     "type": "integer"
    }
   },
   "type": "object"
  },
  "hosts": {
   "items": {
    "pattern": "^\\*?[0-9a-zA-Z-.]+$",
    "type": "string"
   },
   "minItems": 1,
   "type": "array"
  },
  "secret": {
   "description": "ApisixSecret describes the Kubernetes Secret name and namespace.",
   "properties": {
    "name": {
     "minLength": 1,
     "type": "string"
    },
    "namespace": {
     "minLength": 1,
     "type": "string"
    }
   },
   "required": [
    "name",
    "namespace"
   ],
   "type": "object"
  }
 },
 "required": [
  "hosts",
  "secret"
 ],
 "title": "Apisix Tls",
 "type": "object"
}
