schema:
{
 "description": "TerminatorSpec defines the desired state of Terminator",
 "properties": {
  "drain": {
   "description": "Configuration for the cordon and drain actions.",
   "properties": {
    "deleteEmptyDirData": {
     "default": true,
     "description": "Enable termination of pods with local data that will be deleted.",
     "type": "boolean"
    },
    "force": {
     "default": true,
     "description": "Enable termination of pods without a controller.",
     "type": "boolean"
    },
    "gracePeriodSeconds": {
     "default": -1,
     "description": "Wait time for pods to terminate. If negative then the pod's configured gracetime will be used.",
     "type": "integer"
    },
    "ignoreAllDaemonSets": {
     "default": true,
     "description": "Enable ignoring pods managed by a DaemonSet.",
     "type": "boolean"
    },
    "timeoutSeconds": {
     "default": 120,
     "description": "Wait time before failing the action. If zero, then wait forever.",
     "type": "integer"
    }
   },
   "type": "object"
  },
  "events": {
   "description": "Specify what action should be taken when a particular message type is received.",
   "properties": {
    "autoScalingTermination": {
     "default": "CordonAndDrain",
     "enum": [
      "CordonAndDrain",
      "Cordon",
      "NoAction"
     ],
     "type": "string"
    },
    "rebalanceRecommendation": {
     "default": "CordonAndDrain",
     "enum": [
      "CordonAndDrain",
      "Cordon",
      "NoAction"
     ],
     "type": "string"
    },
    "scheduledChange": {
     "default": "CordonAndDrain",
     "enum": [
      "CordonAndDrain",
      "Cordon",
      "NoAction"
     ],
     "type": "string"
    },
    "spotInterruption": {
     "default": "CordonAndDrain",
     "enum": [
      "CordonAndDrain",
      "Cordon",
      "NoAction"
     ],
     "type": "string"
    },
    "stateChange": {
     "default": "CordonAndDrain",
     "enum": [
      "CordonAndDrain",
      "Cordon",
      "NoAction"
     ],
     "type": "string"
    }
   },
   "type": "object"
  },
  "matchLabels": {
   "additionalProperties": {
    "type": "string"
   },
   "description": "Action will only be taken if the target node has all the matching labels and values.",
   "type": "object"
  },
  "sqs": {
   "description": "AWS SQS queue configuration.",
   "properties": {
    "queueURL": {
     "description": "The URL of the Amazon SQS queue from which messages are received.\n\n  * Queue URLs and names are case-sensitive.\n\n  * QueueURL is a required field\n",
     "type": "string"
    }
   },
   "required": [
    "queueURL"
   ],
   "type": "object"
  },
  "webhook": {
   "description": "Send notification of handled events.",
   "properties": {
    "headers": {
     "default": [
      {
       "name": "Content-Type",
       "value": "application/json"
      }
     ],
     "description": "HTTP headers to include when sending notifications.",
     "items": {
      "properties": {
       "name": {
        "description": "Header name.",
        "type": "string"
       },
       "value": {
        "description": "Header value.",
        "type": "string"
       }
      },
      "required": [
       "name",
       "value"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "proxyURL": {
     "description": "Proxy URL to use to send notifications.",
     "type": "string"
    },
    "template": {
     "default": "{\"text\":\"[NTH][Instance Interruption] EventID: {{ .EventID }} - Kind: {{ .Kind }} - Instance: {{ .InstanceID }} - Node: {{ .NodeName }} - Start Time: {{ .StartTime }}\"}",
     "description": "Used to generate the request payload. Template used to generate webhook request body.\nThe template may reference fields EventID, Kind, InstanceID, NodeName, and StartTime.\nSee https://pkg.go.dev/text/template documentation for template format examples and explanation.\n",
     "type": "string"
    },
    "url": {
     "description": "URL to send notifications.",
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Terminator",
 "type": "object"
}
