schema:
{
 "properties": {
  "backup_pvc": {
   "description": "Name of the backup PVC",
   "type": "string"
  },
  "backup_pvc_namespace": {
   "description": "(Deprecated) Namespace the PVC is in",
   "type": "string"
  },
  "backup_resource_requirements": {
   "description": "Resource requirements for the management pod used to create a backup",
   "properties": {
    "limits": {
     "properties": {
      "cpu": {
       "type": "string"
      },
      "memory": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "requests": {
     "properties": {
      "cpu": {
       "type": "string"
      },
      "memory": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  },
  "backup_storage_class": {
   "description": "Storage class to use when creating PVC for backup",
   "type": "string"
  },
  "backup_storage_requirements": {
   "description": "Storage requirements for backup PVC (may be similar to existing postgres PVC backing up from)",
   "type": "string"
  },
  "clean_backup_on_delete": {
   "description": "Flag to indicate if backup should be deleted on PVC if AWXBackup object is deleted",
   "type": "boolean"
  },
  "deployment_name": {
   "description": "Name of the deployment to be backed up",
   "type": "string"
  },
  "no_log": {
   "default": true,
   "description": "Configure no_log for no_log tasks",
   "type": "boolean"
  },
  "pg_dump_suffix": {
   "description": "Additional parameters for the pg_dump command",
   "type": "string"
  },
  "postgres_image": {
   "description": "Registry path to the PostgreSQL container to use",
   "type": "string"
  },
  "postgres_image_version": {
   "description": "PostgreSQL container image version to use",
   "type": "string"
  },
  "postgres_label_selector": {
   "description": "Label selector used to identify postgres pod for backing up data",
   "type": "string"
  },
  "set_self_labels": {
   "default": true,
   "description": "Maintain some of the recommended `app.kubernetes.io/*` labels on the resource (self)",
   "type": "boolean"
  }
 },
 "required": [
  "deployment_name"
 ],
 "title": "AWX Backup",
 "type": "object",
 "x-kubernetes-preserve-unknown-fields": true
}
