schema:
{
 "description": "Configuration affecting network reachability of a sidecar. See more details at: https://istio.io/docs/reference/config/networking/sidecar.html",
 "properties": {
  "egress": {
   "items": {
    "properties": {
     "bind": {
      "type": "string"
     },
     "captureMode": {
      "enum": [
       "DEFAULT",
       "IPTABLES",
       "NONE"
      ],
      "type": "string"
     },
     "hosts": {
      "items": {
       "type": "string"
      },
      "type": "array"
     },
     "port": {
      "description": "The port associated with the listener.",
      "properties": {
       "name": {
        "description": "Label assigned to the port.",
        "type": "string"
       },
       "number": {
        "description": "A valid non-negative integer port number.",
        "type": "integer"
       },
       "protocol": {
        "description": "The protocol exposed on the port.",
        "type": "string"
       },
       "targetPort": {
        "type": "integer"
       }
      },
      "type": "object"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "ingress": {
   "items": {
    "properties": {
     "bind": {
      "description": "The IP to which the listener should be bound.",
      "type": "string"
     },
     "captureMode": {
      "enum": [
       "DEFAULT",
       "IPTABLES",
       "NONE"
      ],
      "type": "string"
     },
     "defaultEndpoint": {
      "type": "string"
     },
     "port": {
      "description": "The port associated with the listener.",
      "properties": {
       "name": {
        "description": "Label assigned to the port.",
        "type": "string"
       },
       "number": {
        "description": "A valid non-negative integer port number.",
        "type": "integer"
       },
       "protocol": {
        "description": "The protocol exposed on the port.",
        "type": "string"
       },
       "targetPort": {
        "type": "integer"
       }
      },
      "type": "object"
     },
     "tls": {
      "properties": {
       "caCertificates": {
        "description": "REQUIRED if mode is `MUTUAL`.",
        "type": "string"
       },
       "cipherSuites": {
        "description": "Optional: If specified, only support the specified cipher list.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "credentialName": {
        "type": "string"
       },
       "httpsRedirect": {
        "type": "boolean"
       },
       "maxProtocolVersion": {
        "description": "Optional: Maximum TLS protocol version.",
        "enum": [
         "TLS_AUTO",
         "TLSV1_0",
         "TLSV1_1",
         "TLSV1_2",
         "TLSV1_3"
        ],
        "type": "string"
       },
       "minProtocolVersion": {
        "description": "Optional: Minimum TLS protocol version.",
        "enum": [
         "TLS_AUTO",
         "TLSV1_0",
         "TLSV1_1",
         "TLSV1_2",
         "TLSV1_3"
        ],
        "type": "string"
       },
       "mode": {
        "enum": [
         "PASSTHROUGH",
         "SIMPLE",
         "MUTUAL",
         "AUTO_PASSTHROUGH",
         "ISTIO_MUTUAL"
        ],
        "type": "string"
       },
       "privateKey": {
        "description": "REQUIRED if mode is `SIMPLE` or `MUTUAL`.",
        "type": "string"
       },
       "serverCertificate": {
        "description": "REQUIRED if mode is `SIMPLE` or `MUTUAL`.",
        "type": "string"
       },
       "subjectAltNames": {
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "verifyCertificateHash": {
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "verifyCertificateSpki": {
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "type": "object"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "outboundTrafficPolicy": {
   "description": "Configuration for the outbound traffic policy.",
   "properties": {
    "egressProxy": {
     "properties": {
      "host": {
       "description": "The name of a service from the service registry.",
       "type": "string"
      },
      "port": {
       "description": "Specifies the port on the host that is being addressed.",
       "properties": {
        "number": {
         "type": "integer"
        }
       },
       "type": "object"
      },
      "subset": {
       "description": "The name of a subset within the service.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "mode": {
     "enum": [
      "REGISTRY_ONLY",
      "ALLOW_ANY"
     ],
     "type": "string"
    }
   },
   "type": "object"
  },
  "workloadSelector": {
   "properties": {
    "labels": {
     "additionalProperties": {
      "type": "string"
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Sidecar",
 "type": "object"
}
