schema:
{
 "properties": {
  "category": {
   "description": "Category defines the category of the application that this editor is for (eg, database)",
   "type": "string"
  },
  "chartRef": {
   "description": "ChartRef references a Helm chart with the ui.json",
   "properties": {
    "name": {
     "type": "string"
    },
    "url": {
     "type": "string"
    },
    "version": {
     "type": "string"
    }
   },
   "required": [
    "name",
    "url",
    "version"
   ],
   "type": "object"
  },
  "provider": {
   "description": "Provider defines the name of the provider for this application (eg, kubedb.com)",
   "type": "string"
  },
  "resource": {
   "description": "Resource identifies the resource to which this editor applies",
   "properties": {
    "group": {
     "type": "string"
    },
    "kind": {
     "description": "Kind is the serialized kind of the resource.  It is normally CamelCase and singular.",
     "type": "string"
    },
    "name": {
     "description": "Name is the plural name of the resource to serve.  It must match the name of the CustomResourceDefinition-registration too: plural.group and it must be all lowercase.",
     "type": "string"
    },
    "scope": {
     "description": "ResourceScope is an enum defining the different scopes available to a custom resource",
     "type": "string"
    },
    "version": {
     "type": "string"
    }
   },
   "required": [
    "group",
    "kind",
    "name",
    "scope",
    "version"
   ],
   "type": "object"
  }
 },
 "required": [
  "category",
  "chartRef",
  "provider",
  "resource"
 ],
 "title": "Options Editor",
 "type": "object"
}
