schema:
{
 "description": "DatadogAgent Deployment with Datadog Operator",
 "properties": {
  "spec": {
   "description": "DatadogAgentSpec defines the desired state of DatadogAgent",
   "properties": {
    "agent": {
     "description": "The desired state of the Agent as an extended daemonset Contains the Node Agent configuration and deployment strategy",
     "properties": {
      "additionalAnnotations": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "AdditionalAnnotations provide annotations that will be added to the Agent Pods.",
       "type": "object"
      },
      "additionalLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "AdditionalLabels provide labels that will be added to the cluster checks runner Pods.",
       "type": "object"
      },
      "apm": {
       "description": "Trace Agent configuration",
       "properties": {
        "enabled": {
         "description": "Enable this to enable APM and tracing, on port 8126 ref: https://github.com/DataDog/docker-dd-agent#tracing-from-the-host",
         "type": "boolean"
        },
        "env": {
         "description": "The Datadog Agent supports many environment variables Ref: https://docs.datadoghq.com/agent/docker/?tab=standard#environment-variables",
         "items": {
          "description": "EnvVar represents an environment variable present in a Container.",
          "properties": {
           "name": {
            "description": "Name of the environment variable. Must be a C_IDENTIFIER.",
            "type": "string"
           },
           "value": {
            "description": "Variable references $(VAR_NAME) are expanded using the previous defined environment variables in the container and any service environment variables. If a variable cannot be resolved, the reference in the input string will be unchanged. The $(VAR_NAME) syntax can be escaped with a double $$, ie: $$(VAR_NAME). Escaped references will never be expanded, regardless of whether the variable exists or not. Defaults to \"\".",
            "type": "string"
           },
           "valueFrom": {
            "description": "Source for the environment variable's value. Cannot be used if value is not empty.",
            "properties": {
             "configMapKeyRef": {
              "description": "Selects a key of a ConfigMap.",
              "properties": {
               "key": {
                "description": "The key to select.",
                "type": "string"
               },
               "name": {
                "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
                "type": "string"
               },
               "optional": {
                "description": "Specify whether the ConfigMap or its key must be defined",
                "type": "boolean"
               }
              },
              "required": [
               "key"
              ],
              "type": "object"
             },
             "fieldRef": {
              "description": "Selects a field of the pod: supports metadata.name, metadata.namespace, metadata.labels, metadata.annotations, spec.nodeName, spec.serviceAccountName, status.hostIP, status.podIP, status.podIPs.",
              "properties": {
               "apiVersion": {
                "description": "Version of the schema the FieldPath is written in terms of, defaults to \"v1\".",
                "type": "string"
               },
               "fieldPath": {
                "description": "Path of the field to select in the specified API version.",
                "type": "string"
               }
              },
              "required": [
               "fieldPath"
              ],
              "type": "object"
             },
             "resourceFieldRef": {
              "description": "Selects a resource of the container: only resources limits and requests (limits.cpu, limits.memory, limits.ephemeral-storage, requests.cpu, requests.memory and requests.ephemeral-storage) are currently supported.",
              "properties": {
               "containerName": {
                "description": "Container name: required for volumes, optional for env vars",
                "type": "string"
               },
               "divisor": {
                "anyOf": [
                 {
                  "type": "integer"
                 },
                 {
                  "type": "string"
                 }
                ],
                "description": "Specifies the output format of the exposed resources, defaults to \"1\"",
                "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
               },
               "resource": {
                "description": "Required: resource to select",
                "type": "string"
               }
              },
              "required": [
               "resource"
              ],
              "type": "object"
             },
             "secretKeyRef": {
              "description": "Selects a key of a secret in the pod's namespace",
              "properties": {
               "key": {
                "description": "The key of the secret to select from.  Must be a valid secret key.",
                "type": "string"
               },
               "name": {
                "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
                "type": "string"
               },
               "optional": {
                "description": "Specify whether the Secret or its key must be defined",
                "type": "boolean"
               }
              },
              "required": [
               "key"
              ],
              "type": "object"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "name"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "hostPort": {
         "description": "Number of port to expose on the host. If specified, this must be a valid port number, 0 \u003c x \u003c 65536. If HostNetwork is specified, this must match ContainerPort. Most containers do not need this.",
         "format": "int32",
         "type": "integer"
        },
        "resources": {
         "description": "Datadog APM Agent resource requests and limits Make sure to keep requests and limits equal to keep the pods in the Guaranteed QoS class Ref: http://kubernetes.io/docs/user-guide/compute-resources/",
         "properties": {
          "limits": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
           },
           "description": "Limits describes the maximum amount of compute resources allowed. More info: https://kubernetes.io/docs/concepts/configuration/manage-compute-resources-container/",
           "type": "object"
          },
          "requests": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
           },
           "description": "Requests describes the minimum amount of compute resources required. If Requests is omitted for a container, it defaults to Limits if that is explicitly specified, otherwise to an implementation-defined value. More info: https://kubernetes.io/docs/concepts/configuration/manage-compute-resources-container/",
           "type": "object"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "config": {
       "description": "Agent configuration",
       "properties": {
        "checksd": {
         "description": "Checksd configuration allowing to specify custom checks placed under /etc/datadog-agent/checks.d/ See https://docs.datadoghq.com/agent/guide/agent-configuration-files/?tab=agentv6 for more details.",
         "properties": {
          "configMapName": {
           "description": "ConfigMapName name of a ConfigMap used to mount a directory",
           "type": "string"
          }
         },
         "type": "object"
        },
        "collectEvents": {
         "description": "nables this to start event collection from the kubernetes API ref: https://docs.datadoghq.com/agent/kubernetes/event_collection/",
         "type": "boolean"
        },
        "confd": {
         "description": "Confd configuration allowing to specify config files for custom checks placed under /etc/datadog-agent/conf.d/. See https://docs.datadoghq.com/agent/guide/agent-configuration-files/?tab=agentv6 for more details.",
         "properties": {
          "configMapName": {
           "description": "ConfigMapName name of a ConfigMap used to mount a directory",
           "type": "string"
          }
         },
         "type": "object"
        },
        "criSocket": {
         "description": "Configure the CRI Socket",
         "properties": {
          "criSocketPath": {
           "description": "Path to the container runtime socket (if different from Docker) This is supported starting from agent 6.6.0",
           "type": "string"
          },
          "dockerSocketPath": {
           "description": "Path to the docker runtime socket",
           "type": "string"
          }
         },
         "type": "object"
        },
        "ddUrl": {
         "description": "The host of the Datadog intake server to send Agent data to, only set this option if you need the Agent to send data to a custom URL. Overrides the site setting defined in \"site\".",
         "type": "string"
        },
        "dogstatsd": {
         "description": "Configure Dogstatsd",
         "properties": {
          "dogstatsdOriginDetection": {
           "description": "Enable origin detection for container tagging https://docs.datadoghq.com/developers/dogstatsd/unix_socket/#using-origin-detection-for-container-tagging",
           "type": "boolean"
          },
          "useDogStatsDSocketVolume": {
           "description": "Enable dogstatsd over Unix Domain Socket ref: https://docs.datadoghq.com/developers/dogstatsd/unix_socket/",
           "type": "boolean"
          }
         },
         "type": "object"
        },
        "env": {
         "description": "The Datadog Agent supports many environment variables Ref: https://docs.datadoghq.com/agent/docker/?tab=standard#environment-variables",
         "items": {
          "description": "EnvVar represents an environment variable present in a Container.",
          "properties": {
           "name": {
            "description": "Name of the environment variable. Must be a C_IDENTIFIER.",
            "type": "string"
           },
           "value": {
            "description": "Variable references $(VAR_NAME) are expanded using the previous defined environment variables in the container and any service environment variables. If a variable cannot be resolved, the reference in the input string will be unchanged. The $(VAR_NAME) syntax can be escaped with a double $$, ie: $$(VAR_NAME). Escaped references will never be expanded, regardless of whether the variable exists or not. Defaults to \"\".",
            "type": "string"
           },
           "valueFrom": {
            "description": "Source for the environment variable's value. Cannot be used if value is not empty.",
            "properties": {
             "configMapKeyRef": {
              "description": "Selects a key of a ConfigMap.",
              "properties": {
               "key": {
                "description": "The key to select.",
                "type": "string"
               },
               "name": {
                "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
                "type": "string"
               },
               "optional": {
                "description": "Specify whether the ConfigMap or its key must be defined",
                "type": "boolean"
               }
              },
              "required": [
               "key"
              ],
              "type": "object"
             },
             "fieldRef": {
              "description": "Selects a field of the pod: supports metadata.name, metadata.namespace, metadata.labels, metadata.annotations, spec.nodeName, spec.serviceAccountName, status.hostIP, status.podIP, status.podIPs.",
              "properties": {
               "apiVersion": {
                "description": "Version of the schema the FieldPath is written in terms of, defaults to \"v1\".",
                "type": "string"
               },
               "fieldPath": {
                "description": "Path of the field to select in the specified API version.",
                "type": "string"
               }
              },
              "required": [
               "fieldPath"
              ],
              "type": "object"
             },
             "resourceFieldRef": {
              "description": "Selects a resource of the container: only resources limits and requests (limits.cpu, limits.memory, limits.ephemeral-storage, requests.cpu, requests.memory and requests.ephemeral-storage) are currently supported.",
              "properties": {
               "containerName": {
                "description": "Container name: required for volumes, optional for env vars",
                "type": "string"
               },
               "divisor": {
                "anyOf": [
                 {
                  "type": "integer"
                 },
                 {
                  "type": "string"
                 }
                ],
                "description": "Specifies the output format of the exposed resources, defaults to \"1\"",
                "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
               },
               "resource": {
                "description": "Required: resource to select",
                "type": "string"
               }
              },
              "required": [
               "resource"
              ],
              "type": "object"
             },
             "secretKeyRef": {
              "description": "Selects a key of a secret in the pod's namespace",
              "properties": {
               "key": {
                "description": "The key of the secret to select from.  Must be a valid secret key.",
                "type": "string"
               },
               "name": {
                "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
                "type": "string"
               },
               "optional": {
                "description": "Specify whether the Secret or its key must be defined",
                "type": "boolean"
               }
              },
              "required": [
               "key"
              ],
              "type": "object"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "name"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "hostPort": {
         "description": "Number of port to expose on the host. If specified, this must be a valid port number, 0 \u003c x \u003c 65536. If HostNetwork is specified, this must match ContainerPort. Most containers do not need this.",
         "format": "int32",
         "type": "integer"
        },
        "leaderElection": {
         "description": "Enables leader election mechanism for event collection.",
         "type": "boolean"
        },
        "logLevel": {
         "description": "Set logging verbosity, valid log levels are: trace, debug, info, warn, error, critical, and off",
         "type": "string"
        },
        "podAnnotationsAsTags": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Provide a mapping of Kubernetes Annotations to Datadog Tags. \u003cKUBERNETES_ANNOTATIONS\u003e: \u003cDATADOG_TAG_KEY\u003e",
         "type": "object"
        },
        "podLabelsAsTags": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Provide a mapping of Kubernetes Labels to Datadog Tags. \u003cKUBERNETES_LABEL\u003e: \u003cDATADOG_TAG_KEY\u003e",
         "type": "object"
        },
        "resources": {
         "description": "Datadog Agent resource requests and limits Make sure to keep requests and limits equal to keep the pods in the Guaranteed QoS class Ref: http://kubernetes.io/docs/user-guide/compute-resources/",
         "properties": {
          "limits": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
           },
           "description": "Limits describes the maximum amount of compute resources allowed. More info: https://kubernetes.io/docs/concepts/configuration/manage-compute-resources-container/",
           "type": "object"
          },
          "requests": {
           "additionalProperties": {
            "anyOf": [
             {
              "type": "integer"
             },
             {
              "type": "string"
             }
            ],
            "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$"
           },
           "description": "Requests describes the minimum amount of compute resources required. If Requests is omitted for a container, it defaults to Limits if that is explicitly specified, otherwise to an implementation-defined value. More info: https://kubernetes.io/docs/concepts/configuration/manage-compute-resources-container/",
           "type": "object"
          }
         },
         "type": "object"
        },
        "securityContext": {
         "description": "Pod-level SecurityContext",
         "properties": {
          "fsGroup": {
           "description": "A special supplemental group that applies to all containers in a pod. Some volume types allow the Kubelet to change the ownership of that volume to be owned by the pod: \n 1. The owning GID will be the FSGroup 2. The setgid bit is set (new files created in the volume will be owned by FSGroup) 3. The permission bits are OR'd with rw-rw---- \n If unset, the Kubelet will not modify the ownership and permissions of any volume.",
           "format": "int64",
           "type": "integer"
          },
          "fsGroupChangePolicy": {
           "description": "fsGroupChangePolicy defines behavior of changing ownership and permission of the volume before being exposed inside Pod. This field will only apply to volume types which support fsGroup based ownership(and permissions). It will have no effect on ephemeral volume types such as: secret, configmaps and emptydir. Valid values are \"OnRootMismatch\" and \"Always\". If not specified defaults to \"Always\".",
           "type": "string"
          },
          "runAsGroup": {
           "description": "The GID to run the entrypoint of the container process. Uses runtime default if unset. May also be set in SecurityContext.  If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence for that container.",
           "format": "int64",
           "type": "integer"
          },
          "runAsNonRoot": {
           "description": "Indicates that the container must run as a non-root user. If true, the Kubelet will validate the image at runtime to ensure that it does not run as UID 0 (root) and fail to start the container if it does. If unset or false, no such validation will be performed. May also be set in SecurityContext.  If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence.",
           "type": "boolean"
          },
          "runAsUser": {
           "description": "The UID to run the entrypoint of the container process. Defaults to user specified in image metadata if unspecified. May also be set in SecurityContext.  If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence for that container.",
           "format": "int64",
           "type": "integer"
          },
          "seLinuxOptions": {
           "description": "The SELinux context to be applied to all containers. If unspecified, the container runtime will allocate a random SELinux context for each container.  May also be set in SecurityContext.  If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence for that container.",
           "properties": {
            "level": {
             "description": "Level is SELinux level label that applies to the container.",
             "type": "string"
            },
            "role": {
             "description": "Role is a SELinux role label that applies to the container.",
             "type": "string"
            },
            "type": {
             "description": "Type is a SELinux type label that applies to the container.",
             "type": "string"
            },
            "user": {
             "description": "User is a SELinux user label that applies to the container.",
             "type": "string"
            }
           },
           "type": "object"
          },
          "supplementalGroups": {
           "description": "A list of groups applied to the first process run in each container, in addition to the container's primary GID.  If unspecified, no groups will be added to any container.",
           "items": {
            "format": "int64",
            "type": "integer"
           },
           "type": "array"
          },
          "sysctls": {
           "description": "Sysctls hold a list of namespaced sysctls used for the pod. Pods with unsupported sysctls (by the container runtime) might fail to launch.",
           "items": {
            "description": "Sysctl defines a kernel parameter to be set",
            "properties": {
             "name": {
              "description": "Name of a property to set",
              "type": "string"
             },
             "value": {
              "description": "Value of a property to set",
              "type": "string"
             }
            },
            "required": [
             "name",
             "value"
            ],
            "type": "object"
           },
           "type": "array"
          },
          "windowsOptions": {
           "description": "The Windows specific settings applied to all containers. If unspecified, the options within a container's SecurityContext will be used. If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence.",
           "properties": {
            "gmsaCredentialSpec": {
             "description": "GMSACredentialSpec is where the GMSA admission webhook (https://github.com/kubernetes-sigs/windows-gmsa) inlines the contents of the GMSA credential spec named by the GMSACredentialSpecName field.",
             "type": "string"
            },
            "gmsaCredentialSpecName": {
             "description": "GMSACredentialSpecName is the name of the GMSA credential spec to use.",
             "type": "string"
            },
            "runAsUserName": {
             "description": "The UserName in Windows to run the entrypoint of the container process. Defaults to the user specified in image metadata if unspecified. May also be set in PodSecurityContext. If set in both SecurityContext and PodSecurityContext, the value specified in SecurityContext takes precedence.",
             "type": "string"
            }
           },
           "type": "object"
          }
         },
         "type": "object"
        },
        "tags": {
         "description": "List of tags to attach to every metric, event and service check collected by this Agent. Learn more about tagging: https://docs.datadoghq.com/tagging/",
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "tolerations": {
         "description": "If specified, the Agent pod's tolerations.",
         "items": {
          "description": "The pod this Toleration is attached to tolerates any taint that matches the triple \u003ckey,value,effect\u003e using the matching operator \u003coperator\u003e.",
          "properties": {
           "effect": {
            "description": "Effect indicates the taint effect to match. Empty means match all taint effects. When specified, allowed values are NoSchedule, PreferNoSchedule and NoExecute.",
            "type": "string"
           },
           "key": {
            "description": "Key is the taint key that the toleration applies to. Empty means match all taint keys. If the key is empty, operator must be Exists; this combination means to match all values and all keys.",
            "type": "string"
           },
           "operator": {
            "description": "Operator represents a key's relationship to the value. Valid operators are Exists and Equal. Defaults to Equal. Exists is equivalent to wildcard for value, so that a pod can tolerate all taints of a particular category.",
            "type": "string"
           },
           "tolerationSeconds": {
            "description": "TolerationSeconds represents the period of time the toleration (which must be of effect NoExecute, otherwise this field is ignored) tolerates the taint. By default, it is not set, which means tolerate the taint forever (do not evict). Zero and negative values will be treated as 0 (evict immediately) by the system.",
            "format": "int64",
            "type": "integer"
           },
           "value": {
            "description": "Value is the taint value the toleration matches to. If the operator is Exists, the value should be empty, otherwise just a regular string.",
            "type": "string"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "volumeMounts": {
         "description": "Specify additional volume mounts in the Datadog Agent container",
         "items": {
          "description": "VolumeMount describes a mounting of a Volume within a container.",
          "properties": {
           "mountPath": {
            "description": "Path within the container at which the volume should be mounted.  Must not contain ':'.",
            "type": "string"
           },
           "mountPropagation": {
            "description": "mountPropagation determines how mounts are propagated from the host to container and the other way around. When not set, MountPropagationNone is used. This field is beta in 1.10.",
            "type": "string"
           },
           "name": {
            "description": "This must match the Name of a Volume.",
            "type": "string"
           },
           "readOnly": {
            "description": "Mounted read-only if true, read-write otherwise (false or unspecified). Defaults to false.",
            "type": "boolean"
           },
           "subPath": {
            "description": "Path within the volume from which the container's volume should be mounted. Defaults to \"\" (volume's root).",
            "type": "string"
           },
           "subPathExpr": {
            "description": "Expanded path within the volume from which the container's volume should be mounted. Behaves similarly to SubPath but environment variable references $(VAR_NAME) are expanded using the container's environment. Defaults to \"\" (volume's root). SubPathExpr and SubPath are mutually exclusive.",
            "type": "string"
           }
          },
          "required": [
           "mountPath",
           "name"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "volumes": {
         "description": "Specify additional volumes in the Datadog Agent container",
         "items": {
          "description": "Volume represents a named volume in a pod that may be accessed by any container in the pod.",
          "properties": {
           "awsElasticBlockStore": {
            "description": "AWSElasticBlockStore represents an AWS Disk resource that is attached to a kubelet's host machine and then exposed to the pod. More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore",
            "properties": {
             "fsType": {
              "description": "Filesystem type of the volume that you want to mount. Tip: Ensure that the filesystem type is supported by the host operating system. Examples: \"ext4\", \"xfs\", \"ntfs\". Implicitly inferred to be \"ext4\" if unspecified. More info: https://kubernetes.io/docs/concepts/storage/volumes#awselasticblockstore TODO: how do we prevent errors in the filesystem from compromising the machine",
              "type": "string"
             },
             "partition": {
              "description": "The partition in the volume that you want to mount. If omitted, the default is to mount by volume name. Examples: For volume /dev/sda1, you specify the partition as \"1\". Similarly, the volume partition for /dev/sda is \"0\" (or you can leave the property empty).",
              "format": "int32",
              "type": "integer"
             },
