schema:
{
 "description": "CertificateRequestSpec defines the desired state of CertificateRequest",
 "properties": {
  "csr": {
   "description": "Byte slice containing the PEM encoded CertificateSigningRequest",
   "format": "byte",
   "type": "string"
  },
  "duration": {
   "description": "Requested certificate default Duration",
   "type": "string"
  },
  "isCA": {
   "description": "IsCA will mark the resulting certificate as valid for signing. This implies that the 'cert sign' usage is set",
   "type": "boolean"
  },
  "issuerRef": {
   "description": "IssuerRef is a reference to the issuer for this CertificateRequest.  If the 'kind' field is not set, or set to 'Issuer', an Issuer resource with the given name in the same namespace as the CertificateRequest will be used.  If the 'kind' field is set to 'ClusterIssuer', a ClusterIssuer with the provided name will be used. The 'name' field in this stanza is required at all times. The group field refers to the API group of the issuer which defaults to 'cert-manager.io' if empty.",
   "properties": {
    "group": {
     "type": "string"
    },
    "kind": {
     "type": "string"
    },
    "name": {
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "usages": {
   "description": "Usages is the set of x509 actions that are enabled for a given key. Defaults are ('digital signature', 'key encipherment') if empty",
   "items": {
    "description": "KeyUsage specifies valid usage contexts for keys. See: https://tools.ietf.org/html/rfc5280#section-4.2.1.3      https://tools.ietf.org/html/rfc5280#section-4.2.1.12 Valid KeyUsage values are as follows: \"signing\", \"digital signature\", \"content commitment\", \"key encipherment\", \"key agreement\", \"data encipherment\", \"cert sign\", \"crl sign\", \"encipher only\", \"decipher only\", \"any\", \"server auth\", \"client auth\", \"code signing\", \"email protection\", \"s/mime\", \"ipsec end system\", \"ipsec tunnel\", \"ipsec user\", \"timestamping\", \"ocsp signing\", \"microsoft sgc\", \"netscape sgc\"",
    "enum": [
     "signing",
     "digital signature",
     "content commitment",
     "key encipherment",
     "key agreement",
     "data encipherment",
     "cert sign",
     "crl sign",
     "encipher only",
     "decipher only",
     "any",
     "server auth",
     "client auth",
     "code signing",
     "email protection",
     "s/mime",
     "ipsec end system",
     "ipsec tunnel",
     "ipsec user",
     "timestamping",
     "ocsp signing",
     "microsoft sgc",
     "netscape sgc"
    ],
    "type": "string"
   },
   "type": "array"
  }
 },
 "required": [
  "csr",
  "issuerRef"
 ],
 "title": "Certificate Request",
 "type": "object"
}
