schema:
{
 "description": "CodebaseSpec defines the desired state of Codebase.",
 "properties": {
  "branchToCopyInDefaultBranch": {
   "description": "While we clone new codebase we can select specific branch to clone. Selected branch will become a default branch for a new codebase (e.g. master, main).",
   "type": "string"
  },
  "buildTool": {
   "description": "A build tool which is used on codebase.",
   "type": "string"
  },
  "ciTool": {
   "description": "A name of tool which should be used as CI.",
   "type": "string"
  },
  "commitMessagePattern": {
   "nullable": true,
   "type": "string"
  },
  "defaultBranch": {
   "description": "Name of default branch.",
   "type": "string"
  },
  "deploymentScript": {
   "type": "string"
  },
  "description": {
   "description": "A short description of codebase.",
   "nullable": true,
   "type": "string"
  },
  "disablePutDeployTemplates": {
   "description": "Controller must skip step \"put deploy templates\" in action chain.",
   "type": "boolean"
  },
  "emptyProject": {
   "description": "A flag indicating how project should be provisioned. Default: false",
   "type": "boolean"
  },
  "framework": {
   "description": "A framework used in codebase.",
   "nullable": true,
   "type": "string"
  },
  "gitServer": {
   "description": "A name of git server which will be used as VCS. Example: \"gerrit\".",
   "type": "string"
  },
  "gitUrlPath": {
   "description": "A link to external git server, used for \"import\" strategy.",
   "nullable": true,
   "type": "string"
  },
  "jenkinsSlave": {
   "description": "A name of Jenkins slave instance which will be used to handle codebase.",
   "nullable": true,
   "type": "string"
  },
  "jiraIssueMetadataPayload": {
   "nullable": true,
   "type": "string"
  },
  "jiraServer": {
   "nullable": true,
   "type": "string"
  },
  "jobProvisioning": {
   "nullable": true,
   "type": "string"
  },
  "lang": {
   "description": "Programming language used in codebase.",
   "type": "string"
  },
  "perf": {
   "nullable": true,
   "properties": {
    "dataSources": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "name": {
     "type": "string"
    }
   },
   "required": [
    "dataSources",
    "name"
   ],
   "type": "object"
  },
  "repository": {
   "nullable": true,
   "properties": {
    "url": {
     "type": "string"
    }
   },
   "required": [
    "url"
   ],
   "type": "object"
  },
  "strategy": {
   "description": "integration strategy for a codebase, e.g. clone, import, etc.",
   "enum": [
    "create",
    "clone",
    "import"
   ],
   "type": "string"
  },
  "testReportFramework": {
   "nullable": true,
   "type": "string"
  },
  "ticketNamePattern": {
   "nullable": true,
   "type": "string"
  },
  "type": {
   "description": "Type of codebase. E.g. application, autotest or library.",
   "type": "string"
  },
  "versioning": {
   "properties": {
    "startFrom": {
     "nullable": true,
     "type": "string"
    },
    "type": {
     "type": "string"
    }
   },
   "required": [
    "type"
   ],
   "type": "object"
  }
 },
 "required": [
  "buildTool",
  "ciTool",
  "defaultBranch",
  "emptyProject",
  "gitServer",
  "lang",
  "strategy",
  "type",
  "versioning"
 ],
 "title": "Codebase",
 "type": "object"
}
