schema:
{
 "description": "GerritSpec defines the desired state of Gerrit.",
 "properties": {
  "basePath": {
   "description": "BasePath gerrit http route base path",
   "type": "string"
  },
  "keycloakSpec": {
   "properties": {
    "enabled": {
     "type": "boolean"
    },
    "realm": {
     "type": "string"
    },
    "url": {
     "type": "string"
    }
   },
   "required": [
    "enabled"
   ],
   "type": "object"
  },
  "restAPIUrl": {
   "description": "RestAPIUrl gerrit http full api url",
   "type": "string"
  },
  "sshPort": {
   "format": "int32",
   "type": "integer"
  },
  "sshUrl": {
   "description": "SSHUrl gerrit ssh url",
   "type": "string"
  }
 },
 "required": [
  "keycloakSpec"
 ],
 "title": "Gerrit",
 "type": "object"
}
