schema:
{
 "description": "CDPipelineSpec defines the desired state of CDPipeline.",
 "properties": {
  "applications": {
   "description": "A list of applications included in CDPipeline.",
   "items": {
    "type": "string"
   },
   "minItems": 1,
   "type": "array"
  },
  "applicationsToPromote": {
   "description": "A list of applications which will promote after successful release.",
   "items": {
    "type": "string"
   },
   "nullable": true,
   "type": "array"
  },
  "deploymentType": {
   "description": "Which type of kind will be deployed e.g. Container, Custom",
   "type": "string"
  },
  "inputDockerStreams": {
   "description": "A list of docker streams",
   "items": {
    "type": "string"
   },
   "minItems": 1,
   "type": "array"
  },
  "name": {
   "description": "Name of CD pipeline",
   "minLength": 2,
   "type": "string"
  }
 },
 "required": [
  "applications",
  "deploymentType",
  "inputDockerStreams",
  "name"
 ],
 "title": "CD Pipeline",
 "type": "object"
}
