schema:
{
 "description": "GitServerSpec defines the desired state of GitServer.",
 "properties": {
  "gitHost": {
   "type": "string"
  },
  "gitProvider": {
   "default": "gerrit",
   "description": "GitProvider is a git provider type. It can be gerrit, github or gitlab. Default value is gerrit.",
   "enum": [
    "gerrit",
    "gitlab",
    "github"
   ],
   "type": "string"
  },
  "gitUser": {
   "default": "git",
   "description": "GitUser is a user name for git server.",
   "type": "string"
  },
  "httpsPort": {
   "format": "int32",
   "type": "integer"
  },
  "nameSshKeySecret": {
   "type": "string"
  },
  "sshPort": {
   "format": "int32",
   "type": "integer"
  }
 },
 "required": [
  "gitHost",
  "httpsPort",
  "nameSshKeySecret",
  "sshPort"
 ],
 "title": "Git Server",
 "type": "object"
}
