schema:
{
 "description": "SonarSpec defines the desired state of Sonar.",
 "properties": {
  "basePath": {
   "type": "string"
  },
  "defaultPermissionTemplate": {
   "type": "string"
  },
  "edpSpec": {
   "properties": {
    "dnsWildcard": {
     "type": "string"
    }
   },
   "required": [
    "dnsWildcard"
   ],
   "type": "object"
  },
  "exposeToKeycloak": {
   "description": "ExposeToKeycloak specifies whether the sonar should be exposed to keycloak.",
   "type": "boolean"
  },
  "groups": {
   "description": "Groups specify which groups should be created.",
   "items": {
    "properties": {
     "name": {
      "type": "string"
     },
     "permissions": {
      "items": {
       "type": "string"
      },
      "type": "array"
     }
    },
    "required": [
     "name"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "plugins": {
   "description": "Plugins specify which plugins should be installed to sonar.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "qualityGates": {
   "description": "QualityGates specify which quality gates should be created.",
   "items": {
    "properties": {
     "conditions": {
      "items": {
       "properties": {
        "error": {
         "type": "string"
        },
        "metric": {
         "type": "string"
        },
        "op": {
         "type": "string"
        },
        "period": {
         "type": "string"
        }
       },
       "required": [
        "error",
        "metric",
        "op"
       ],
       "type": "object"
      },
      "type": "array"
     },
     "name": {
      "type": "string"
     },
     "setAsDefault": {
      "type": "boolean"
     }
    },
    "required": [
     "conditions",
     "name"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "secret": {
   "description": "Secret is the name of the k8s object Secret related to sonar.",
   "type": "string"
  },
  "settings": {
   "description": "Settings specify which settings should be configured.",
   "items": {
    "properties": {
     "key": {
      "type": "string"
     },
     "value": {
      "type": "string"
     },
     "valueType": {
      "type": "string"
     }
    },
    "required": [
     "key",
     "value",
     "valueType"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "url": {
   "description": "Url is used to explicitly specify the url of sonar. It may not be needed if the sonar is deployed in the same cluster.",
   "type": "string"
  },
  "users": {
   "description": "Users specify which users should be created.",
   "items": {
    "properties": {
     "exposeToJenkins": {
      "type": "boolean"
     },
     "group": {
      "type": "string"
     },
     "login": {
      "type": "string"
     },
     "permissions": {
      "items": {
       "type": "string"
      },
      "type": "array"
     },
     "username": {
      "type": "string"
     }
    },
    "required": [
     "login",
     "username"
    ],
    "type": "object"
   },
   "type": "array"
  }
 },
 "required": [
  "edpSpec",
  "secret"
 ],
 "title": "Sonar",
 "type": "object"
}
