     "type": "object"
    },
    "json": {
     "type": "string"
    },
    "jsonnet": {
     "type": "string"
    },
    "plugins": {
     "items": {
      "description": "GrafanaPlugin contains information about a single plugin",
      "properties": {
       "name": {
        "type": "string"
       },
       "version": {
        "type": "string"
       }
      },
      "required": [
       "name",
       "version"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "url": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Grafana Dashboard",
 "type": "object"
}
