schema:
{
 "description": "GerritMergeRequest is the Schema for the gerrit merge request API.",
 "properties": {
  "spec": {
   "description": "GerritMergeRequestSpec defines the desired state of GerritMergeRequest.",
   "properties": {
    "additionalArguments": {
     "description": "AdditionalArguments contains merge command additional command line arguments.",
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "authorEmail": {
     "type": "string"
    },
    "authorName": {
     "type": "string"
    },
    "changesConfigMap": {
     "type": "string"
    },
    "commitMessage": {
     "type": "string"
    },
    "ownerName": {
     "description": "OwnerName indicates which gerrit CR should be taken to initialize correct client.",
     "nullable": true,
     "type": "string"
    },
    "projectName": {
     "description": "ProjectName is gerrit project name.",
     "type": "string"
    },
    "sourceBranch": {
     "type": "string"
    },
    "targetBranch": {
     "description": "TargetBranch default value is master.",
     "type": "string"
    }
   },
   "required": [
    "authorEmail",
    "authorName",
    "ownerName",
    "projectName"
   ],
   "type": "object"
  }
 },
 "title": "Gerrit Merge Request",
 "type": "object"
}
