schema:
{
 "description": "QueueSpec defines the desired state of Queue It cannot be updated after the Queue is created",
 "properties": {
  "asyncBackupCount": {
   "default": 0,
   "description": "Number of asynchronous backups.",
   "format": "int32",
   "type": "integer"
  },
  "backupCount": {
   "default": 1,
   "description": "Number of synchronous backups.",
   "format": "int32",
   "type": "integer"
  },
  "emptyQueueTTLSeconds": {
   "default": -1,
   "description": "Time in seconds after which the Queue will be destroyed if it stays empty or unused. If the values is not provided the Queue will never be destroyed.",
   "format": "int32",
   "type": "integer"
  },
  "hazelcastResourceName": {
   "description": "HazelcastResourceName defines the name of the Hazelcast resource.",
   "minLength": 1,
   "type": "string"
  },
  "maxSize": {
   "default": 0,
   "description": "Max size of the queue.",
   "format": "int32",
   "type": "integer"
  },
  "name": {
   "description": "Name of the data structure config to be created. If empty, CR name will be used. It cannot be updated after the config is created successfully.",
   "type": "string"
  },
  "priorityComparatorClassName": {
   "description": "The name of the comparator class. If the class name is provided, the Queue becomes Priority Queue. You can learn more at https://docs.hazelcast.com/hazelcast/latest/data-structures/priority-queue.",
   "type": "string"
  }
 },
 "required": [
  "hazelcastResourceName"
 ],
 "title": "Queue",
 "type": "object"
}
