schema:
{
 "description": "ReplicatedMapSpec defines the desired state of ReplicatedMap",
 "properties": {
  "asyncFillup": {
   "default": true,
   "description": "AsyncFillup specifies whether the ReplicatedMap is available for reads before the initial replication is completed",
   "type": "boolean"
  },
  "hazelcastResourceName": {
   "description": "HazelcastResourceName defines the name of the Hazelcast resource.",
   "minLength": 1,
   "type": "string"
  },
  "inMemoryFormat": {
   "default": "OBJECT",
   "description": "InMemoryFormat specifies in which format data will be stored in the ReplicatedMap",
   "enum": [
    "BINARY",
    "OBJECT"
   ],
   "type": "string"
  },
  "name": {
   "description": "Name of the ReplicatedMap config to be created. If empty, CR name will be used.",
   "type": "string"
  }
 },
 "required": [
  "hazelcastResourceName"
 ],
 "title": "Replicated Map",
 "type": "object"
}
