schema:
{
 "description": "TopicSpec defines the desired state of Topic",
 "properties": {
  "globalOrderingEnabled": {
   "default": false,
   "description": "When true all nodes listening to the same topic get their messages in the same order",
   "type": "boolean"
  },
  "hazelcastResourceName": {
   "description": "HazelcastResourceName defines the name of the Hazelcast resource for which topic config will be created",
   "minLength": 1,
   "type": "string"
  },
  "multiThreadingEnabled": {
   "default": false,
   "description": "When true enables multi-threaded processing of incoming messages, otherwise a single thread will handle all topic messages",
   "type": "boolean"
  },
  "name": {
   "description": "Name of the topic config to be created. If empty, CR name will be used.",
   "type": "string"
  }
 },
 "required": [
  "hazelcastResourceName"
 ],
 "title": "Topic",
 "type": "object"
}
