schema:
{
 "description": "VaultClientConfigSpec defines the desired state of VaultClientConfig.",
 "properties": {
  "address": {
   "type": "string"
  },
  "authMountPath": {
   "type": "string"
  },
  "caCerts": {
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "certificateAuthorities": {
   "items": {
    "properties": {
     "capabilities": {
      "items": {
       "description": "VaultBindingCertificateAuthorityCapability represents Vault capabilities for VaultCertificateAuthority objects which can be granted to a subject. The \"read_public\" capability is granted by default",
       "enum": [
        "read_public",
        "read_private"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "enginePath": {
      "type": "string"
     },
     "kvSecrets": {
      "properties": {
       "enginePath": {
        "type": "string"
       },
       "privateSecret": {
        "type": "string"
       },
       "publicSecret": {
        "type": "string"
       }
      },
      "type": "object"
     },
     "name": {
      "type": "string"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "certificates": {
   "items": {
    "properties": {
     "capabilities": {
      "items": {
       "description": "VaultBindingCertificateCapability represents capabilities for VaultCertificateRole objects which can be granted to a subject. The \"issue\" capability is granted by default",
       "enum": [
        "issue",
        "sign_csr",
        "sign_verbatim"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "enginePath": {
      "type": "string"
     },
     "name": {
      "type": "string"
     },
     "roleName": {
      "type": "string"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "kvSecrets": {
   "items": {
    "properties": {
     "capabilities": {
      "items": {
       "description": "VaultBindingKVCapability represents capabilities for VaultKVSecret objects which can be granted to a subject. The \"read\" capability is granted by default.",
       "enum": [
        "read"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "enginePath": {
      "type": "string"
     },
     "name": {
      "type": "string"
     },
     "secretPath": {
      "type": "string"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "role": {
   "type": "string"
  },
  "templates": {
   "properties": {
    "certificateTemplates": {
     "description": "CertificateTemplates is a list of certificate templates to be used when issuing certificates in the agent.",
     "items": {
      "properties": {
       "alias": {
        "description": "Alias is the name of this certificate template.",
        "type": "string"
       },
       "certificateRole": {
        "description": "CertificateRole is the name of the VaultCertificateRole to be used for issuing this certificate.",
        "type": "string"
       },
       "commonName": {
        "description": "CommonName is the CN (common name) of the issued certificate.",
        "type": "string"
       },
       "dnsSans": {
        "description": "DNSSans is a list of DNS subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "excludeCNFromSans": {
        "description": "ExcludeCNFromSans toggles if the common name should be excluded from the subject alternative names of the certificate.",
        "type": "boolean"
       },
       "ipSans": {
        "description": "IPSans is a list of IP subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "otherSans": {
        "description": "OtherSans is a list of custom OID/UTF-8 subject alternative names requested for this certificate. Expected Format: `\u003coid\u003e;\u003ctype\u003e:\u003cvalue\u003e`",
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "ttl": {
        "description": "TTL is the Time-To-Live requested for this certificate.",
        "type": "string"
       },
       "uriSans": {
        "description": "AlternativeNames is a list of URI subject alternative names requested for this certificate.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "certificateRole"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "templates": {
     "description": "Templates is a list of files to be populated in relevant pods by the Heist agent.",
     "items": {
      "properties": {
       "mode": {
        "description": "Mode is the desired file mode of the output file. 0640 is the default. The agent is the owner of the file, owner permissions will always be read + write and cannot be modified this way. Must be specified as octal.",
        "pattern": "^[0][0-7]{3}$",
        "type": "string"
       },
       "path": {
        "description": "Path is the desired output path for this value. Relative paths are interpreted to be relative to the default Heist secret directory /heist/secrets. The path must be in a shared directory, where the Heist Agent and application container have access.",
        "type": "string"
       },
       "template": {
        "description": "Template is the template for this value. The template supports [sprig](https://masterminds.github.io/sprig/) template functions and can access all bound secrets and associated capabilities with additional template functions: - `kvSecret \"\u003cname\u003e\" \"\u003cfield\u003e\"`: retrieves the value of field \"\u003cfield\u003e\" from a KV secret with name \"\u003cname\u003e\". - `caField \"\u003cname\u003e\" \"\u003cfield\u003e\"`: retrieves the value of field \"\u003cfield\u003e\" from CA \"\u003cname\u003e\". Supported values for \"\u003cfield\u003e\" are defined in VaultCertificateFieldType. - `certField \"\u003cname\u003e\" \"\u003cfield\u003e\"`: retrieves the value of field \"\u003cfield\u003e\" from certificate template \"\u003cname\u003e\". Supported values for \"\u003cfield\u003e\" are defined in VaultCertificateFieldType.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array"
    }
   },
   "type": "object"
  },
  "transitKeys": {
   "items": {
    "properties": {
     "capabilities": {
      "items": {
       "description": "VaultBindingTransitKeyCapability represents capabilities for VaultTransitKey objects which can be granted to a subject.",
       "enum": [
        "encrypt",
        "decrypt",
        "datakey",
        "rewrap",
        "sign",
        "hmac",
        "verify",
        "read"
       ],
       "type": "string"
      },
      "type": "array"
     },
     "enginePath": {
      "type": "string"
     },
     "keyName": {
      "type": "string"
     },
     "name": {
      "type": "string"
     }
    },
    "type": "object"
   },
   "type": "array"
  }
 },
 "title": "Vault Client Config",
 "type": "object"
}
