schema:
{
 "description": "IPInstance is the Schema for the ipinstances API",
 "properties": {
  "spec": {
   "description": "IPInstanceSpec defines the desired state of IPInstance",
   "properties": {
    "address": {
     "properties": {
      "gateway": {
       "type": "string"
      },
      "ip": {
       "type": "string"
      },
      "mac": {
       "type": "string"
      },
      "netID": {
       "format": "int32",
       "type": "integer"
      },
      "version": {
       "type": "string"
      }
     },
     "required": [
      "ip",
      "mac",
      "version"
     ],
     "type": "object"
    },
    "binding": {
     "description": "Binding defines a binding object with necessary info of an IPInstance",
     "properties": {
      "nodeName": {
       "type": "string"
      },
      "podName": {
       "type": "string"
      },
      "podUID": {
       "description": "UID is a type that holds unique ID values, including UUIDs.  Because we don't ONLY use UUIDs, this is an alias to string.  Being a type captures intent and helps make sure that UIDs and names do not get conflated.",
       "type": "string"
      },
      "referredObject": {
       "description": "ObjectMeta is a short version of ObjectMeta which is pointing to an Object in specified namespace",
       "properties": {
        "kind": {
         "type": "string"
        },
        "name": {
         "type": "string"
        },
        "uid": {
         "description": "UID is a type that holds unique ID values, including UUIDs.  Because we don't ONLY use UUIDs, this is an alias to string.  Being a type captures intent and helps make sure that UIDs and names do not get conflated.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "stateful": {
       "description": "StatefulInfo is a collection of related info if binding to a stateful workload",
       "properties": {
        "index": {
         "format": "int32",
         "type": "integer"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "network": {
     "type": "string"
    },
    "subnet": {
     "type": "string"
    }
   },
   "required": [
    "address",
    "network",
    "subnet"
   ],
   "type": "object"
  }
 },
 "title": "IP Instance",
 "type": "object"
}
