schema:
{
 "description": "NodeInfo is the Schema for the NodeInfos API",
 "properties": {
  "spec": {
   "description": "NodeInfoSpec defines the desired state of NodeInfo",
   "properties": {
    "vtepInfo": {
     "description": "vtepInfo is the basic information of this node as a VTEP. Not necessary if no overlay network exist.",
     "properties": {
      "ip": {
       "description": "IP is the gateway IP address of this VTEP.",
       "type": "string"
      },
      "localIPs": {
       "description": "localIPs are the usable ip addresses for the VTEP itself.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "mac": {
       "description": "MAC is the MAC address of this VTEP.",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Node Info",
 "type": "object"
}
