schema:
{
 "description": "RemoteVtepSpec defines the desired state of RemoteVtep",
 "properties": {
  "clusterName": {
   "description": "ClusterName is the name of parent cluster who owns this remote VTEP.",
   "type": "string"
  },
  "endpointIPList": {
   "description": "EndpointIPList is the IP list of all local endpoints of this VTEP.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "ip": {
   "description": "IP is the gateway IP address of this VTEP.",
   "type": "string"
  },
  "localIPs": {
   "description": "localIPs are the usable ip addresses for the VTEP itself.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "mac": {
   "description": "MAC is the MAC address of this VTEP.",
   "type": "string"
  },
  "nodeName": {
   "description": "NodeName is the name of corresponding node in remote cluster.",
   "type": "string"
  }
 },
 "title": "Remote Vtep",
 "type": "object"
}
