schema:
{
 "description": "Customizing Envoy configuration generated by Istio. See more details at: https://istio.io/docs/reference/config/networking/envoy-filter.html",
 "properties": {
  "configPatches": {
   "description": "One or more patches with match conditions.",
   "items": {
    "properties": {
     "applyTo": {
      "enum": [
       "INVALID",
       "LISTENER",
       "FILTER_CHAIN",
       "NETWORK_FILTER",
       "HTTP_FILTER",
       "ROUTE_CONFIGURATION",
       "VIRTUAL_HOST",
       "HTTP_ROUTE",
       "CLUSTER",
       "EXTENSION_CONFIG",
       "BOOTSTRAP"
      ],
      "type": "string"
     },
     "match": {
      "description": "Match on listener/route configuration/cluster.",
      "oneOf": [
       {
        "not": {
         "anyOf": [
          {
           "required": [
            "listener"
           ]
          },
          {
           "required": [
            "routeConfiguration"
           ]
          },
          {
           "required": [
            "cluster"
           ]
          }
         ]
        }
       },
       {
        "required": [
         "listener"
        ]
       },
       {
        "required": [
         "routeConfiguration"
        ]
       },
       {
        "required": [
         "cluster"
        ]
       }
      ],
      "properties": {
       "cluster": {
        "description": "Match on envoy cluster attributes.",
        "properties": {
         "name": {
          "description": "The exact name of the cluster to match.",
          "type": "string"
         },
         "portNumber": {
          "description": "The service port for which this cluster was generated.",
          "type": "integer"
         },
         "service": {
          "description": "The fully qualified service name for this cluster.",
          "type": "string"
         },
         "subset": {
          "description": "The subset associated with the service.",
          "type": "string"
         }
        },
        "type": "object"
       },
       "context": {
        "description": "The specific config generation context to match on.",
        "enum": [
         "ANY",
         "SIDECAR_INBOUND",
         "SIDECAR_OUTBOUND",
         "GATEWAY"
        ],
        "type": "string"
       },
       "listener": {
        "description": "Match on envoy listener attributes.",
        "properties": {
         "filterChain": {
          "description": "Match a specific filter chain in a listener.",
          "properties": {
           "applicationProtocols": {
            "description": "Applies only to sidecars.",
            "type": "string"
           },
           "destinationPort": {
            "description": "The destination_port value used by a filter chain's match condition.",
            "type": "integer"
           },
           "filter": {
            "description": "The name of a specific filter to apply the patch to.",
            "properties": {
             "name": {
              "description": "The filter name to match on.",
              "type": "string"
             },
             "subFilter": {
              "properties": {
               "name": {
                "description": "The filter name to match on.",
                "type": "string"
               }
              },
              "type": "object"
             }
            },
            "type": "object"
           },
           "name": {
            "description": "The name assigned to the filter chain.",
            "type": "string"
           },
           "sni": {
            "description": "The SNI value used by a filter chain's match condition.",
            "type": "string"
           },
           "transportProtocol": {
            "description": "Applies only to `SIDECAR_INBOUND` context.",
            "type": "string"
           }
          },
          "type": "object"
         },
         "name": {
          "description": "Match a specific listener by its name.",
          "type": "string"
         },
         "portName": {
          "type": "string"
         },
         "portNumber": {
          "type": "integer"
         }
        },
        "type": "object"
       },
       "proxy": {
        "description": "Match on properties associated with a proxy.",
        "properties": {
         "metadata": {
          "additionalProperties": {
           "type": "string"
          },
          "type": "object"
         },
         "proxyVersion": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "routeConfiguration": {
        "description": "Match on envoy HTTP route configuration attributes.",
        "properties": {
         "gateway": {
          "type": "string"
         },
         "name": {
          "description": "Route configuration name to match on.",
          "type": "string"
         },
         "portName": {
          "description": "Applicable only for GATEWAY context.",
          "type": "string"
         },
         "portNumber": {
          "type": "integer"
         },
         "vhost": {
          "properties": {
           "name": {
            "type": "string"
           },
           "route": {
            "description": "Match a specific route within the virtual host.",
            "properties": {
             "action": {
              "description": "Match a route with specific action type.",
              "enum": [
               "ANY",
               "ROUTE",
               "REDIRECT",
               "DIRECT_RESPONSE"
              ],
              "type": "string"
             },
             "name": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "patch": {
      "description": "The patch to apply along with the operation.",
      "properties": {
       "filterClass": {
        "description": "Determines the filter insertion order.",
        "enum": [
         "UNSPECIFIED",
         "AUTHN",
         "AUTHZ",
         "STATS"
        ],
        "type": "string"
       },
       "operation": {
        "description": "Determines how the patch should be applied.",
        "enum": [
         "INVALID",
         "MERGE",
         "ADD",
         "REMOVE",
         "INSERT_BEFORE",
         "INSERT_AFTER",
         "INSERT_FIRST",
         "REPLACE"
        ],
        "type": "string"
       },
       "value": {
        "description": "The JSON config of the object being patched.",
        "type": "object",
        "x-kubernetes-preserve-unknown-fields": true
       }
      },
      "type": "object"
     }
    },
    "type": "object"
   },
   "type": "array"
  },
  "priority": {
   "description": "Priority defines the order in which patch sets are applied within a context.",
   "format": "int32",
   "type": "integer"
  },
  "workloadSelector": {
   "properties": {
    "labels": {
     "additionalProperties": {
      "type": "string"
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Envoy Filter",
 "type": "object"
}
