schema:
{
 "description": "KeycloakRealm is the Schema for the KeycloakRealms API",
 "properties": {
  "spec": {
   "description": "KeycloakRealmSpec defines the desired state of KeycloakRealm",
   "properties": {
    "address": {
     "type": "string"
    },
    "authSecret": {
     "description": "Contains a credentials set of a user with enough permission to manage keycloak",
     "properties": {
      "name": {
       "description": "Name referrs to the name of the secret, must be located whithin the same namespace",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace, by default the same namespace is used.",
       "type": "string"
      },
      "passwordField": {
       "default": "password",
       "type": "string"
      },
      "userField": {
       "default": "username",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "interval": {
     "description": "Interval reconciliation",
     "type": "string"
    },
    "realm": {
     "description": "Realm is the unstructured keycloak realm representation",
     "format": "textarea",
     "type": "string"
    },
    "suspend": {
     "description": "Suspend reconciliation",
     "type": "boolean"
    },
    "version": {
     "description": "Version is the keycloak version",
     "type": "string"
    }
   },
   "required": [
    "address",
    "realm",
    "version"
   ],
   "type": "object"
  }
 },
 "title": "Keycloak Realm",
 "type": "object"
}
