schema:
{
 "description": "MedusaTaskSpec defines the desired state of MedusaTask",
 "properties": {
  "backupName": {
   "description": "Name of the backup. Will be necessary for operations such as verify or status.",
   "type": "string"
  },
  "cassandraDatacenter": {
   "description": "The name of the CassandraDatacenter to run the task on",
   "type": "string"
  },
  "operation": {
   "description": "Requested operation to perform.",
   "enum": [
    "sync",
    "purge",
    "prepare_restore"
   ],
   "type": "string"
  },
  "restoreKey": {
   "description": "Restore key to use for the prepare_restore operation.",
   "type": "string"
  }
 },
 "required": [
  "cassandraDatacenter"
 ],
 "title": "Medusa Task",
 "type": "object"
}
