schema:
{
 "description": "CassandraBackup is the Schema for the cassandrabackups API",
 "properties": {
  "spec": {
   "description": "CassandraBackupSpec defines the desired state of CassandraBackup",
   "properties": {
    "backupType": {
     "default": "differential",
     "description": "The type of the backup: \"full\" or \"differential\"",
     "enum": [
      "differential",
      "full"
     ],
     "type": "string"
    },
    "cassandraDatacenter": {
     "description": "The name of the CassandraDatacenter to back up",
     "type": "string"
    },
    "name": {
     "description": "The name of the backup. TODO document format of generated name",
     "type": "string"
    }
   },
   "required": [
    "cassandraDatacenter"
   ],
   "type": "object"
  }
 },
 "title": "Cassandra Backup",
 "type": "object"
}
