schema:
{
 "description": "KeycloakBackupSpec defines the desired state of KeycloakBackup.",
 "properties": {
  "aws": {
   "description": "If provided, an automatic database backup will be created on AWS S3 instead of a local Persistent Volume. If this property is not provided - a local Persistent Volume backup will be chosen.",
   "properties": {
    "credentialsSecretName": {
     "description": "Provides a secret name used for connecting to AWS S3 Service. The secret needs to be in the following form: \n     apiVersion: v1     kind: Secret     metadata:       name: \u003cSecret name\u003e     type: Opaque     stringData:       AWS_S3_BUCKET_NAME: \u003cS3 Bucket Name\u003e       AWS_ACCESS_KEY_ID: \u003cAWS Access Key ID\u003e       AWS_SECRET_ACCESS_KEY: \u003cAWS Secret Key\u003e \n For more information, please refer to the Operator documentation.",
     "type": "string"
    },
    "encryptionKeySecretName": {
     "description": "If provided, the database backup will be encrypted. Provides a secret name used for encrypting database data. The secret needs to be in the following form: \n     apiVersion: v1     kind: Secret     metadata:       name: \u003cSecret name\u003e     type: Opaque     stringData:       GPG_PUBLIC_KEY: \u003cGPG Public Key\u003e       GPG_TRUST_MODEL: \u003cGPG Trust Model\u003e       GPG_RECIPIENT: \u003cGPG Recipient\u003e \n For more information, please refer to the Operator documentation.",
     "type": "string"
    },
    "schedule": {
     "description": "If specified, it will be used as a schedule for creating a CronJob.",
     "type": "string"
    }
   },
   "type": "object"
  },
  "instanceSelector": {
   "description": "Selector for looking up Keycloak Custom Resources.",
   "properties": {
    "matchExpressions": {
     "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
     "items": {
      "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
      "properties": {
       "key": {
        "description": "key is the label key that the selector applies to.",
        "type": "string"
       },
       "operator": {
        "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
        "type": "string"
       },
       "values": {
        "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
        "items": {
         "type": "string"
        },
        "type": "array"
       }
      },
      "required": [
       "key",
       "operator"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "matchLabels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
     "type": "object"
    }
   },
   "type": "object"
  },
  "restore": {
   "description": "Controls automatic restore behavior. Currently not implemented. \n In the future this will be used to trigger automatic restore for a given KeycloakBackup. Each backup will correspond to a single snapshot of the database (stored either in a Persistent Volume or AWS). If a user wants to restore it, all he/she needs to do is to change this flag to true. Potentially, it will be possible to restore a single backup multiple times.",
   "type": "boolean"
  },
  "storageClassName": {
   "description": "Name of the StorageClass for Postgresql Backup Persistent Volume Claim",
   "type": "string"
  }
 },
 "title": "Keycloak Backup",
 "type": "object"
}
