schema:
{
 "description": "ServiceDNSRecordSpec defines the desired state of ServiceDNSRecord.",
 "properties": {
  "allowServiceWithoutEndpoints": {
   "description": "AllowServiceWithoutEndpoints allows DNS records to be written for Service shards without endpoints",
   "type": "boolean"
  },
  "dnsPrefix": {
   "description": "DNSPrefix when specified, an additional DNS record would be created with \u003cDNSPrefix\u003e.\u003cKubeFedDomain\u003e",
   "type": "string"
  },
  "domainRef": {
   "description": "DomainRef is the name of the domain object to which the corresponding federated service belongs",
   "type": "string"
  },
  "externalName": {
   "description": "ExternalName when specified, replaces the service name portion of a resource record with the value of ExternalName.",
   "type": "string"
  },
  "recordTTL": {
   "description": "RecordTTL is the TTL in seconds for DNS records created for this Service, if omitted a default would be used",
   "format": "int64",
   "type": "integer"
  }
 },
 "required": [
  "domainRef"
 ],
 "title": "Service DNS Record",
 "type": "object"
}
