schema:
{
 "description": "ClusterSpec defines the desired state of Cluster",
 "properties": {
  "deletionPolicy": {
   "default": "Delete",
   "description": "DeletionPolicy specifies what will happen to the underlying external when this managed resource is deleted - either \"Delete\" or \"Orphan\" the external resource. This field is planned to be deprecated in favor of the ManagementPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
   "enum": [
    "Orphan",
    "Delete"
   ],
   "type": "string"
  },
  "forProvider": {
   "properties": {
    "allocatedStorage": {
     "description": "The amount of storage in gibibytes (GiB) to allocate to each DB instance in the Multi-AZ DB cluster.",
     "type": "number"
    },
    "allowMajorVersionUpgrade": {
     "description": "Enable to allow major engine version upgrades when changing engine versions. Defaults to false.",
     "type": "boolean"
    },
    "applyImmediately": {
     "description": "Specifies whether any cluster modifications are applied immediately, or during the next maintenance window. Default is false. See Amazon RDS Documentation for more information.",
     "type": "boolean"
    },
    "availabilityZones": {
     "description": "List of EC2 Availability Zones for the DB cluster storage where DB cluster instances can be created. We recommend specifying 3 AZs or using the  if necessary. A maximum of 3 AZs can be configured.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "backtrackWindow": {
     "description": "Target backtrack window, in seconds. Only available for aurora and aurora-mysql engines currently. To disable backtracking, set this value to 0. Defaults to 0. Must be between 0 and 259200 (72 hours)",
     "type": "number"
    },
    "backupRetentionPeriod": {
     "description": "Days to retain backups for. Default 1",
     "type": "number"
    },
    "clusterMembers": {
     "description": "– List of RDS Instances that are a part of this cluster",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "copyTagsToSnapshot": {
     "description": "–  Copy all Cluster tags to snapshots. Default is false.",
     "type": "boolean"
    },
    "databaseName": {
     "description": "Name for an automatically created database on cluster creation. There are different naming restrictions per database engine: RDS Naming Constraints",
     "type": "string"
    },
    "dbClusterInstanceClass": {
     "description": "Compute and memory capacity of each DB instance in the Multi-AZ DB cluster, for example db.m6g.xlarge. Not all DB instance classes are available in all AWS Regions, or for all database engines. For the full list of DB instance classes and availability for your engine, see DB instance class in the Amazon RDS User Guide. (This setting is required to create a Multi-AZ DB cluster).",
     "type": "string"
    },
    "dbClusterParameterGroupName": {
     "type": "string"
    },
    "dbInstanceParameterGroupName": {
     "description": "Instance parameter group to associate with all instances of the DB cluster. The db_instance_parameter_group_name parameter is only valid in combination with the allow_major_version_upgrade parameter.",
     "type": "string"
    },
    "dbSubnetGroupName": {
     "description": "DB subnet group to associate with this DB instance. NOTE: This must match the db_subnet_group_name specified on every aws_rds_cluster_instance in the cluster.",
     "type": "string"
    },
    "dbSubnetGroupNameRef": {
     "description": "Reference to a SubnetGroup to populate dbSubnetGroupName.",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "dbSubnetGroupNameSelector": {
     "description": "Selector for a SubnetGroup to populate dbSubnetGroupName.",
     "properties": {
      "matchControllerRef": {
       "description": "MatchControllerRef ensures an object with the same controller reference as the selecting object is selected.",
       "type": "boolean"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "MatchLabels ensures an object with matching labels is selected.",
       "type": "object"
      },
      "policy": {
       "description": "Policies for selection.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "deletionProtection": {
     "description": "If the DB instance should have deletion protection enabled. The database can't be deleted when this value is set to true. The default is false.",
     "type": "boolean"
    },
    "enableGlobalWriteForwarding": {
     "description": "Whether cluster should forward writes to an associated global cluster. Applied to secondary clusters to enable them to forward writes to an aws_rds_global_cluster's primary cluster. See the Aurora Userguide documentation for more information.",
     "type": "boolean"
    },
    "enableHttpEndpoint": {
     "description": "Enable HTTP endpoint (data API). Only valid when engine_mode is set to serverless.",
     "type": "boolean"
    },
    "enabledCloudwatchLogsExports": {
     "description": "Set of log types to export to cloudwatch. If omitted, no logs will be exported. The following log types are supported: audit, error, general, slowquery, postgresql (PostgreSQL).",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "engine": {
     "description": "Name of the database engine to be used for this DB cluster. Valid Values: aurora-mysql, aurora-postgresql, mysql, postgres. (Note that mysql and postgres are Multi-AZ RDS clusters).",
     "type": "string"
    },
    "engineMode": {
     "description": "Database engine mode. Valid values: global (only valid for Aurora MySQL 1.21 and earlier), multimaster, parallelquery, provisioned, serverless. Defaults to: provisioned. See the RDS User Guide for limitations when using serverless.",
     "type": "string"
    },
    "engineVersion": {
     "description": "Database engine version. Updating this argument results in an outage. See the Aurora MySQL and Aurora Postgres documentation for your configured engine to determine this value, or by running aws rds describe-db-engine-versions. For example with Aurora MySQL 2, a potential value for this argument is 5.7.mysql_aurora.2.03.2. The value can contain a partial version where supported by the API. The actual engine version used is returned in the attribute engine_version_actual, , see Attributes Reference below.",
     "type": "string"
    },
    "finalSnapshotIdentifier": {
     "description": "Name of your final DB snapshot when this DB cluster is deleted. If omitted, no final snapshot will be made.",
     "type": "string"
    },
    "globalClusterIdentifier": {
     "description": "Global cluster identifier specified on aws_rds_global_cluster.",
     "type": "string"
    },
    "iamDatabaseAuthenticationEnabled": {
     "description": "Specifies whether or not mappings of AWS Identity and Access Management (IAM) accounts to database accounts is enabled. Please see AWS Documentation for availability and limitations.",
     "type": "boolean"
    },
    "iops": {
     "description": "Amount of Provisioned IOPS (input/output operations per second) to be initially allocated for each DB instance in the Multi-AZ DB cluster. For information about valid Iops values, see Amazon RDS Provisioned IOPS storage to improve performance in the Amazon RDS User Guide. (This setting is required to create a Multi-AZ DB cluster). Must be a multiple between .5 and 50 of the storage amount for the DB cluster.",
     "type": "number"
    },
    "kmsKeyId": {
     "description": "ARN for the KMS encryption key. When specifying kms_key_id, storage_encrypted needs to be set to true.",
     "type": "string"
    },
    "manageMasterUserPassword": {
     "description": "Set to true to allow RDS to manage the master user password in Secrets Manager. Cannot be set if master_password is provided.",
     "type": "boolean"
    },
    "masterPasswordSecretRef": {
     "description": "Password for the master DB user. Note that this may show up in logs, and it will be stored in the state file. Please refer to the RDS Naming Constraints. Cannot be set if manage_master_user_password is set to true.",
     "properties": {
      "key": {
       "description": "The key to select.",
       "type": "string"
      },
      "name": {
       "description": "Name of the secret.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the secret.",
       "type": "string"
      }
     },
     "required": [
      "key",
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "masterUserSecretKmsKeyId": {
     "description": "Amazon Web Services KMS key identifier is the key ARN, key ID, alias ARN, or alias name for the KMS key. To use a KMS key in a different Amazon Web Services account, specify the key ARN or alias ARN. If not specified, the default KMS key for your Amazon Web Services account is used.",
     "type": "string"
    },
    "masterUsername": {
     "description": "Username for the master DB user. Please refer to the RDS Naming Constraints. This argument does not support in-place updates and cannot be changed during a restore from snapshot.",
     "type": "string"
    },
    "networkType": {
     "description": "Network type of the cluster. Valid values: IPV4, DUAL.",
     "type": "string"
    },
    "port": {
     "description": "Port on which the DB accepts connections",
     "type": "number"
    },
    "preferredBackupWindow": {
     "description": "Daily time range during which automated backups are created if automated backups are enabled using the BackupRetentionPeriod parameter.Time in UTC. Default: A 30-minute window selected at random from an 8-hour block of time per regionE.g., 04:00-09:00",
     "type": "string"
    },
    "preferredMaintenanceWindow": {
     "description": "Weekly time range during which system maintenance can occur, in (UTC) e.g., wed:04:00-wed:04:30",
     "type": "string"
    },
    "region": {
     "description": "Region is the region you'd like your resource to be created in.",
     "type": "string"
    },
    "replicationSourceIdentifier": {
     "description": "ARN of a source DB cluster or DB instance if this DB cluster is to be created as a Read Replica.",
     "type": "string"
    },
    "restoreToPointInTime": {
     "description": "Nested attribute for point in time restore. More details below.",
     "items": {
      "properties": {
       "restoreToTime": {
        "description": "Date and time in UTC format to restore the database cluster to. Conflicts with use_latest_restorable_time.",
        "type": "string"
       },
       "restoreType": {
        "description": "Type of restore to be performed. Valid options are full-copy (default) and copy-on-write.",
        "type": "string"
       },
       "sourceClusterIdentifier": {
        "description": "Identifier of the source database cluster from which to restore.",
        "type": "string"
       },
       "sourceClusterIdentifierRef": {
        "description": "Reference to a Cluster to populate sourceClusterIdentifier.",
        "properties": {
         "name": {
          "description": "Name of the referenced object.",
          "type": "string"
         },
         "policy": {
