schema:
{
 "description": "A ProviderConfigUsage indicates that a resource is using a ProviderConfig.",
 "properties": {
  "providerConfigRef": {
   "description": "ProviderConfigReference to the provider config being used.",
   "properties": {
    "name": {
     "description": "Name of the referenced object.",
     "type": "string"
    },
    "policy": {
     "description": "Policies for referencing.",
     "properties": {
      "resolution": {
       "default": "Required",
       "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
       "enum": [
        "Required",
        "Optional"
       ],
       "type": "string"
      },
      "resolve": {
       "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
       "enum": [
        "Always",
        "IfNotPresent"
       ],
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "resourceRef": {
   "description": "ResourceReference to the managed resource using the provider config.",
   "properties": {
    "apiVersion": {
     "description": "APIVersion of the referenced object.",
     "type": "string"
    },
    "kind": {
     "description": "Kind of the referenced object.",
     "type": "string"
    },
    "name": {
     "description": "Name of the referenced object.",
     "type": "string"
    },
    "uid": {
     "description": "UID of the referenced object.",
     "type": "string"
    }
   },
   "required": [
    "apiVersion",
    "kind",
    "name"
   ],
   "type": "object"
  }
 },
 "required": [
  "providerConfigRef",
  "resourceRef"
 ],
 "title": "Provider Config Usage",
 "type": "object"
}
