schema:
{
 "description": "ServiceExport is the Schema for the serviceexports API",
 "properties": {
  "spec": {
   "description": "ServiceExportSpec defines the desired state of ServiceExport",
   "properties": {
    "aliases": {
     "description": "Alias names for the exported service. The service could be addressed by the alias names in addition to the slice.local name.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "ingressEnabled": {
     "description": "IngressEnabled denotes whether the traffic should be proxied through an ingress gateway",
     "type": "boolean"
    },
    "ports": {
     "description": "Ports which should be exposed through the service",
     "items": {
      "description": "ServicePort is the port exposed by ServicePod",
      "properties": {
       "containerPort": {
        "description": "Port number exposed from the container",
        "format": "int32",
        "type": "integer"
       },
       "name": {
        "description": "Name of the port",
        "type": "string"
       },
       "protocol": {
        "default": "TCP",
        "description": "Protocol for port. Must be UDP, TCP, or SCTP. Defaults to \"TCP\".",
        "type": "string"
       }
      },
      "required": [
       "containerPort"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "selector": {
     "description": "Selector is a label query over pods that should be exposed as a service",
     "properties": {
      "matchExpressions": {
       "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
       "items": {
        "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
        "properties": {
         "key": {
          "description": "key is the label key that the selector applies to.",
          "type": "string"
         },
         "operator": {
          "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
          "type": "string"
         },
         "values": {
          "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "key",
         "operator"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "slice": {
     "description": "Slice denotes the slice which the app is part of",
     "type": "string"
    }
   },
   "required": [
    "ports",
    "selector",
    "slice"
   ],
   "type": "object"
  }
 },
 "title": "Service Export",
 "type": "object"
}
