schema:
{
 "description": "ServiceImport is the Schema for the serviceimports API",
 "properties": {
  "spec": {
   "description": "ServiceImportSpec defines the desired state of ServiceImport",
   "properties": {
    "aliases": {
     "description": "Alias names for the exported service. The service could be addressed by the alias names in addition to the slice.local name.",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "dnsName": {
     "description": "DNSName shows the FQDN to reach the service",
     "type": "string"
    },
    "ports": {
     "description": "Ports which should be exposed through the service",
     "items": {
      "description": "ServicePort is the port exposed by ServicePod",
      "properties": {
       "containerPort": {
        "description": "Port number exposed from the container",
        "format": "int32",
        "type": "integer"
       },
       "name": {
        "description": "Name of the port",
        "type": "string"
       },
       "protocol": {
        "default": "TCP",
        "description": "Protocol for port. Must be UDP, TCP, or SCTP. Defaults to \"TCP\".",
        "type": "string"
       }
      },
      "required": [
       "containerPort"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "slice": {
     "description": "Slice denotes the slice which the app is part of",
     "type": "string"
    }
   },
   "required": [
    "dnsName",
    "ports",
    "slice"
   ],
   "type": "object"
  }
 },
 "title": "Service Import",
 "type": "object"
}
